    50```typescript
    51// packages/hooks/src/ai/useAI.ts - Primary AI Hook Interface
    52export interface AIHookSystem {
    53  // Model Management
    54  availableModels: AIModel[];
    55  currentModel: AIModel | null;
    56  switchModel: (modelId: string) => Promise<void>;
    57  
    58  // Text Generation
    59  generate: (prompt: string, config?: GenerationConfig) => Promise<AIResponse>;
    60  stream: (prompt: string, config?: GenerationConfig) => AsyncGenerator<AIResponseChunk>;
    61  
    62  // Multimodal Capabilities
    63  generateWithImages: (prompt: string, images: File[], config?: MultimodalConfig) => Promise<AIResponse>;
    64  speechToText: (audio: File, config?: STTConfig) => Promise<string>;
    65  textToSpeech: (text: string, config?: TTSConfig) => Promise<AudioBuffer>;
