    81
    82export function useAI(config?: AIConfig): AIHookSystem {
    83  const [currentModel, setCurrentModel] = useState<AIModel | null>(null);
    84  const [availableModels, setAvailableModels] = useState<AIModel[]>([]);
    85  const [isLoading, setIsLoading] = useState(false);
    86  const [error, setError] = useState<AIError | null>(null);
    87  const [securityStatus, setSecurityStatus] = useState<SecurityStatus>('safe');
    88  const [performanceMetrics, setPerformanceMetrics] = useState<AIMetrics>({});
    89  
    90  // WASM-accelerated inference integration
    91  const { rust: rustWasm } = useWasmRuntime();
    92  const security = useAISecurity();
    93  
    94  const generate = useCallback(async (prompt: string, config?: GenerationConfig) => {
    95    if (!currentModel) throw new Error('No AI model selected');
    96    
    97    setIsLoading(true);
    98    setError(null);
    99    
   100    try {
   101      // Content moderation check
   102      const moderationResult = await security.moderateContent(prompt);
   103      if (moderationResult.flagged) {
   104        throw new AIError('Content flagged by security system', 'CONTENT_MODERATED');
   105      }
   106      
   107      const startTime = performance.now();
   108      
   109      // Use WASM acceleration for local models
   110      let response: AIResponse;
   111      if (currentModel.runtime === 'wasm' && rustWasm.isLoaded) {
   112        response = await generateWithWasm(prompt, config);
   113      } else {
   114        response = await generateWithAPI(prompt, config);
   115      }
   116      
   117      const duration = performance.now() - startTime;
   118      
   119      // Update performance metrics
   120      setPerformanceMetrics(prev => ({
   121        ...prev,
   122        lastInferenceTime: duration,
   123        totalInferences: (prev.totalInferences || 0) + 1,
   124        averageLatency: calculateAverageLatency(prev, duration)
   125      }));
   126      
   127      // Post-generation content check
   128      const responseModeration = await security.moderateContent(response.content);
   129      if (responseModeration.flagged) {
   130        throw new AIError('Generated content flagged by security system', 'OUTPUT_MODERATED');
   131      }
   132      
   133      setIsLoading(false);
   134      return response;
   135      
   136    } catch (err) {
   137      setError(err as AIError);
   138      setIsLoading(false);
   139      throw err;
   140    }
   141  }, [currentModel, rustWasm, security]);
   142  
   143  const stream = useCallback(async function* (prompt: string, config?: GenerationConfig) {
   144    if (!currentModel) throw new Error('No AI model selected');
