   146    // Security check
   147    const moderationResult = await security.moderateContent(prompt);
   148    if (moderationResult.flagged) {
   149      throw new AIError('Content flagged by security system', 'CONTENT_MODERATED');
   150    }
   151    
   152    const responseStream = currentModel.stream(prompt, config);
   153    let fullResponse = '';
   154    
   155    for await (const chunk of responseStream) {
   156      fullResponse += chunk.content;
   157      
   158      // Real-time content moderation for streaming
   159      if (fullResponse.length % 100 === 0) { // Check every 100 characters
   160        const streamModeration = await security.moderateContent(fullResponse);
   161        if (streamModeration.flagged) {
   162          throw new AIError('Streaming content flagged', 'STREAM_MODERATED');
   163        }
   164      }
   165      
   166      yield chunk;
   167    }
   168  }, [currentModel, security]);
   169  
   170  // Additional methods implementation...
