/** @type {import('next').NextConfig} */
const nextConfig = {
  experimental: {
    appDir: true,
    serverComponents: true,
    webAssembly: true,
    reactCompiler: true
  },
  webpack: (config, { isServer }) => {
    // Enable WASM support
    config.experiments = {
      ...config.experiments,
      asyncWebAssembly: true,
      layers: true,
    }

    // Handle WASM files
    config.module.rules.push({
      test: /\.wasm$/,
      type: 'webassembly/async',
    })

    // Handle Julia files (for syntax highlighting and processing)
    config.module.rules.push({
      test: /\.jl$/,
      use: 'raw-loader',
    })

    if (!isServer) {
      config.resolve.fallback = {
        ...config.resolve.fallback,
        fs: false,
        path: false,
        crypto: false,
      }
    }

    return config
  },
  env: {
    PHOENIX_API_URL: process.env.PHOENIX_API_URL || 'http://localhost:4000',
    VERCEL_ENV: process.env.VERCEL_ENV || 'development',
  },
  headers: async () => [
    {
      source: '/(.*)',
      headers: [
        {
          key: 'Cross-Origin-Embedder-Policy',
          value: 'require-corp'
        },
        {
          key: 'Cross-Origin-Opener-Policy', 
          value: 'same-origin'
        }
      ]
    }
  ],
  images: {
    domains: ['images.unsplash.com', 'avatars.githubusercontent.com'],
  },
  typescript: {
    tsconfigPath: './tsconfig.json',
  },
}

module.exports = nextConfig

