import type { Metadata } from 'next'
import { Inter } from 'next/font/google'
import './globals.css'
import { Analytics } from '@vercel/analytics/react'
import { SpeedInsights } from '@vercel/speed-insights/next'
import { KatalystProvider } from '@/components/providers/KatalystProvider'
import { WasmProvider } from '@/components/providers/WasmProvider'
import { Toaster } from '@/components/ui/toaster'

const inter = Inter({ subsets: ['latin'] })

export const metadata: Metadata = {
  title: 'Supercompute.me - Julia WASM Computing Platform',
  description: 'High-performance Julia computations in the browser via WebAssembly, powered by REASSEMBLY and Katalyst frameworks.',
  keywords: 'Julia, WebAssembly, WASM, High Performance Computing, React 19, Phoenix, Elixir',
  authors: [{ name: 'Supercompute Foundation' }],
  openGraph: {
    title: 'Supercompute.me - Julia WASM Computing Platform',
    description: 'Experience high-performance Julia computations directly in your browser',
    url: 'https://supercompute.me',
    siteName: 'Supercompute.me',
    type: 'website',
  },
  twitter: {
    card: 'summary_large_image',
    title: 'Supercompute.me - Julia WASM Computing Platform',
    description: 'High-performance Julia computations in the browser via WebAssembly',
  },
  robots: {
    index: true,
    follow: true,
  },
}

export default function RootLayout({
  children,
}: {
  children: React.ReactNode
}) {
  return (
    <html lang="en" className="dark">
      <body className={inter.className}>
        <KatalystProvider>
          <WasmProvider>
            <main className="min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-violet-900">
              {children}
            </main>
            <Toaster />
          </WasmProvider>
        </KatalystProvider>
        <Analytics />
        <SpeedInsights />
      </body>
    </html>
  )
}

