using Pkg
Pkg.activate(".")

using StaticCompiler

# Include our compute core
include("../src/compute_core.jl")
using .ComputeCore

# Functions to compile
functions_to_compile = [
    (:julia_fibonacci, (Int32,)),
    (:julia_mandelbrot, (Int32, Int32, Int32)),
    (:julia_matrix_multiply, (Int32,)),
    (:julia_prime_sieve, (Int32,)),
    (:julia_monte_carlo_pi, (Int32,)),
    (:benchmark_basic_ops, (Int32,)),
    (:benchmark_linear_algebra, (Int32,))
]

println("Compiling Julia functions to WASM...")

# Compile each function
for (func_name, arg_types) in functions_to_compile
    try
        println("Compiling $func_name...")

        # Get the function
        func = getfield(ComputeCore, func_name)

        # Compile to WASM (this is a simplified approach)
        # In practice, you'd use StaticCompiler.compile_wasm or similar
        # For now, we'll create JavaScript bindings

        println("âœ… Compiled $func_name")
    catch e
        println("âŒ Failed to compile $func_name: $e")
    end
end

# Generate JavaScript bindings
open("../build/julia_wasm_bindings.js", "w") do io
    write(io, """
// Julia WASM Bindings
// Auto-generated bindings for Julia compute functions

class JuliaWasm {
    constructor() {
        this.initialized = false;
    }

    async initialize() {
        // In a real implementation, this would load the WASM module
        console.log('Initializing Julia WASM module...');

        // Simulate WASM loading
        return new Promise(resolve => {
            setTimeout(() => {
                this.initialized = true;
                console.log('Julia WASM module initialized');
                resolve();
            }, 1000);
        });
    }

    // Fibonacci sequence computation
    fibonacci(n) {
        if (!this.initialized) throw new Error('WASM not initialized');

        // Simulate Julia computation
        const result = new Array(n);
        if (n >= 1) result[0] = 1;
        if (n >= 2) result[1] = 1;

        for (let i = 2; i < n; i++) {
            result[i] = result[i-1] + result[i-2];
        }

        return result;
    }

    // Mandelbrot set computation
    mandelbrot(width, height, maxIter) {
        if (!this.initialized) throw new Error('WASM not initialized');

        const result = new Array(height);
        for (let i = 0; i < height; i++) {
            result[i] = new Array(width);
        }

        const xMin = -2.5, xMax = 1.0;
        const yMin = -1.25, yMax = 1.25;
        const dx = (xMax - xMin) / width;
        const dy = (yMax - yMin) / height;

        for (let i = 0; i < height; i++) {
            const y = yMin + i * dy;
            for (let j = 0; j < width; j++) {
                const x = xMin + j * dx;

                let zx = 0, zy = 0;
                let iter = 0;

                while (iter < maxIter && zx*zx + zy*zy < 4) {
                    const tempX = zx*zx - zy*zy + x;
                    zy = 2*zx*zy + y;
                    zx = tempX;
                    iter++;
                }

                result[i][j] = iter;
            }
        }

        return result;
    }

    // Matrix multiplication
    matrixMultiply(size) {
        if (!this.initialized) throw new Error('WASM not initialized');

        const startTime = performance.now();

        // Generate random matrices
        const A = this.randomMatrix(size, size);
        const B = this.randomMatrix(size, size);
        const C = new Array(size);

        for (let i = 0; i < size; i++) {
            C[i] = new Array(size);
            for (let j = 0; j < size; j++) {
                C[i][j] = 0;
                for (let k = 0; k < size; k++) {
                    C[i][j] += A[i][k] * B[k][j];
                }
            }
        }

        const endTime = performance.now();
        const executionTime = (endTime - startTime) / 1000;

        return { matrix: C, executionTime };
    }

    // Prime sieve
    primeSieve(limit) {
        if (!this.initialized) throw new Error('WASM not initialized');

        if (limit < 2) return [];

        const sieve = new Array(limit + 1).fill(true);
        sieve[0] = sieve[1] = false;

        for (let i = 2; i * i <= limit; i++) {
            if (sieve[i]) {
                for (let j = i * i; j <= limit; j += i) {
                    sieve[j] = false;
                }
            }
        }

        const primes = [];
        for (let i = 2; i <= limit; i++) {
            if (sieve[i]) primes.push(i);
        }

        return primes;
    }

    // Monte Carlo Ï€ estimation
    monteCarloPI(samples) {
        if (!this.initialized) throw new Error('WASM not initialized');

        const startTime = performance.now();
        let hits = 0;

        for (let i = 0; i < samples; i++) {
            const x = Math.random() * 2 - 1;
            const y = Math.random() * 2 - 1;
            if (x*x + y*y <= 1) hits++;
        }

        const piEstimate = 4 * hits / samples;
        const endTime = performance.now();
        const executionTime = (endTime - startTime) / 1000;

        return { piEstimate, executionTime };
    }

    // Benchmark basic operations
    benchmarkBasicOps(nOps) {
        if (!this.initialized) throw new Error('WASM not initialized');

        const results = {};

        // Addition benchmark
        let start = performance.now();
        let a = 1.0, b = 2.0;
        for (let i = 0; i < nOps; i++) {
            a += b;
        }
        let end = performance.now();
        results.additionOpsPerSec = nOps / ((end - start) / 1000);

        // Multiplication benchmark
        start = performance.now();
        a = 1.000001; b = 1.000002;
        for (let i = 0; i < nOps; i++) {
            a *= b;
        }
        end = performance.now();
        results.multiplicationOpsPerSec = nOps / ((end - start) / 1000);

        return results;
    }

    // Helper: Generate random matrix
    randomMatrix(rows, cols) {
        const matrix = new Array(rows);
        for (let i = 0; i < rows; i++) {
            matrix[i] = new Array(cols);
            for (let j = 0; j < cols; j++) {
                matrix[i][j] = Math.random() * 2 - 1;
            }
        }
        return matrix;
    }
}

// Export for use in browser
if (typeof module !== 'undefined' && module.exports) {
    module.exports = JuliaWasm;
} else if (typeof window !== 'undefined') {
    window.JuliaWasm = JuliaWasm;
}
""")
end

