'use client'

import { createContext, useContext, useState, useEffect, ReactNode, useCallback } from 'react'

interface WasmContextType {
  wasmModule: any
  isLoading: boolean
  isReady: boolean
  error: string | null
  loadWasm: () => Promise<void>
  executeComputation: (fn: string, args: any[]) => Promise<any>
  getPerformanceMetrics: () => any
}

const WasmContext = createContext<WasmContextType | undefined>(undefined)

export function WasmProvider({ children }: { children: ReactNode }) {
  const [wasmModule, setWasmModule] = useState<any>(null)
  const [isLoading, setIsLoading] = useState(false)
  const [isReady, setIsReady] = useState(false)
  const [error, setError] = useState<string | null>(null)

  const loadWasm = useCallback(async () => {
    if (isLoading || isReady) return

    setIsLoading(true)
    setError(null)

    try {
      // Dynamically import the Julia WASM module
      const { default: JuliaWasm } = await import('/wasm/julia_wasm_bindings.js')
      const module = new JuliaWasm()

      await module.initialize()

      setWasmModule(module)
      setIsReady(true)
      console.log('ðŸš€ Julia WASM module loaded successfully')
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Unknown error'
      setError(`Failed to load WASM module: ${errorMessage}`)
      console.error('âŒ Failed to load Julia WASM:', err)
    } finally {
      setIsLoading(false)
    }
  }, [isLoading, isReady])

  const executeComputation = useCallback(async (fn: string, args: any[]) => {
    if (!wasmModule || !isReady) {
      throw new Error('WASM module not ready')
    }

    const startTime = performance.now()

    try {
      let result
      switch (fn) {
        case 'fibonacci':
          result = wasmModule.fibonacci(args[0])
          break
        case 'mandelbrot':
          result = wasmModule.mandelbrot(args[0], args[1], args[2])
          break
        case 'matrixMultiply':
          result = wasmModule.matrixMultiply(args[0])
          break
        case 'primeSieve':
          result = wasmModule.primeSieve(args[0])
          break
        case 'monteCarloPI':
          result = wasmModule.monteCarloPI(args[0])
          break
        case 'benchmarkBasicOps':
          result = wasmModule.benchmarkBasicOps(args[0])
          break
        default:
          throw new Error(`Unknown function: ${fn}`)
      }

      const endTime = performance.now()

      return {
        result,
        executionTime: endTime - startTime,
        timestamp: new Date().toISOString()
      }
    } catch (err) {
      const endTime = performance.now()
      throw new Error(`Computation failed: ${err instanceof Error ? err.message : 'Unknown error'}`)
    }
  }, [wasmModule, isReady])

  const getPerformanceMetrics = useCallback(() => {
    if (!wasmModule || !isReady) {
      return null
    }

    return {
      memoryUsage: {
        used: performance.memory?.usedJSHeapSize || 0,
        total: performance.memory?.totalJSHeapSize || 0,
        limit: performance.memory?.jsHeapSizeLimit || 0
      },
      timing: {
        navigationStart: performance.timeOrigin,
        loadEventEnd: performance.timing?.loadEventEnd || 0
      },
      wasmReady: isReady,
      moduleLoaded: wasmModule !== null
    }
  }, [wasmModule, isReady])

  useEffect(() => {
    // Auto-load WASM module on mount
    loadWasm()
  }, [loadWasm])

  const value: WasmContextType = {
    wasmModule,
    isLoading,
    isReady,
    error,
    loadWasm,
    executeComputation,
    getPerformanceMetrics
  }

  return (
    <WasmContext.Provider value={value}>
      {children}
    </WasmContext.Provider>
  )
}

export function useWasm() {
  const context = useContext(WasmContext)
  if (context === undefined) {
    throw new Error('useWasm must be used within a WasmProvider')
  }
  return context
}

