for (func_name, test_args) in functions_to_test
    try
        println("Testing $func_name...")

        # Get the function
        func = getfield(ComputeCore, func_name)

        # Test with sample arguments
        result = func(test_args...)

        println("âœ… $func_name works correctly")
    catch e
        println("âŒ Failed to test $func_name: $e")
    end
