    95â†’  ): Promise<AdvancedTaskResult<T>> => {
    96â†’    if (!context.isInitialized) {
    97â†’      throw new Error('Multithreading context not initialized');
    98â†’    }
    99â†’
   100â†’    setActiveOperations(prev => new Set([...prev, task.id]));
   101â†’    
   102â†’    try {
   103â†’      // Convert to context-compatible task format
   104â†’      const contextTask = {
   105â†’        id: task.id,
   106â†’        type: task.type,
   107â†’        operation: task.operation,
   108â†’        data: task.data,
   109â†’        priority: task.priority,
   110â†’        timeout: task.timeout,
   111â†’        retries: task.retries,
   112â†’        dependencies: task.dependencies,
   113â†’        subagentRequirement: task.subagentRequirement,
   114â†’      };
   115â†’
   116â†’      const result = await context.submitTask(contextTask);
   117â†’      
   118â†’      const advancedResult: AdvancedTaskResult<T> = {
   119â†’        ...result,
   120â†’        resourceUsage: {
   121â†’          memoryPeak: context.metrics.memoryUsage,
   122â†’          cpuTime: result.executionTime,
   123â†’          threadPoolUtilization: context.metrics.threadUtilization,
   124â†’        },
   125â†’      };
   126â†’
   127â†’      setTaskHistory(prev => new Map([...prev, [task.id, advancedResult]]));
   128â†’      
   129â†’      return advancedResult;
   130â†’    } finally {
   131â†’      setActiveOperations(prev => {
   132â†’        const newSet = new Set(prev);
   133â†’        newSet.delete(task.id);
   134â†’        return newSet;
   135â†’      });
   136â†’    }
   137â†’  }, [context]);
   138â†’
   139â†’  // Batch processing with intelligent load balancing
   140â†’  const submitTaskBatch = useCallback(async <T>(
   141â†’    tasks: AdvancedThreadTask<T>[],
   142â†’    options: {
   143â†’      maxConcurrency?: number;
   144â†’      loadBalancing?: 'round-robin' | 'priority' | 'resource-aware';
   145â†’      failureHandling?: 'fail-fast' | 'continue' | 'retry';
   146â†’    } = {}
   147â†’  ): Promise<AdvancedTaskResult<T>[]> => {
   148â†’    const { maxConcurrency = 4, loadBalancing = 'priority', failureHandling = 'continue' } = options;
   149â†’    
   150â†’    // Sort tasks by priority if using priority load balancing
   151â†’    const sortedTasks = loadBalancing === 'priority' 
   152â†’      ? [...tasks].sort((a, b) => {
   153â†’          const priorityMap = { low: 0, normal: 1, high: 2, critical: 3 };
   154â†’          return priorityMap[b.priority] - priorityMap[a.priority];
   155â†’        })
   156â†’      : tasks;
   157â†’
   158â†’    const results: AdvancedTaskResult<T>[] = [];
   159â†’    const chunks = [];
   160â†’    
   161â†’    // Chunk tasks based on max concurrency
   162â†’    for (let i = 0; i < sortedTasks.length; i += maxConcurrency) {
   163â†’      chunks.push(sortedTasks.slice(i, i + maxConcurrency));
   164â†’    }
   165â†’
   166â†’    for (const chunk of chunks) {
   167â†’      const chunkPromises = chunk.map(task => 
   168â†’        submitAdvancedTask(task).catch(error => ({
   169â†’          id: task.id,
   170â†’          status: 'failed' as const,
   171â†’          error: error.message,
   172â†’          executionTime: 0,
   173â†’          threadId: '',
   174â†’          retryCount: 0,
   175â†’        }))
   176â†’      );
   177â†’
   178â†’      const chunkResults = await Promise.all(chunkPromises);
   179â†’      results.push(...chunkResults);
   180â†’
   181â†’      // Handle failures based on policy
   182â†’      if (failureHandling === 'fail-fast' && chunkResults.some(r => r.status === 'failed')) {
   183â†’        break;
   184â†’      }
   185â†’    }
   186â†’
   187â†’    return results;
   188â†’  }, [submitAdvancedTask]);
   189â†’
   190â†’  // Thread pool monitoring and metrics
   191â†’  const getThreadPoolMetrics = useCallback((): ThreadPoolMetrics[] => {
   192â†’    if (!context.isInitialized) return [];
   193â†’
   194â†’    // This would typically come from the native module or context
   195â†’    return [
   196â†’      {
   197â†’        id: 'rayon_global',
   198â†’        type: 'cpu',
   199â†’        workerCount: 8,
   200â†’        activeJobs: context.metrics.activeThreads,
   201â†’        queuedJobs: context.metrics.queuedTasks,
   202â†’        completedJobs: context.metrics.completedTasks,
   203â†’        failedJobs: context.metrics.failedTasks,
   204â†’        averageJobTime: context.metrics.averageExecutionTime,
   205â†’        utilization: context.metrics.threadUtilization,
   206â†’        health: context.metrics.threadUtilization > 90 ? 'critical' : 
   207â†’                context.metrics.threadUtilization > 70 ? 'degraded' : 'healthy',
   208â†’      },
   209â†’      {
   210â†’        id: 'tokio_runtime',
   211â†’        type: 'io',
   212â†’        workerCount: 4,
   213â†’        activeJobs: Math.floor(context.metrics.activeThreads * 0.3),
   214â†’        queuedJobs: Math.floor(context.metrics.queuedTasks * 0.2),
   215â†’        completedJobs: Math.floor(context.metrics.completedTasks * 0.4),
   216â†’        failedJobs: Math.floor(context.metrics.failedTasks * 0.1),
   217â†’        averageJobTime: context.metrics.averageExecutionTime * 0.8,
   218â†’        utilization: context.metrics.threadUtilization * 0.7,
   219â†’        health: 'healthy',
   220â†’      },
   221â†’    ];
   222â†’  }, [context]);
   223â†’
   224â†’  // System-wide performance metrics
   225â†’  const getSystemMetrics = useCallback((): SystemMetrics => {
   226â†’    if (!context.isInitialized) return performanceMetricsRef.current;
   227â†’
   228â†’    const metrics: SystemMetrics = {
   229â†’      totalThreads: 12, // This would come from system info
   230â†’      availableThreads: 12 - context.metrics.activeThreads,
   231â†’      memoryUsage: context.metrics.memoryUsage,
   232â†’      cpuUsage: context.metrics.cpuUsage,
   233â†’      queueBacklog: context.metrics.queuedTasks,
   234â†’      throughput: context.metrics.completedTasks / (Date.now() / 1000 / 60), // tasks per minute
   235â†’      errorRate: context.metrics.failedTasks / Math.max(context.metrics.completedTasks + context.metrics.failedTasks, 1),
   236â†’      uptime: Date.now() - (performanceMetricsRef.current?.uptime || Date.now()),
   237â†’    };
   238â†’
   239â†’    performanceMetricsRef.current = metrics;
   240â†’    return metrics;
   241â†’  }, [context]);
   242â†’
   243â†’  // Advanced task cancellation with cleanup
   244â†’  const cancelAdvancedTask = useCallback(async (taskId: string): Promise<boolean> => {
   245â†’    const cancelled = await context.cancelTask(taskId);
   246â†’    
   247â†’    if (cancelled) {
   248â†’      setActiveOperations(prev => {
   249â†’        const newSet = new Set(prev);
   250â†’        newSet.delete(taskId);
   251â†’        return newSet;
   252â†’      });
   253â†’      
   254â†’      setTaskHistory(prev => {
   255â†’        const newMap = new Map(prev);
   256â†’        const existing = newMap.get(taskId);
   257â†’        if (existing) {
   258â†’          newMap.set(taskId, { ...existing, status: 'cancelled' });
   259â†’        }
   260â†’        return newMap;
   261â†’      });
   262â†’    }
   263â†’    
   264â†’    return cancelled;
   265â†’  }, [context]);
   266â†’
   267â†’  // Health monitoring with proactive alerts
   268â†’  const monitorHealth = useCallback(() => {
   269â†’    const health = context.getHealthStatus();
   270â†’    const systemMetrics = getSystemMetrics();
   271â†’    
   272â†’    return {
