   274â†’      systemMetrics,
   275â†’      alerts: [] as string[], // Would contain health alerts
   276â†’      recommendations: [] as string[], // Performance recommendations
   277â†’    };
   278â†’  }, [context, getSystemMetrics]);
   279â†’
   280â†’  return {
