   287â†’    
   288â†’    // Enhanced task management
   289â†’    submitTask: submitAdvancedTask,
   290â†’    submitBatch: submitTaskBatch,
   291â†’    cancelTask: cancelAdvancedTask,
   292â†’    
   293â†’    // Lifecycle management
   294â†’    initialize: context.initialize,
