   296â†’    scaleThreads: context.scaleThreads,
   297â†’    pauseThreadPool: context.pauseThreadPool,
   298â†’    resumeThreadPool: context.resumeThreadPool,
   299â†’    
   300â†’    // Monitoring and metrics
   301â†’    getThreadPoolMetrics,
   302â†’    getSystemMetrics,
   303â†’    monitorHealth,
   304â†’    subscribeToMetrics: context.subscribeToMetrics,
   305â†’    getHealthStatus: context.getHealthStatus,
   306â†’    
   307â†’    // Subagent coordination
   308â†’    subagentRegistry: context.subagentRegistry,
   309â†’    publishToSubagents: context.publishToSubagents,
   310â†’    subscribeToSubagentEvents: context.subscribeToSubagentEvents,
