   424â†’// Hook for batch processing with progress tracking
   425â†’export function useBatchProcessor<T>() {
   426â†’  const multithreading = useAdvancedMultithreading();
   427â†’  const [batchState, setBatchState] = useState<{
   428â†’    isProcessing: boolean;
   429â†’    progress: number;
   430â†’    completedTasks: number;
   431â†’    totalTasks: number;
   432â†’    errors: string[];
   433â†’    results: AdvancedTaskResult<T>[];
   434â†’  }>({
   435â†’    isProcessing: false,
   436â†’    progress: 0,
   437â†’    completedTasks: 0,
   438â†’    totalTasks: 0,
   439â†’    errors: [],
   440â†’    results: [],
   441â†’  });
   442â†’
   443â†’  const processBatch = useCallback(async (
   444â†’    tasks: AdvancedThreadTask<T>[],
   445â†’    options: {
   446â†’      maxConcurrency?: number;
   447â†’      progressCallback?: (progress: number, completed: number, total: number) => void;
   448â†’      errorHandling?: 'fail-fast' | 'continue' | 'retry';
   449â†’    } = {}
   450â†’  ) => {
   451â†’    setBatchState({
   452â†’      isProcessing: true,
   453â†’      progress: 0,
   454â†’      completedTasks: 0,
   455â†’      totalTasks: tasks.length,
   456â†’      errors: [],
   457â†’      results: [],
   458â†’    });
   459â†’
   460â†’    try {
   461â†’      const results = await multithreading.submitBatch(tasks, {
   462â†’        maxConcurrency: options.maxConcurrency,
   463â†’        failureHandling: options.errorHandling,
   464â†’      });
   465â†’
   466â†’      const errors = results.filter(r => r.status === 'failed').map(r => r.error || 'Unknown error');
   467â†’      
   468â†’      setBatchState({
   469â†’        isProcessing: false,
   470â†’        progress: 100,
   471â†’        completedTasks: results.filter(r => r.status === 'completed').length,
   472â†’        totalTasks: tasks.length,
   473â†’        errors,
   474â†’        results,
   475â†’      });
   476â†’
   477â†’      return results;
   478â†’    } catch (error) {
   479â†’      setBatchState(prev => ({
   480â†’        ...prev,
   481â†’        isProcessing: false,
   482â†’        errors: [...prev.errors, error instanceof Error ? error.message : 'Batch processing failed'],
   483â†’      }));
   484â†’      throw error;
   485â†’    }
   486â†’  }, [multithreading]);
   487â†’
   488â†’  return {
   489â†’    ...batchState,
   490â†’    processBatch,
   491â†’    cancelBatch: () => {
   492â†’      // This would need implementation to cancel all running tasks
   493â†’      setBatchState(prev => ({ ...prev, isProcessing: false }));
   494â†’    },
   495â†’  };
   496â†’}
