   498â†’// Hook for subagent coordination
   499â†’export function useSubagentCoordination() {
   500â†’  const multithreading = useAdvancedMultithreading();
   501â†’  const [subagents, setSubagents] = useState<Map<string, { capabilities: string[], lastSeen: number }>>(new Map());
   502â†’  const [taskQueue, setTaskQueue] = useState<any[]>([]);
   503â†’
   504â†’  const registerSubagent = useCallback(async (id: string, capabilities: string[]) => {
   505â†’    await multithreading.subagentRegistry.register(id, capabilities);
   506â†’    setSubagents(prev => new Map([...prev, [id, { capabilities, lastSeen: Date.now() }]]));
   507â†’  }, [multithreading]);
   508â†’
   509â†’  const requestSubagentWork = useCallback(async (capability: string, task: any) => {
   510â†’    const availableAgents = await multithreading.subagentRegistry.findAvailable(capability);
   511â†’    
   512â†’    if (availableAgents.length === 0) {
   513â†’      throw new Error(`No subagents available with capability: ${capability}`);
   514â†’    }
   515â†’
   516â†’    // Simple load balancing - pick first available
   517â†’    const selectedAgent = availableAgents[0];
   518â†’    return multithreading.subagentRegistry.requestWork(selectedAgent, task);
   519â†’  }, [multithreading]);
   520â†’
   521â†’  const broadcastToSubagents = useCallback(async (event: string, data: any) => {
   522â†’    return multithreading.publishToSubagents(event, data);
   523â†’  }, [multithreading]);
   524â†’
   525â†’  useEffect(() => {
   526â†’    if (!multithreading.isInitialized) return;
   527â†’
   528â†’    const unsubscribeRegistration = multithreading.subscribeToSubagentEvents('agent.registered', (data) => {
   529â†’      setSubagents(prev => new Map([...prev, [data.id, { capabilities: data.capabilities, lastSeen: Date.now() }]]));
   530â†’    });
   531â†’
   532â†’    const unsubscribeUnregistration = multithreading.subscribeToSubagentEvents('agent.unregistered', (data) => {
   533â†’      setSubagents(prev => {
   534â†’        const newMap = new Map(prev);
   535â†’        newMap.delete(data.id);
   536â†’        return newMap;
   537â†’      });
   538â†’    });
   539â†’
   540â†’    return () => {
   541â†’      unsubscribeRegistration();
   542â†’      unsubscribeUnregistration();
   543â†’    };
   544â†’  }, [multithreading]);
   545â†’
   546â†’  return {
   547â†’    subagents: Array.from(subagents.entries()).map(([id, info]) => ({ id, ...info })),
   548â†’    taskQueue,
   549â†’    registerSubagent,
   550â†’    requestSubagentWork,
   551â†’    broadcastToSubagents,
   552â†’    isCoordinatorReady: multithreading.isInitialized,
   553â†’  };
   554â†’}
