import { createContext, useContext, useState, useEffect, ReactNode } from 'react'

interface KatalystContextType {
  theme: 'light' | 'dark'
  setTheme: (theme: 'light' | 'dark') => void
  isInitialized: boolean
  performance: {
    reactVersion: string
    renderCount: number
    lastRenderTime: number
  }
}

const KatalystContext = createContext<KatalystContextType | undefined>(undefined)

export function KatalystProvider({ children }: { children: ReactNode }) {
  const [theme, setTheme] = useState<'light' | 'dark'>('dark')
  const [isInitialized, setIsInitialized] = useState(false)
  const [performance, setPerformance] = useState({
    reactVersion: '19.0.0-rc.0',
    renderCount: 0,
    lastRenderTime: 0
  })

  useEffect(() => {
    // Initialize Katalyst framework
    const startTime = performance.now()

    // Simulate framework initialization
    setTimeout(() => {
      setIsInitialized(true)
      const endTime = performance.now()
      setPerformance(prev => ({
        ...prev,
        renderCount: prev.renderCount + 1,
        lastRenderTime: endTime - startTime
      }))
    }, 100)
  }, [])

  const value: KatalystContextType = {
    theme,
    setTheme,
    isInitialized,
    performance
  }

  return (
    <KatalystContext.Provider value={value}>
      {children}
    </KatalystContext.Provider>
  )
}

export function useKatalyst() {
  const context = useContext(KatalystContext)
  if (context === undefined) {
    throw new Error('useKatalyst must be used within a KatalystProvider')
  }
  return context
}

