import { useState, useCallback, useEffect } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Play, Square, Download, Code, Zap } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { Badge } from '@/components/ui/badge'
import { useWasm } from '@/components/providers/WasmProvider'
import { MandelbrotVisualization } from './MandelbrotVisualization'
import { PerformanceChart } from '@/components/performance/PerformanceChart'

interface ComputationResult {
  name: string
  result: any
  executionTime: number
  timestamp: string
  status: 'success' | 'error'
  error?: string
}

interface JuliaComputeShowcaseProps {
  wasmReady: boolean
  performanceMetrics: any
}

const DEMO_COMPUTATIONS = [
  {
    id: 'fibonacci',
    name: 'Fibonacci Sequence',
    description: 'Generate Fibonacci numbers using optimized Julia implementation',
    icon: 'ðŸ”¢',
    defaultArgs: [25],
    argLabels: ['Number of terms'],
    code: `function julia_fibonacci(n::Int32)::Vector{Float64}
    result = Vector{Float64}(undef, n)
    if n >= 1; result[1] = 1.0; end
    if n >= 2; result[2] = 1.0; end

    for i in 3:n
        result[i] = result[i-1] + result[i-2]
    end

    return result
end`
  },
  {
    id: 'mandelbrot',
    name: 'Mandelbrot Set',
    description: 'Generate fractal data with parallel computation',
    icon: 'ðŸŒ€',
    defaultArgs: [400, 400, 100],
    argLabels: ['Width', 'Height', 'Max Iterations'],
    code: `function julia_mandelbrot(width::Int32, height::Int32, max_iter::Int32)
    result = Matrix{Int32}(undef, height, width)

    Threads.@threads for i in 1:height
        # Complex plane computation...
    end

    return result
end`
  },
  {
    id: 'matrixMultiply',
    name: 'Matrix Multiplication',
    description: 'High-performance matrix operations using Julia BLAS',
    icon: 'ðŸ”²',
    defaultArgs: [256],
    argLabels: ['Matrix Size'],
    code: `function julia_matrix_multiply(size::Int32)
    A = randn(size, size)
    B = randn(size, size)

    start_time = time_ns()
    C = A * B
    end_time = time_ns()

    return C, (end_time - start_time) / 1e9
end`
  },
  {
    id: 'primeSieve',
    name: 'Prime Sieve',
    description: 'Generate prime numbers using Sieve of Eratosthenes',
    icon: 'ðŸ”',
    defaultArgs: [10000],
    argLabels: ['Upper Limit'],
    code: `function julia_prime_sieve(limit::Int32)
    sieve = trues(limit)
    sieve[1] = false

    for i in 2:Int32(sqrt(limit))
        if sieve[i]
            for j in (i*i):i:limit
                sieve[j] = false
            end
        end
    end

    return findall(sieve)[2:end]
end`
  },
  {
    id: 'monteCarloPI',
    name: 'Monte Carlo Ï€',
    description: 'Estimate Ï€ using random sampling',
    icon: 'ðŸŽ¯',
    defaultArgs: [1000000],
    argLabels: ['Sample Count'],
    code: `function julia_monte_carlo_pi(n_samples::Int32)
    hits = 0
    for _ in 1:n_samples
        x, y = rand() * 2 - 1, rand() * 2 - 1
