end`
  }
]

export function JuliaComputeShowcase({ wasmReady, performanceMetrics }: JuliaComputeShowcaseProps) {
  const { executeComputation, isReady, error } = useWasm()
  const [selectedDemo, setSelectedDemo] = useState(DEMO_COMPUTATIONS[0])
  const [isRunning, setIsRunning] = useState(false)
  const [results, setResults] = useState<ComputationResult[]>([])
  const [currentArgs, setCurrentArgs] = useState(selectedDemo.defaultArgs)

  const runComputation = useCallback(async () => {
    if (!isReady || isRunning) return

    setIsRunning(true)
    const startTime = performance.now()

    try {
      const computationResult = await executeComputation(selectedDemo.id, currentArgs)
      const endTime = performance.now()

      const result: ComputationResult = {
        name: selectedDemo.name,
        result: computationResult.result,
        executionTime: endTime - startTime,
        timestamp: new Date().toISOString(),
        status: 'success'
      }

      setResults(prev => [result, ...prev.slice(0, 9)]) // Keep last 10 results
    } catch (err) {
      const result: ComputationResult = {
        name: selectedDemo.name,
        result: null,
        executionTime: 0,
        timestamp: new Date().toISOString(),
        status: 'error',
        error: err instanceof Error ? err.message : 'Unknown error'
      }

      setResults(prev => [result, ...prev.slice(0, 9)])
    } finally {
      setIsRunning(false)
    }
  }, [executeComputation, selectedDemo, currentArgs, isReady, isRunning])

  const formatResult = (result: any, computation: string) => {
    if (result === null || result === undefined) return 'No result'

    switch (computation) {
      case 'Fibonacci Sequence':
        return Array.isArray(result) ? 
          `[${result.slice(0, 10).map(n => n.toFixed(0)).join(', ')}${result.length > 10 ? '...' : ''}]` :
          result.toString()
      case 'Prime Sieve':
        return Array.isArray(result) ?
          `${result.length} primes found. First 10: [${result.slice(0, 10).join(', ')}${result.length > 10 ? '...' : ''}]` :
          result.toString()
      case 'Monte Carlo Ï€':
        return typeof result === 'object' && result.piEstimate ?
          `Ï€ â‰ˆ ${result.piEstimate.toFixed(6)}` :
          result.toString()
      case 'Matrix Multiplication':
        return typeof result === 'object' && result.matrix ?
          `${result.matrix.length}Ã—${result.matrix[0]?.length} matrix computed` :
          result.toString()
      case 'Mandelbrot Set':
        return Array.isArray(result) ?
          `${result.length}Ã—${result[0]?.length} fractal data generated` :
          result.toString()
      default:
        return typeof result === 'object' ? JSON.stringify(result).slice(0, 100) + '...' : result.toString()
    }
  }

  useEffect(() => {
    setCurrentArgs(selectedDemo.defaultArgs)
  }, [selectedDemo])

  if (error) {
    return (
      <Card className="border-red-500/20 bg-red-900/10">
        <CardContent className="pt-6">
          <div className="text-center text-red-400">
            <Square className="h-12 w-12 mx-auto mb-4" />
            <p className="text-lg font-semibold mb-2">WASM Module Error</p>
            <p className="text-sm">{error}</p>
          </div>
        </CardContent>
      </Card>
    )
  }

  return (
    <div className="space-y-6">
      <Tabs value={selectedDemo.id} onValueChange={(value) => {
        const demo = DEMO_COMPUTATIONS.find(d => d.id === value)
        if (demo) setSelectedDemo(demo)
      }}>
        <TabsList className="grid grid-cols-5 w-full bg-gray-900/50">
          {DEMO_COMPUTATIONS.map((demo) => (
            <TabsTrigger 
              key={demo.id} 
              value={demo.id}
              className="text-xs data-[state=active]:bg-purple-600 data-[state=active]:text-white"
            >
              <span className="mr-1">{demo.icon}</span>
              {demo.name.split(' ')[0]}
            </TabsTrigger>
          ))}
        </TabsList>

        {DEMO_COMPUTATIONS.map((demo) => (
          <TabsContent key={demo.id} value={demo.id} className="mt-6">
            <div className="grid lg:grid-cols-2 gap-6">
              {/* Computation Panel */}
              <Card className="bg-gray-900/50 border-gray-700">
                <CardHeader>
                  <div className="flex items-center gap-2">
                    <span className="text-2xl">{demo.icon}</span>
                    <div>
                      <CardTitle className="text-white">{demo.name}</CardTitle>
                      <CardDescription>{demo.description}</CardDescription>
                    </div>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  {/* Parameters */}
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-gray-300">Parameters:</label>
                    <div className="grid gap-2">
                      {demo.argLabels.map((label, index) => (
                        <div key={index} className="flex items-center gap-2">
                          <span className="text-sm text-gray-400 w-20">{label}:</span>
                          <input
                            type="number"
                            value={currentArgs[index] || 0}
                            onChange={(e) => {
                              const newArgs = [...currentArgs]
                              newArgs[index] = parseInt(e.target.value) || 0
                              setCurrentArgs(newArgs)
                            }}
                            className="flex-1 px-2 py-1 text-sm bg-gray-800 border border-gray-600 rounded text-white"
                          />
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Controls */}
                  <div className="flex gap-2">
                    <Button
                      onClick={runComputation}
                      disabled={!isReady || isRunning}
                      className="flex-1 bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700"
                    >
                      {isRunning ? (
                        <>
                          <Square className="mr-2 h-4 w-4" />
                          Running...
                        </>
                      ) : (
                        <>
                          <Play className="mr-2 h-4 w-4" />
                          Execute Julia Code
                        </>
                      )}
                    </Button>

                    <Badge variant={isReady ? "default" : "secondary"} className="px-3 py-1">
                      <Zap className="mr-1 h-3 w-3" />
                      {isReady ? 'WASM Ready' : 'Loading...'}
                    </Badge>
                  </div>

                  {/* Code Display */}
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-gray-300">Julia Code:</label>
                    <pre className="julia-code text-xs overflow-x-auto">
                      <code>{demo.code}</code>
                    </pre>
                  </div>
                </CardContent>
              </Card>

              {/* Results Panel */}
              <Card className="bg-gray-900/50 border-gray-700">
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-white">Execution Results</CardTitle>
                    {results.length > 0 && (
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => {
                          const data = JSON.stringify(results, null, 2)
                          const blob = new Blob([data], { type: 'application/json' })
                          const url = URL.createObjectURL(blob)
                          const a = document.createElement('a')
                          a.href = url
                          a.download = 'julia-compute-results.json'
                          a.click()
                        }}
                      >
                        <Download className="mr-2 h-4 w-4" />
                        Export
                      </Button>
                    )}
                  </div>
                </CardHeader>
                <CardContent>
                  <AnimatePresence>
                    {results.length === 0 ? (
                      <div className="text-center py-8 text-gray-500">
                        <Code className="h-12 w-12 mx-auto mb-4 opacity-50" />
                        <p>No computations run yet. Click "Execute Julia Code" to start.</p>
                      </div>
                    ) : (
                      <div className="space-y-3 max-h-80 overflow-y-auto">
                        {results.map((result, index) => (
                          <motion.div
                            key={`${result.timestamp}-${index}`}
                            initial={{ opacity: 0, y: -20 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: -20 }}
                            className={`p-3 rounded border ${
                              result.status === 'success' 
                                ? 'border-green-500/20 bg-green-900/10' 
                                : 'border-red-500/20 bg-red-900/10'
                            }`}
                          >
                            <div className="flex items-center justify-between mb-2">
                              <span className="text-sm font-medium text-white">
                                {result.name}
                              </span>
                              <span className="text-xs text-gray-400">
                                {result.executionTime.toFixed(2)}ms
                              </span>
                            </div>
                            <div className="text-sm text-gray-300">
                              {result.status === 'success' ? (
                                formatResult(result.result, result.name)
                              ) : (
                                <span className="text-red-400">Error: {result.error}</span>
                              )}
                            </div>
                          </motion.div>
                        ))}
                      </div>
                    )}
                  </AnimatePresence>
                </CardContent>
              </Card>
            </div>

            {/* Special Visualizations */}
            {demo.id === 'mandelbrot' && results.length > 0 && results[0].status === 'success' && (
              <div className="mt-6">
                <MandelbrotVisualization 
                  data={results[0].result} 
                  width={currentArgs[0]} 
                  height={currentArgs[1]} 
                />
              </div>
            )}
          </TabsContent>
        ))}
      </Tabs>

      {/* Performance Chart */}
      {results.length > 0 && (
        <Card className="bg-gray-900/50 border-gray-700">
          <CardHeader>
            <CardTitle className="text-white">Performance Timeline</CardTitle>
            <CardDescription>Execution times for recent computations</CardDescription>
          </CardHeader>
          <CardContent>
            <PerformanceChart data={results.slice(0, 10).reverse()} />
          </CardContent>
        </Card>
      )}
    </div>
  )
}

