export function JuliaComputeShowcase({ wasmReady, performanceMetrics }: JuliaComputeShowcaseProps) {
  const { executeComputation, isReady, error } = useWasm()
  const { 
    executeAdvancedTask, 
    createComputePool, 
    getThreadingMetrics, 
    threadingReady 
  } = useAdvancedCompute()

  // Advanced multithreading hooks
  const { coordinateSubagents, getActiveSubagents } = useSubagentCoordination()
  const { metrics: threadMetrics } = useThreadMonitoring()

  const [selectedDemo, setSelectedDemo] = useState(DEMO_COMPUTATIONS[0])
  const [isRunning, setIsRunning] = useState(false)
  const [results, setResults] = useState<ComputationResult[]>([])
  const [currentArgs, setCurrentArgs] = useState(selectedDemo.defaultArgs)
  const [progress, setProgress] = useState(0)
  const [activeTask, setActiveTask] = useState<AdvancedComputeTask | null>(null)
  const [useAdvancedMode, setUseAdvancedMode] = useState(true)

  // Create dedicated compute pool for Julia operations
  const computePool = useMemo(() => {
    if (!threadingReady) return null

    try {
      return createComputePool('julia-showcase', {
        threads: Math.min(navigator.hardwareConcurrency || 4, 8),
        threadName: 'julia-worker',
        priority: 'high'
      })
    } catch (err) {
      console.warn('Failed to create compute pool:', err)
      return null
    }
  }, [threadingReady, createComputePool])

  const runComputation = useCallback(async () => {
    if (!isReady || isRunning) return

    setIsRunning(true)
    setProgress(0)
    const startTime = performance.now()
    const taskId = `task-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`

    try {
      let computationResult
      let subagentUsed = 'none'
      let threadId = 'main'

      if (useAdvancedMode && threadingReady && computePool) {
        // Create advanced task for multithreaded execution
        const advancedTask: AdvancedComputeTask = {
          id: taskId,
          type: 'julia',
          operation: selectedDemo.id,
          data: currentArgs,
          priority: selectedDemo.complexity === 'high' ? 'high' : 'normal',
          timeout: selectedDemo.estimatedTime * 2,
          retries: 2,
          subagentRequirement: selectedDemo.subagentType
        }

        setActiveTask(advancedTask)

        // Coordinate with subagents for complex tasks
        if (selectedDemo.complexity === 'high') {
          const subagents = await coordinateSubagents([
            { type: selectedDemo.subagentType, capability: selectedDemo.id }
          ])
          subagentUsed = subagents[0]?.id || 'fallback'
        }

        // Progress simulation for demonstration
        const progressInterval = setInterval(() => {
          setProgress(prev => Math.min(prev + 10, 90))
        }, selectedDemo.estimatedTime / 10)

        computationResult = await executeAdvancedTask(advancedTask)
        clearInterval(progressInterval)
        setProgress(100)

        threadId = computationResult.threadId || 'pool-thread'
      } else {
        // Fallback to basic execution
        computationResult = await executeComputation(selectedDemo.id, currentArgs)
      }

      const endTime = performance.now()
      const currentMetrics = getThreadingMetrics()

      const result: ComputationResult = {
        name: selectedDemo.name,
        result: computationResult.result,
        executionTime: endTime - startTime,
        timestamp: new Date().toISOString(),
        status: 'success',
        taskId,
        threadId,
        subagentUsed,
        performance: {
          memoryUsed: currentMetrics?.memoryUsageMb || 0,
          cpuUsage: currentMetrics?.cpuUsage || 0,
          threadsUsed: currentMetrics?.activeThreads || 1
        }
      }

      setResults(prev => [result, ...prev.slice(0, 9)]) // Keep last 10 results
    } catch (err) {
      const result: ComputationResult = {
        name: selectedDemo.name,
        result: null,
        executionTime: 0,
        timestamp: new Date().toISOString(),
        status: 'error',
        error: err instanceof Error ? err.message : 'Unknown error',
        taskId
      }

      setResults(prev => [result, ...prev.slice(0, 9)])
    } finally {
      setIsRunning(false)
      setProgress(0)
      setActiveTask(null)
    }
  }, [
    executeComputation, executeAdvancedTask, selectedDemo, currentArgs, 
    isReady, isRunning, useAdvancedMode, threadingReady, computePool,
    coordinateSubagents, getThreadingMetrics
  ])

  const formatResult = (result: any, computation: string) => {
    if (result === null || result === undefined) return 'No result'

    switch (computation) {
      case 'Fibonacci Sequence':
        return Array.isArray(result) ? 
          `[${result.slice(0, 10).map(n => n.toFixed(0)).join(', ')}${result.length > 10 ? '...' : ''}]` :
          result.toString()
      case 'Prime Sieve':
        return Array.isArray(result) ?
          `${result.length} primes found. First 10: [${result.slice(0, 10).join(', ')}${result.length > 10 ? '...' : ''}]` :
          result.toString()
      case 'Monte Carlo Ï€':
        return typeof result === 'object' && result.piEstimate ?
          `Ï€ â‰ˆ ${result.piEstimate.toFixed(6)}` :
          result.toString()
      case 'Matrix Multiplication':
        return typeof result === 'object' && result.matrix ?
          `${result.matrix.length}Ã—${result.matrix[0]?.length} matrix computed` :
          result.toString()
      case 'Mandelbrot Set':
        return Array.isArray(result) ?
          `${result.length}Ã—${result[0]?.length} fractal data generated` :
          result.toString()
      default:
        return typeof result === 'object' ? JSON.stringify(result).slice(0, 100) + '...' : result.toString()
    }
  }

  useEffect(() => {
    setCurrentArgs(selectedDemo.defaultArgs)
  }, [selectedDemo])

