                          </motion.div>
                        ))}
                      </div>
                    )}
                  </AnimatePresence>
                </CardContent>
              </Card>
            </div>

            {/* Special Visualizations */}
            {demo.id === 'mandelbrot' && results.length > 0 && results[0].status === 'success' && (
              <div className="mt-6">
                <MandelbrotVisualization 
                  data={results[0].result} 
                  width={currentArgs[0]} 
                  height={currentArgs[1]} 
                />
              </div>
            )}
          </TabsContent>
        ))}
      </Tabs>

      {/* Performance Chart */}
      {results.length > 0 && (
        <Card className="bg-gray-900/50 border-gray-700">
          <CardHeader>
            <CardTitle className="text-white">Performance Timeline</CardTitle>
            <CardDescription>Execution times for recent computations</CardDescription>
          </CardHeader>
          <CardContent>
            <PerformanceChart data={results.slice(0, 10).reverse()} />
          </CardContent>
        </Card>
      )}
    </div>
  )
}

