'use client'

import { useState, useCallback, useEffect, useMemo } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Play, Square, Download, Code, Zap, Settings, Activity, Cpu } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { Badge } from '@/components/ui/badge'
import { Progress } from '@/components/ui/progress'
import { useWasm, useAdvancedCompute } from '@/components/providers/WasmProvider'
import { useSubagentCoordination, useThreadMonitoring } from '@katalyst/hooks'
import { MandelbrotVisualization } from './MandelbrotVisualization'
import { PerformanceChart } from '@/components/performance/PerformanceChart'

interface ComputationResult {
  name: string
  result: any
  executionTime: number
  timestamp: string
  status: 'success' | 'error' | 'running'
  error?: string
  taskId?: string
  threadId?: string
  subagentUsed?: string
  performance?: {
    memoryUsed: number
    cpuUsage: number
    threadsUsed: number
  }
}

interface AdvancedComputeTask {
  id: string
  type: 'cpu' | 'gpu' | 'wasm' | 'julia'
  operation: string
  data: any
  priority: 'low' | 'normal' | 'high' | 'critical'
  timeout?: number
  retries?: number
  dependencies?: string[]
  subagentRequirement?: string
}

interface JuliaComputeShowcaseProps {
  wasmReady: boolean
  performanceMetrics: any
}

const DEMO_COMPUTATIONS = [
  {
    id: 'fibonacci',
    name: 'Fibonacci Sequence',
    description: 'Generate Fibonacci numbers using optimized Julia implementation',
    icon: 'ðŸ”¢',
    defaultArgs: [25],
    argLabels: ['Number of terms'],
    complexity: 'low',
    subagentType: 'numerical',
    estimatedTime: 100,
    code: `function julia_fibonacci(n::Int32)::Vector{Float64}
    result = Vector{Float64}(undef, n)
    if n >= 1; result[1] = 1.0; end
    if n >= 2; result[2] = 1.0; end

    for i in 3:n
        result[i] = result[i-1] + result[i-2]
    end

    return result
end`
  },
  {
    id: 'mandelbrot',
    name: 'Mandelbrot Set',
    description: 'Generate fractal data with parallel computation',
    icon: 'ðŸŒ€',
    defaultArgs: [400, 400, 100],
    argLabels: ['Width', 'Height', 'Max Iterations'],
    complexity: 'high',
    subagentType: 'visualization',
    estimatedTime: 2000,
    code: `function julia_mandelbrot(width::Int32, height::Int32, max_iter::Int32)
    result = Matrix{Int32}(undef, height, width)

    Threads.@threads for i in 1:height
        # Complex plane computation...
    end

    return result
end`
  },
  {
    id: 'matrixMultiply',
    name: 'Matrix Multiplication',
    description: 'High-performance matrix operations using Julia BLAS',
    icon: 'ðŸ”²',
    defaultArgs: [256],
    argLabels: ['Matrix Size'],
    complexity: 'high',
    subagentType: 'numerical',
    estimatedTime: 1500,
    code: `function julia_matrix_multiply(size::Int32)
    A = randn(size, size)
    B = randn(size, size)

    start_time = time_ns()
    C = A * B
    end_time = time_ns()

    return C, (end_time - start_time) / 1e9
end`
  },
  {
    id: 'primeSieve',
    name: 'Prime Sieve',
    description: 'Generate prime numbers using Sieve of Eratosthenes',
    icon: 'ðŸ”',
    defaultArgs: [10000],
    argLabels: ['Upper Limit'],
    complexity: 'medium',
    subagentType: 'numerical',
    estimatedTime: 500,
    code: `function julia_prime_sieve(limit::Int32)
    sieve = trues(limit)
    sieve[1] = false

    for i in 2:Int32(sqrt(limit))
        if sieve[i]
            for j in (i*i):i:limit
                sieve[j] = false
            end
        end
    end

    return findall(sieve)[2:end]
end`
  },
  {
    id: 'monteCarloPI',
    name: 'Monte Carlo Ï€',
    description: 'Estimate Ï€ using random sampling',
    icon: 'ðŸŽ¯',
    defaultArgs: [1000000],
    argLabels: ['Sample Count'],
    complexity: 'medium',
    subagentType: 'statistical',
    estimatedTime: 800,
    code: `function julia_monte_carlo_pi(n_samples::Int32)
    hits = 0
    for _ in 1:n_samples
        x, y = rand() * 2 - 1, rand() * 2 - 1
        if x^2 + y^2 â‰¤ 1.0
            hits += 1
        end
    end

    return 4.0 * hits / n_samples
end`
  }
]

export function JuliaComputeShowcase({ wasmReady, performanceMetrics }: JuliaComputeShowcaseProps) {
  const { executeComputation, isReady, error } = useWasm()
  const { 
    executeAdvancedTask, 
    createComputePool, 
    getThreadingMetrics, 
    threadingReady 
  } = useAdvancedCompute()

  // Advanced multithreading hooks
  const { coordinateSubagents, getActiveSubagents } = useSubagentCoordination()
  const { metrics: threadMetrics } = useThreadMonitoring()

  const [selectedDemo, setSelectedDemo] = useState(DEMO_COMPUTATIONS[0])
  const [isRunning, setIsRunning] = useState(false)
  const [results, setResults] = useState<ComputationResult[]>([])
  const [currentArgs, setCurrentArgs] = useState(selectedDemo.defaultArgs)
  const [progress, setProgress] = useState(0)
  const [activeTask, setActiveTask] = useState<AdvancedComputeTask | null>(null)
  const [useAdvancedMode, setUseAdvancedMode] = useState(true)

  // Create dedicated compute pool for Julia operations
  const computePool = useMemo(() => {
    if (!threadingReady) return null

    try {
      return createComputePool('julia-showcase', {
        threads: Math.min(navigator.hardwareConcurrency || 4, 8),
        threadName: 'julia-worker',
        priority: 'high'
      })
    } catch (err) {
      console.warn('Failed to create compute pool:', err)
      return null
    }
  }, [threadingReady, createComputePool])

  const runComputation = useCallback(async () => {
    if (!isReady || isRunning) return

    setIsRunning(true)
    setProgress(0)
    const startTime = performance.now()
    const taskId = `task-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`

    try {
      let computationResult
      let subagentUsed = 'none'
      let threadId = 'main'

      if (useAdvancedMode && threadingReady && computePool) {
        // Create advanced task for multithreaded execution
        const advancedTask: AdvancedComputeTask = {
          id: taskId,
          type: 'julia',
          operation: selectedDemo.id,
          data: currentArgs,
          priority: selectedDemo.complexity === 'high' ? 'high' : 'normal',
          timeout: selectedDemo.estimatedTime * 2,
          retries: 2,
          subagentRequirement: selectedDemo.subagentType
        }

        setActiveTask(advancedTask)

        // Coordinate with subagents for complex tasks
        if (selectedDemo.complexity === 'high') {
          const subagents = await coordinateSubagents([
            { type: selectedDemo.subagentType, capability: selectedDemo.id }
          ])
          subagentUsed = subagents[0]?.id || 'fallback'
        }

        // Progress simulation for demonstration
        const progressInterval = setInterval(() => {
          setProgress(prev => Math.min(prev + 10, 90))
        }, selectedDemo.estimatedTime / 10)

        computationResult = await executeAdvancedTask(advancedTask)
        clearInterval(progressInterval)
        setProgress(100)

        threadId = computationResult.threadId || 'pool-thread'
      } else {
        // Fallback to basic execution
        computationResult = await executeComputation(selectedDemo.id, currentArgs)
      }

      const endTime = performance.now()
      const currentMetrics = getThreadingMetrics()

      const result: ComputationResult = {
        name: selectedDemo.name,
        result: computationResult.result,
        executionTime: endTime - startTime,
        timestamp: new Date().toISOString(),
        status: 'success',
        taskId,
        threadId,
        subagentUsed,
        performance: {
          memoryUsed: currentMetrics?.memoryUsageMb || 0,
          cpuUsage: currentMetrics?.cpuUsage || 0,
          threadsUsed: currentMetrics?.activeThreads || 1
        }
      }

      setResults(prev => [result, ...prev.slice(0, 9)]) // Keep last 10 results
    } catch (err) {
      const result: ComputationResult = {
        name: selectedDemo.name,
        result: null,
        executionTime: 0,
        timestamp: new Date().toISOString(),
        status: 'error',
        error: err instanceof Error ? err.message : 'Unknown error',
        taskId
      }

      setResults(prev => [result, ...prev.slice(0, 9)])
    } finally {
      setIsRunning(false)
      setProgress(0)
      setActiveTask(null)
    }
  }, [
    executeComputation, executeAdvancedTask, selectedDemo, currentArgs, 
    isReady, isRunning, useAdvancedMode, threadingReady, computePool,
    coordinateSubagents, getThreadingMetrics
  ])

  const formatResult = (result: any, computation: string) => {
    if (result === null || result === undefined) return 'No result'

    switch (computation) {
      case 'Fibonacci Sequence':
        return Array.isArray(result) ? 
          `[${result.slice(0, 10).map(n => n.toFixed(0)).join(', ')}${result.length > 10 ? '...' : ''}]` :
          result.toString()
      case 'Prime Sieve':
        return Array.isArray(result) ?
          `${result.length} primes found. First 10: [${result.slice(0, 10).join(', ')}${result.length > 10 ? '...' : ''}]` :
          result.toString()
      case 'Monte Carlo Ï€':
        return typeof result === 'object' && result.piEstimate ?
          `Ï€ â‰ˆ ${result.piEstimate.toFixed(6)}` :
          result.toString()
      case 'Matrix Multiplication':
        return typeof result === 'object' && result.matrix ?
          `${result.matrix.length}Ã—${result.matrix[0]?.length} matrix computed` :
          result.toString()
      case 'Mandelbrot Set':
        return Array.isArray(result) ?
          `${result.length}Ã—${result[0]?.length} fractal data generated` :
          result.toString()
      default:
        return typeof result === 'object' ? JSON.stringify(result).slice(0, 100) + '...' : result.toString()
    }
  }

  useEffect(() => {
    setCurrentArgs(selectedDemo.defaultArgs)
  }, [selectedDemo])

  if (error) {
    return (
      <Card className="border-red-500/20 bg-red-900/10">
        <CardContent className="pt-6">
          <div className="text-center text-red-400">
            <Square className="h-12 w-12 mx-auto mb-4" />
            <p className="text-lg font-semibold mb-2">WASM Module Error</p>
            <p className="text-sm">{error}</p>
          </div>
        </CardContent>
      </Card>
    )
  }

  return (
    <div className="space-y-6">
      <Tabs value={selectedDemo.id} onValueChange={(value) => {
        const demo = DEMO_COMPUTATIONS.find(d => d.id === value)
        if (demo) setSelectedDemo(demo)
      }}>
        <TabsList className="grid grid-cols-5 w-full bg-gray-900/50">
          {DEMO_COMPUTATIONS.map((demo) => (
            <TabsTrigger 
              key={demo.id} 
              value={demo.id}
              className="text-xs data-[state=active]:bg-purple-600 data-[state=active]:text-white"
            >
              <span className="mr-1">{demo.icon}</span>
              {demo.name.split(' ')[0]}
            </TabsTrigger>
          ))}
        </TabsList>

        {DEMO_COMPUTATIONS.map((demo) => (
          <TabsContent key={demo.id} value={demo.id} className="mt-6">
            <div className="grid lg:grid-cols-2 gap-6">
              {/* Computation Panel */}
              <Card className="bg-gray-900/50 border-gray-700">
                <CardHeader>
                  <div className="flex items-center gap-2">
                    <span className="text-2xl">{demo.icon}</span>
                    <div>
                      <CardTitle className="text-white">{demo.name}</CardTitle>
                      <CardDescription>{demo.description}</CardDescription>
                    </div>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  {/* Parameters */}
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-gray-300">Parameters:</label>
                    <div className="grid gap-2">
                      {demo.argLabels.map((label, index) => (
                        <div key={index} className="flex items-center gap-2">
                          <span className="text-sm text-gray-400 w-20">{label}:</span>
                          <input
                            type="number"
                            value={currentArgs[index] || 0}
                            onChange={(e) => {
                              const newArgs = [...currentArgs]
                              newArgs[index] = parseInt(e.target.value) || 0
                              setCurrentArgs(newArgs)
                            }}
                            className="flex-1 px-2 py-1 text-sm bg-gray-800 border border-gray-600 rounded text-white"
                          />
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Advanced Settings */}
                  <div className="space-y-2">
                    <div className="flex items-center gap-2">
                      <input
                        type="checkbox"
                        id="advanced-mode"
                        checked={useAdvancedMode}
                        onChange={(e) => setUseAdvancedMode(e.target.checked)}
                        className="rounded border-gray-600"
                      />
                      <label htmlFor="advanced-mode" className="text-sm text-gray-300 flex items-center gap-1">
                        <Settings className="h-3 w-3" />
                        Advanced Multithreading
                      </label>
                      {threadingReady && (
                        <Badge variant="default" className="text-xs">
                          <Cpu className="mr-1 h-3 w-3" />
                          {navigator.hardwareConcurrency || 4} cores
                        </Badge>
                      )}
                    </div>

                    {isRunning && progress > 0 && (
                      <div className="space-y-1">
                        <div className="flex justify-between text-xs text-gray-400">
                          <span>Computation Progress</span>
                          <span>{progress}%</span>
                        </div>
                        <Progress value={progress} className="h-2" />
                      </div>
                    )}
                  </div>

                  {/* Controls */}
                  <div className="flex gap-2">
                    <Button
                      onClick={runComputation}
                      disabled={!isReady || isRunning}
                      className="flex-1 bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700"
                    >
                      {isRunning ? (
                        <>
                          <Square className="mr-2 h-4 w-4 animate-pulse" />
                          {useAdvancedMode ? 'Threading...' : 'Running...'}
                        </>
                      ) : (
                        <>
                          <Play className="mr-2 h-4 w-4" />
                          Execute Julia Code
                        </>
                      )}
                    </Button>

                    <div className="flex flex-col gap-1">
                      <Badge variant={isReady ? "default" : "secondary"} className="px-2 py-1 text-xs">
                        <Zap className="mr-1 h-3 w-3" />
                        {isReady ? 'WASM' : 'Loading'}
                      </Badge>
                      {threadingReady && (
                        <Badge variant="default" className="px-2 py-1 text-xs bg-green-600">
                          <Activity className="mr-1 h-3 w-3" />
                          Threading
                        </Badge>
                      )}
                    </div>
                  </div>

                  {/* Code Display */}
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-gray-300">Julia Code:</label>
                    <pre className="julia-code text-xs overflow-x-auto">
                      <code>{demo.code}</code>
                    </pre>
                  </div>
                </CardContent>
              </Card>

              {/* Results Panel */}
              <Card className="bg-gray-900/50 border-gray-700">
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-white">Execution Results</CardTitle>
                    {results.length > 0 && (
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => {
                          const data = JSON.stringify(results, null, 2)
                          const blob = new Blob([data], { type: 'application/json' })
                          const url = URL.createObjectURL(blob)
                          const a = document.createElement('a')
                          a.href = url
                          a.download = 'julia-compute-results.json'
                          a.click()
                        }}
                      >
                        <Download className="mr-2 h-4 w-4" />
                        Export
                      </Button>
                    )}
                  </div>
                </CardHeader>
                <CardContent>
                  <AnimatePresence>
                    {results.length === 0 ? (
                      <div className="text-center py-8 text-gray-500">
                        <Code className="h-12 w-12 mx-auto mb-4 opacity-50" />
                        <p>No computations run yet. Click "Execute Julia Code" to start.</p>
                      </div>
                    ) : (
                      <div className="space-y-3 max-h-80 overflow-y-auto">
                        {results.map((result, index) => (
                          <motion.div
                            key={`${result.timestamp}-${index}`}
                            initial={{ opacity: 0, y: -20 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: -20 }}
                            className={`p-3 rounded border ${
                              result.status === 'success' 
                                ? 'border-green-500/20 bg-green-900/10' 
                                : 'border-red-500/20 bg-red-900/10'
                            }`}
                          >
                            <div className="flex items-center justify-between mb-2">
                              <span className="text-sm font-medium text-white">
                                {result.name}
                              </span>
                              <span className="text-xs text-gray-400">
                                {result.executionTime.toFixed(2)}ms
                              </span>
                            </div>
                            <div className="text-sm text-gray-300">
                              {result.status === 'success' ? (
                                formatResult(result.result, result.name)
                              ) : (
                                <span className="text-red-400">Error: {result.error}</span>
                              )}
                            </div>
                          </motion.div>
                        ))}
                      </div>
                    )}
                  </AnimatePresence>
                </CardContent>
              </Card>
            </div>

            {/* Special Visualizations */}
            {demo.id === 'mandelbrot' && results.length > 0 && results[0].status === 'success' && (
              <div className="mt-6">
                <MandelbrotVisualization 
                  data={results[0].result} 
                  width={currentArgs[0]} 
                  height={currentArgs[1]} 
                />
              </div>
            )}
          </TabsContent>
        ))}
      </Tabs>

      {/* Performance Chart */}
      {results.length > 0 && (
        <Card className="bg-gray-900/50 border-gray-700">
          <CardHeader>
            <CardTitle className="text-white">Performance Timeline</CardTitle>
            <CardDescription>Execution times for recent computations</CardDescription>
          </CardHeader>
          <CardContent>
            <PerformanceChart data={results.slice(0, 10).reverse()} />
          </CardContent>
        </Card>
      )}
    </div>
  )
}

