'use client'

import { useMemo } from 'react'
import { motion } from 'framer-motion'
import { TrendingUp, TrendingDown, Minus } from 'lucide-react'

interface ComputationResult {
  name: string
  result: any
  executionTime: number
  timestamp: string
  status: 'success' | 'error' | 'running'
  performance?: {
    memoryUsed: number
    cpuUsage: number
    threadsUsed: number
  }
}

interface PerformanceChartProps {
  data: ComputationResult[]
}

export function PerformanceChart({ data }: PerformanceChartProps) {
  const chartData = useMemo(() => {
    const successfulResults = data.filter(r => r.status === 'success')

    if (successfulResults.length === 0) return null

    const maxTime = Math.max(...successfulResults.map(r => r.executionTime))
    const minTime = Math.min(...successfulResults.map(r => r.executionTime))
    const avgTime = successfulResults.reduce((sum, r) => sum + r.executionTime, 0) / successfulResults.length

    return {
      results: successfulResults,
      maxTime,
      minTime,
      avgTime,
      trend: successfulResults.length > 1 ? 
        successfulResults[successfulResults.length - 1].executionTime - successfulResults[0].executionTime : 0
    }
  }, [data])

  if (!chartData || chartData.results.length === 0) {
    return (
      <div className="h-32 flex items-center justify-center text-gray-500">
        <div className="text-center">
          <TrendingUp className="h-8 w-8 mx-auto mb-2 opacity-50" />
          <p>No performance data available</p>
        </div>
      </div>
    )
  }

  const getTrendIcon = () => {
    if (chartData.trend > 50) return <TrendingUp className="h-4 w-4 text-red-400" />
    if (chartData.trend < -50) return <TrendingDown className="h-4 w-4 text-green-400" />
    return <Minus className="h-4 w-4 text-yellow-400" />
  }

  const getBarColor = (time: number) => {
    const normalized = (time - chartData.minTime) / (chartData.maxTime - chartData.minTime)
    if (normalized > 0.7) return 'bg-red-500'
    if (normalized > 0.4) return 'bg-yellow-500'
    return 'bg-green-500'
  }

  return (
    <div className="space-y-4">
      {/* Performance Summary */}
      <div className="grid grid-cols-3 gap-4 text-sm">
        <div className="text-center">
          <div className="text-gray-400 mb-1">Average</div>
          <div className="text-white font-mono">{chartData.avgTime.toFixed(1)}ms</div>
        </div>
        <div className="text-center">
          <div className="text-gray-400 mb-1">Best</div>
          <div className="text-green-400 font-mono">{chartData.minTime.toFixed(1)}ms</div>
        </div>
        <div className="text-center">
          <div className="text-gray-400 mb-1">Worst</div>
          <div className="text-red-400 font-mono">{chartData.maxTime.toFixed(1)}ms</div>
        </div>
      </div>

      {/* Trend Indicator */}
      <div className="flex items-center justify-center gap-2 text-sm">
        {getTrendIcon()}
        <span className="text-gray-400">
          {Math.abs(chartData.trend) < 10 ? 'Stable' : 
           chartData.trend > 0 ? 'Increasing' : 'Improving'} trend
        </span>
      </div>

      {/* Bar Chart */}
      <div className="h-32 relative bg-gray-800/50 rounded-lg p-4">
        <div className="flex items-end justify-between h-full gap-1">
          {chartData.results.map((result, index) => {
            const heightPercent = (result.executionTime / chartData.maxTime) * 100

            return (
              <motion.div
                key={`${result.timestamp}-${index}`}
                initial={{ height: 0 }}
                animate={{ height: `${heightPercent}%` }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                className={`flex-1 rounded-t ${getBarColor(result.executionTime)} opacity-80 hover:opacity-100 transition-opacity relative group`}
                style={{ minHeight: '4px' }}
              >
                {/* Tooltip */}
                <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-2 py-1 bg-black/90 text-white text-xs rounded opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none whitespace-nowrap">
                  <div>{result.name}</div>
                  <div>{result.executionTime.toFixed(1)}ms</div>
                  {result.performance && (
                    <>
                      <div>{result.performance.threadsUsed}T</div>
                      <div>{result.performance.memoryUsed.toFixed(1)}MB</div>
                    </>
                  )}
                </div>
              </motion.div>
            )
          })}
        </div>

        {/* Y-axis labels */}
        <div className="absolute left-0 top-0 h-full flex flex-col justify-between text-xs text-gray-500 -translate-x-8">
          <span>{chartData.maxTime.toFixed(0)}ms</span>
          <span>{(chartData.maxTime / 2).toFixed(0)}ms</span>
          <span>0ms</span>
        </div>
      </div>

      {/* Threading Performance Details */}
      {chartData.results.some(r => r.performance) && (
        <div className="grid grid-cols-2 gap-4 text-xs">
          <div>
            <div className="text-gray-400 mb-1">Threading Usage</div>
            <div className="space-y-1">
              {chartData.results.slice(-3).map((result, index) => 
                result.performance && (
                  <div key={index} className="flex justify-between">
                    <span className="text-gray-300">{result.name.split(' ')[0]}:</span>
                    <span className="text-white">{result.performance.threadsUsed} threads</span>
                  </div>
                )
              )}
            </div>
          </div>
          <div>
            <div className="text-gray-400 mb-1">Memory Impact</div>
            <div className="space-y-1">
              {chartData.results.slice(-3).map((result, index) => 
                result.performance && (
                  <div key={index} className="flex justify-between">
                    <span className="text-gray-300">{result.name.split(' ')[0]}:</span>
                    <span className="text-white">{result.performance.memoryUsed.toFixed(1)}MB</span>
                  </div>
                )
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

