    30â†’
    31â†’# Include our compute core
    32â†’include("../src/compute_core.jl")
    33â†’using .ComputeCore
    34â†’
    35â†’println("Generating Julia compute functions...")
    36â†’
    37â†’# Test functions to ensure they work
    38â†’functions_to_test = [
    39â†’    (:julia_fibonacci, (Int32(10),)),
    40â†’    (:julia_mandelbrot, (Int32(50), Int32(50), Int32(50))),
    41â†’    (:julia_matrix_multiply, (Int32(10),)),
    42â†’    (:julia_prime_sieve, (Int32(100),)),
    43â†’    (:julia_monte_carlo_pi, (Int32(10000),)),
    44â†’    (:benchmark_basic_ops, (Int32(1000),)),
    45â†’    (:benchmark_linear_algebra, (Int32(10),))
    46â†’]
    47â†’
    48â†’# Test each function to ensure it works
    49â†’for (func_name, test_args) in functions_to_test
    50â†’    try
    51â†’        println("Testing $func_name...")
    52â†’        
    53â†’        # Get the function
    54â†’        func = getfield(ComputeCore, func_name)
    55â†’        
    56â†’        # Test with sample arguments
    57â†’        result = func(test_args...)
    58â†’        
    59â†’        println("âœ… $func_name works correctly")
    60â†’    catch e
    61â†’        println("âŒ Failed to test $func_name: $e")
    62â†’    end
    63â†’end
    64â†’
    65â†’# Generate JavaScript bindings
    66â†’open("julia_wasm_bindings.js", "w") do io
    67â†’    write(io, """
    68â†’// Julia WASM Bindings
    69â†’// Auto-generated bindings for Julia compute functions
    70â†’
    71â†’class JuliaWasm {
    72â†’    constructor() {
    73â†’        this.initialized = false;
    74â†’    }
    75â†’
    76â†’    async initialize() {
    77â†’        // In a real implementation, this would load the WASM module
    78â†’        console.log('Initializing Julia WASM module...');
    79â†’        
    80â†’        // Simulate WASM loading
    81â†’        return new Promise(resolve => {
    82â†’            setTimeout(() => {
    83â†’                this.initialized = true;
    84â†’                console.log('Julia WASM module initialized');
    85â†’                resolve();
    86â†’            }, 1000);
    87â†’        });
    88â†’    }
    89â†’
    90â†’    // Fibonacci sequence computation
    91â†’    fibonacci(n) {
    92â†’        if (!this.initialized) throw new Error('WASM not initialized');
    93â†’        
    94â†’        // Simulate Julia computation
    95â†’        const result = new Array(n);
    96â†’        if (n >= 1) result[0] = 1;
    97â†’        if (n >= 2) result[1] = 1;
    98â†’        
    99â†’        for (let i = 2; i < n; i++) {
   100â†’            result[i] = result[i-1] + result[i-2];
   101â†’        }
   102â†’        
   103â†’        return result;
   104â†’    }
   105â†’
   106â†’    // Mandelbrot set computation
   107â†’    mandelbrot(width, height, maxIter) {
   108â†’        if (!this.initialized) throw new Error('WASM not initialized');
   109â†’        
   110â†’        const result = new Array(height);
   111â†’        for (let i = 0; i < height; i++) {
   112â†’            result[i] = new Array(width);
   113â†’        }
   114â†’        
   115â†’        const xMin = -2.5, xMax = 1.0;
   116â†’        const yMin = -1.25, yMax = 1.25;
   117â†’        const dx = (xMax - xMin) / width;
   118â†’        const dy = (yMax - yMin) / height;
   119â†’        
   120â†’        for (let i = 0; i < height; i++) {
   121â†’            const y = yMin + i * dy;
   122â†’            for (let j = 0; j < width; j++) {
   123â†’                const x = xMin + j * dx;
   124â†’                
   125â†’                let zx = 0, zy = 0;
   126â†’                let iter = 0;
   127â†’                
   128â†’                while (iter < maxIter && zx*zx + zy*zy < 4) {
   129â†’                    const tempX = zx*zx - zy*zy + x;
   130â†’                    zy = 2*zx*zy + y;
   131â†’                    zx = tempX;
   132â†’                    iter++;
   133â†’                }
   134â†’                
   135â†’                result[i][j] = iter;
   136â†’            }
   137â†’        }
   138â†’        
   139â†’        return result;
   140â†’    }
   141â†’
   142â†’    // Matrix multiplication
   143â†’    matrixMultiply(size) {
   144â†’        if (!this.initialized) throw new Error('WASM not initialized');
   145â†’        
   146â†’        const startTime = performance.now();
   147â†’        
   148â†’        // Generate random matrices
   149â†’        const A = this.randomMatrix(size, size);
   150â†’        const B = this.randomMatrix(size, size);
   151â†’        const C = new Array(size);
   152â†’        
   153â†’        for (let i = 0; i < size; i++) {
   154â†’            C[i] = new Array(size);
   155â†’            for (let j = 0; j < size; j++) {
   156â†’                C[i][j] = 0;
   157â†’                for (let k = 0; k < size; k++) {
   158â†’                    C[i][j] += A[i][k] * B[k][j];
   159â†’                }
   160â†’            }
   161â†’        }
   162â†’        
   163â†’        const endTime = performance.now();
   164â†’        const executionTime = (endTime - startTime) / 1000;
   165â†’        
   166â†’        return { matrix: C, executionTime };
   167â†’    }
   168â†’
   169â†’    // Prime sieve
   170â†’    primeSieve(limit) {
   171â†’        if (!this.initialized) throw new Error('WASM not initialized');
   172â†’        
   173â†’        if (limit < 2) return [];
   174â†’        
   175â†’        const sieve = new Array(limit + 1).fill(true);
   176â†’        sieve[0] = sieve[1] = false;
   177â†’        
   178â†’        for (let i = 2; i * i <= limit; i++) {
   179â†’            if (sieve[i]) {
   180â†’                for (let j = i * i; j <= limit; j += i) {
   181â†’                    sieve[j] = false;
   182â†’                }
   183â†’            }
   184â†’        }
   185â†’        
   186â†’        const primes = [];
   187â†’        for (let i = 2; i <= limit; i++) {
   188â†’            if (sieve[i]) primes.push(i);
   189â†’        }
   190â†’        
   191â†’        return primes;
   192â†’    }
   193â†’
   194â†’    // Monte Carlo Ï€ estimation
   195â†’    monteCarloPI(samples) {
   196â†’        if (!this.initialized) throw new Error('WASM not initialized');
   197â†’        
   198â†’        const startTime = performance.now();
   199â†’        let hits = 0;
   200â†’        
   201â†’        for (let i = 0; i < samples; i++) {
   202â†’            const x = Math.random() * 2 - 1;
   203â†’            const y = Math.random() * 2 - 1;
   204â†’            if (x*x + y*y <= 1) hits++;
   205â†’        }
   206â†’        
   207â†’        const piEstimate = 4 * hits / samples;
   208â†’        const endTime = performance.now();
   209â†’        const executionTime = (endTime - startTime) / 1000;
   210â†’        
   211â†’        return { piEstimate, executionTime };
   212â†’    }
   213â†’
   214â†’    // Benchmark basic operations
   215â†’    benchmarkBasicOps(nOps) {
   216â†’        if (!this.initialized) throw new Error('WASM not initialized');
   217â†’        
   218â†’        const results = {};
   219â†’        
   220â†’        // Addition benchmark
   221â†’        let start = performance.now();
   222â†’        let a = 1.0, b = 2.0;
   223â†’        for (let i = 0; i < nOps; i++) {
   224â†’            a += b;
   225â†’        }
   226â†’        let end = performance.now();
   227â†’        results.additionOpsPerSec = nOps / ((end - start) / 1000);
   228â†’        
   229â†’        // Multiplication benchmark
   230â†’        start = performance.now();
   231â†’        a = 1.000001; b = 1.000002;
   232â†’        for (let i = 0; i < nOps; i++) {
   233â†’            a *= b;
   234â†’        }
   235â†’        end = performance.now();
   236â†’        results.multiplicationOpsPerSec = nOps / ((end - start) / 1000);
   237â†’        
   238â†’        return results;
   239â†’    }
   240â†’
   241â†’    // Helper: Generate random matrix
   242â†’    randomMatrix(rows, cols) {
   243â†’        const matrix = new Array(rows);
   244â†’        for (let i = 0; i < rows; i++) {
   245â†’            matrix[i] = new Array(cols);
   246â†’            for (let j = 0; j < cols; j++) {
   247â†’                matrix[i][j] = Math.random() * 2 - 1;
   248â†’            }
   249â†’        }
   250â†’        return matrix;
   251â†’    }
   252â†’}
   253â†’
   254â†’// Export for use in browser
   255â†’if (typeof module !== 'undefined' && module.exports) {
   256â†’    module.exports = JuliaWasm;
   257â†’} else if (typeof window !== 'undefined') {
   258â†’    window.JuliaWasm = JuliaWasm;
   259â†’}
   260â†’""")
   261â†’end
   262â†’
   263â†’println("âœ… JavaScript bindings generated")
   264â†’EOF
   265â†’
   266â†’# Run the compilation
   267â†’julia build/compile_to_wasm.jl
   268â†’
   269â†’# Copy bindings to public directory
   270â†’cp julia_wasm_bindings.js ../public/wasm/
   271â†’
   272â†’# Create TypeScript definitions
   273â†’cat > ../public/wasm/julia_wasm.d.ts << 'EOF'
   274â†’// TypeScript definitions for Julia WASM module
   275â†’
   276â†’export interface PerformanceMetrics {
   277â†’  additionOpsPerSec: number;
   278â†’  multiplicationOpsPerSec: number;
   279â†’}
   280â†’
   281â†’export interface MatrixResult {
   282â†’  matrix: number[][];
   283â†’  executionTime: number;
   284â†’}
   285â†’
   286â†’export interface PIResult {
   287â†’  piEstimate: number;
   288â†’  executionTime: number;
   289â†’}
   290â†’
   291â†’export declare class JuliaWasm {
   292â†’  initialized: boolean;
   293â†’  
   294â†’  constructor();
   295â†’  
   296â†’  initialize(): Promise<void>;
   297â†’  
   298â†’  fibonacci(n: number): number[];
   299â†’  
   300â†’  mandelbrot(width: number, height: number, maxIter: number): number[][];
   301â†’  
   302â†’  matrixMultiply(size: number): MatrixResult;
   303â†’  
