    const body: PhoenixRequest = await request.json()
    const phoenixClient = PhoenixOrchestratorClient.getInstance()

    let result: any

    switch (body.action) {
      case 'submit':
        if (!body.jobSpec) {
          return NextResponse.json(
            { success: false, error: 'Missing jobSpec for submission' },
            { status: 400 }
