            { success: false, error: 'Missing jobSpec for submission' },
            { status: 400 }
          )
        }
        result = await phoenixClient.submitJob(body.jobSpec)
        break

      case 'status':
        if (!body.jobId) {
          return NextResponse.json(
            { success: false, error: 'Missing jobId for status check' },
            { status: 400 }
          )
        }
        result = await phoenixClient.getJobStatus(body.jobId)
        break

      case 'list_backends':
        result = await phoenixClient.listBackends()
        break

      case 'get_stats':
        result = await phoenixClient.getStats()
        break

      default:
        return NextResponse.json(
          { success: false, error: 'Invalid action' },
          { status: 400 }
        )
    }

    return NextResponse.json({
      success: true,
      data: result
    })

  } catch (error) {
    console.error('Phoenix API error:', error)
    return NextResponse.json(
      { 
        success: false, 
        error: error instanceof Error ? error.message : 'Internal server error' 
      },
      { status: 500 }
    )
  }
}

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const action = searchParams.get('action') || 'get_stats'
    const jobId = searchParams.get('jobId')

    const phoenixClient = PhoenixOrchestratorClient.getInstance()
    let result: any

    switch (action) {
      case 'get_stats':
        result = await phoenixClient.getStats()
        break

      case 'list_backends':
        result = await phoenixClient.listBackends()
        break

      case 'status':
        if (!jobId) {
          return NextResponse.json(
            { success: false, error: 'Missing jobId parameter' },
            { status: 400 }
          )
        }
        result = await phoenixClient.getJobStatus(jobId)
        break

      default:
        return NextResponse.json(
          { success: false, error: 'Invalid action' },
