import { NextRequest } from 'next/server'

interface PerformanceMetrics {
  timestamp: number
  cpuUsage: number
  memoryUsage: number
  threadsActive: number
  threadsTotal: number
  tasksCompleted: number
  tasksActive: number
  throughput: number
  latency: number
  orchestratorStats?: {
    queueLength: number
    activeWorkers: number
    backendHealth: { [key: string]: string }
  }
}

class PerformanceStreamer {
  private static instance: PerformanceStreamer
  private clients: Set<ReadableStreamDefaultController> = new Set()
  private intervalId: NodeJS.Timeout | null = null
  private isStreaming: boolean = false

  static getInstance(): PerformanceStreamer {
    if (!PerformanceStreamer.instance) {
      PerformanceStreamer.instance = new PerformanceStreamer()
    }
    return PerformanceStreamer.instance
  }

  addClient(controller: ReadableStreamDefaultController) {
    this.clients.add(controller)

    if (!this.isStreaming) {
      this.startStreaming()
    }
  }

  removeClient(controller: ReadableStreamDefaultController) {
    this.clients.delete(controller)

    if (this.clients.size === 0 && this.isStreaming) {
      this.stopStreaming()
    }
  }

  private startStreaming() {
    if (this.isStreaming) return

    this.isStreaming = true
    console.log('ðŸ“Š Starting performance metrics streaming')

    this.intervalId = setInterval(() => {
      this.broadcastMetrics()
    }, 1000) // Send metrics every second
  }

  private stopStreaming() {
    if (!this.isStreaming) return

    this.isStreaming = false

    if (this.intervalId) {
      clearInterval(this.intervalId)
      this.intervalId = null
    }

    console.log('ðŸ“Š Stopped performance metrics streaming')
  }

  private generateMetrics(): PerformanceMetrics {
    // In production, this would collect real metrics from:
    // - System monitoring (CPU, memory)
    // - Threading system (active threads, tasks)
    // - REASSEMBLY orchestrator (queue, workers, backends)

    const baseMemory = 50
    const memoryVariation = Math.sin(Date.now() / 10000) * 30 + 30

    const baseCPU = 25
    const cpuVariation = Math.sin(Date.now() / 8000) * 35 + 35

    const threadsTotal = 8
    const threadsActive = Math.min(threadsTotal, Math.floor(Math.random() * 6) + 1)

    return {
      timestamp: Date.now(),
      cpuUsage: Math.max(0, Math.min(100, baseCPU + cpuVariation + (Math.random() - 0.5) * 10)),
      memoryUsage: Math.max(0, Math.min(100, baseMemory + memoryVariation + (Math.random() - 0.5) * 5)),
      threadsActive,
      threadsTotal,
      tasksCompleted: Math.floor(Date.now() / 1000) + Math.floor(Math.random() * 100),
      tasksActive: Math.floor(Math.random() * 5),
      throughput: Math.random() * 50 + 25,
      latency: Math.random() * 100 + 50,
      orchestratorStats: {
        queueLength: Math.floor(Math.random() * 5),
        activeWorkers: Math.floor(Math.random() * 8) + 2,
        backendHealth: {
          julia: Math.random() > 0.95 ? 'degraded' : 'healthy',
          rust_native: Math.random() > 0.98 ? 'degraded' : 'healthy',
          wasm: 'healthy',
          gpu: Math.random() > 0.9 ? 'checking' : 'healthy'
        }
      }
    }
  }

  private broadcastMetrics() {
    const metrics = this.generateMetrics()
    const data = JSON.stringify(metrics)
    const message = `data: ${data}\n\n`

    // Send to all connected clients
    const disconnectedClients: ReadableStreamDefaultController[] = []

    for (const controller of this.clients) {
      try {
        controller.enqueue(new TextEncoder().encode(message))
      } catch (error) {
        // Client disconnected
        disconnectedClients.push(controller)
      }
    }

    // Clean up disconnected clients
    for (const controller of disconnectedClients) {
      this.clients.delete(controller)
    }
  }
}

export async function GET(request: NextRequest) {
  // Check if client accepts server-sent events
  const acceptHeader = request.headers.get('accept') || ''
  if (!acceptHeader.includes('text/event-stream')) {
    return new Response('This endpoint only supports Server-Sent Events', {
      status: 400,
      headers: {
        'Content-Type': 'text/plain'
      }
    })
  }

  const streamer = PerformanceStreamer.getInstance()

  // Create a readable stream for Server-Sent Events
  const stream = new ReadableStream({
    start(controller) {
      // Send initial connection message
      const welcomeMessage = `data: ${JSON.stringify({
        type: 'connected',
        message: 'Performance metrics stream connected',
        timestamp: Date.now()
      })}\n\n`

      controller.enqueue(new TextEncoder().encode(welcomeMessage))

      // Add client to streamer
      streamer.addClient(controller)
    },

    cancel() {
      // Client disconnected, remove from streamer
      // Note: We can't access the controller here, but the streamer
      // will clean up disconnected clients automatically
      console.log('ðŸ“Š Client disconnected from performance stream')
    }
  })

  return new Response(stream, {
    headers: {
      'Content-Type': 'text/event-stream',
      'Cache-Control': 'no-cache',
      'Connection': 'keep-alive',
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Headers': 'Content-Type',
      'Access-Control-Allow-Methods': 'GET',
    }
  })
}

export async function OPTIONS(request: NextRequest) {
  return new Response(null, {
    status: 200,
    headers: {
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Headers': 'Content-Type',
      'Access-Control-Allow-Methods': 'GET',
    }
  })
}

