    )
  }
}

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const action = searchParams.get('action')

    switch (action) {
      case 'status':
        const engine = JuliaComputeEngine.getInstance()
        return NextResponse.json({
          success: true,
          status: {
            initialized: engine['isInitialized'],
            wasmLoaded: engine['wasmModule'] !== null,
            cacheSize: engine['computeCache'].size,
            supportedFunctions: ['fibonacci', 'mandelbrot', 'matrixMultiply', 'primeSieve', 'monteCarloPI']
          }
        })

      case 'cache':
        return NextResponse.json({
          success: true,
          cache: {
            size: JuliaComputeEngine.getInstance()['computeCache'].size,
            maxSize: 100
          }
        })

      default:
        return NextResponse.json(
          { success: false, error: 'Invalid action' },
          { status: 400 }
        )
    }

  } catch (error) {
    console.error('Julia compute API error:', error)
    return NextResponse.json(
