'use client'

import { useState, useEffect, useCallback, useRef } from 'react'
import { getPhoenixClient, initializePhoenixClient, PhoenixRealtimeClient } from '@/lib/phoenix-channel'
import { useAdvancedCompute } from '@/components/providers/WasmProvider'

interface FlameJob {
  id: string
  operation: string
  args: any[]
  priority: 'low' | 'normal' | 'high' | 'critical'
  backend?: 'julia' | 'rust' | 'wasm' | 'cpu' | 'gpu' | 'auto'
  subagentRequirement?: string
  multithreading?: {
    enabled: boolean
    threads?: number
    strategy?: 'parallel' | 'distributed' | 'hybrid'
  }
}

interface FlameJobResult {
  jobId: string
  status: 'queued' | 'running' | 'completed' | 'failed'
  progress?: number
  result?: any
  error?: string
  performance?: {
    backend: string
    executionTime: number
    memoryUsed: number
    threadsUsed: number
    distributedNodes?: number
  }
  metadata?: {
    phoenixNodeId?: string
    flamePatternVersion?: string
    orchestratorVersion?: string
  }
}

interface FlameMetrics {
  realtime: {
    cpuUsage: number
    memoryUsage: number
    threadsActive: number
    threadsTotal: number
    queueLength: number
    throughput: number
  }
  orchestrator: {
    activeJobs: number
    completedJobs: number
    failedJobs: number
    avgExecutionTime: number
    backendHealth: { [key: string]: string }
  }
  networking: {
    phoenixConnected: boolean
    websocketLatency: number
    messagesSent: number
    messagesReceived: number
  }
}

interface FlamePatternOptions {
  phoenixEndpoint?: string
  enableAutoReconnect?: boolean
  enableMetricsStream?: boolean
  enableBackendFailover?: boolean
  debugMode?: boolean
}

/**
 * FLAME Pattern Hook - Fast Language Application Multithreading Engine
 * 
 * This hook implements the complete FLAME pattern by combining:
 * - Phoenix WebSocket real-time orchestration 
 * - Katalyst advanced multithreading
 * - Intelligent backend selection
 * - Distributed computation coordination
 * - Real-time performance monitoring
 */
export function useFlamePattern(options: FlamePatternOptions = {}) {
  const [phoenixClient, setPhoenixClient] = useState<PhoenixRealtimeClient | null>(null)
  const [isConnected, setIsConnected] = useState(false)
  const [activeJobs, setActiveJobs] = useState<Map<string, FlameJobResult>>(new Map())
  const [metrics, setMetrics] = useState<FlameMetrics | null>(null)
  const [backendStatus, setBackendStatus] = useState<any>(null)
  const [connectionError, setConnectionError] = useState<string | null>(null)

  // Advanced threading integration
  const { 
    executeAdvancedTask, 
    createComputePool, 
    getThreadingMetrics, 
    threadingReady 
  } = useAdvancedCompute()

  // Refs for managing state
  const jobCallbacks = useRef<Map<string, (result: FlameJobResult) => void>>(new Map())
  const metricsInterval = useRef<NodeJS.Timeout | null>(null)
  const reconnectAttempts = useRef(0)

  /**
   * Initialize FLAME pattern - connect to Phoenix and setup channels
   */
  const initializeFlame = useCallback(async () => {
    try {
      const endpoint = options.phoenixEndpoint || 
        (process.env.NODE_ENV === 'production' 
          ? 'wss://api.supercompute.me/socket' 
          : 'ws://localhost:4000/socket')

      const client = initializePhoenixClient({
        endpoint,
        params: {
          flame_pattern: true,
          client_version: '1.0.0',
          threading_enabled: threadingReady
        },
        logger: options.debugMode || false
      })

      const connected = await client.connect()

      if (connected) {
        setPhoenixClient(client)
        setIsConnected(true)
        setConnectionError(null)
        reconnectAttempts.current = 0

        // Setup event subscriptions
        setupFlameSubscriptions(client)

        if (options.debugMode) {
          console.log('ðŸ”¥ FLAME Pattern initialized successfully')
        }
      } else {
        throw new Error('Failed to connect to Phoenix orchestrator')
      }

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Unknown connection error'
      setConnectionError(errorMessage)
      setIsConnected(false)

      if (options.enableAutoReconnect && reconnectAttempts.current < 5) {
        reconnectAttempts.current++
        const delay = Math.min(1000 * Math.pow(2, reconnectAttempts.current), 30000)
        setTimeout(initializeFlame, delay)
      }
    }
  }, [options, threadingReady])

  /**
   * Setup Phoenix channel subscriptions for FLAME pattern
   */
  const setupFlameSubscriptions = useCallback((client: PhoenixRealtimeClient) => {
    // Subscribe to job updates
    client.subscribeToJobs((update) => {
      setActiveJobs(prev => {
        const newJobs = new Map(prev)
        newJobs.set(update.job_id, {
          jobId: update.job_id,
          status: update.status,
          progress: update.progress,
          result: update.result,
          error: update.error,
          performance: update.performance
        })
        return newJobs
      })

      // Call specific job callback if exists
      const callback = jobCallbacks.current.get(update.job_id)
      if (callback) {
        callback({
          jobId: update.job_id,
          status: update.status,
          progress: update.progress,
          result: update.result,
          error: update.error,
          performance: update.performance
        })
      }
    })

    // Subscribe to real-time metrics
    if (options.enableMetricsStream) {
      client.subscribeToMetrics((metricsData) => {
        const threadingMetrics = getThreadingMetrics()

        setMetrics({
          realtime: {
            cpuUsage: metricsData.cpu_usage || 0,
            memoryUsage: metricsData.memory_usage || 0,
            threadsActive: threadingMetrics?.activeThreads || 0,
            threadsTotal: threadingMetrics?.totalThreads || 4,
            queueLength: metricsData.queue_length || 0,
            throughput: metricsData.throughput || 0
          },
          orchestrator: {
            activeJobs: metricsData.active_jobs || 0,
            completedJobs: metricsData.completed_jobs || 0,
            failedJobs: metricsData.failed_jobs || 0,
            avgExecutionTime: metricsData.avg_execution_time || 0,
            backendHealth: metricsData.backend_health || {}
          },
          networking: {
            phoenixConnected: true,
            websocketLatency: metricsData.websocket_latency || 0,
            messagesSent: metricsData.messages_sent || 0,
            messagesReceived: metricsData.messages_received || 0
          }
        })
      })
    }

    // Subscribe to backend status
    client.subscribeToBackendStatus((status) => {
      setBackendStatus(status)
    })
  }, [options, getThreadingMetrics])

  /**
   * Execute computation using FLAME pattern
   * This intelligently routes between local multithreading and distributed Phoenix execution
   */
  const executeFlameJob = useCallback(async (
    job: FlameJob, 
    callback?: (result: FlameJobResult) => void
  ): Promise<FlameJobResult> => {
    if (!phoenixClient || !isConnected) {
      throw new Error('FLAME pattern not initialized or Phoenix not connected')
    }

    // Register callback for this job
    if (callback) {
      jobCallbacks.current.set(job.id, callback)
    }

    try {
      // Determine execution strategy based on job characteristics
      const executionStrategy = determineExecutionStrategy(job)

      if (executionStrategy === 'local_multithreading' && threadingReady) {
        // Execute locally using advanced threading
        return await executeLocalMultithreading(job)
      } else {
        // Execute through Phoenix orchestrator
        return await executeDistributedComputation(job)
      }

    } catch (error) {
      const errorResult: FlameJobResult = {
        jobId: job.id,
        status: 'failed',
        error: error instanceof Error ? error.message : 'Unknown execution error'
      }

      if (callback) {
        callback(errorResult)
      }

      return errorResult
    }
  }, [phoenixClient, isConnected, threadingReady])

  /**
   * Determine optimal execution strategy for a job
   */
  const determineExecutionStrategy = useCallback((job: FlameJob): 'local_multithreading' | 'distributed' => {
    // Simple heuristics - in production this would be more sophisticated
    const complexOperations = ['mandelbrot', 'matrixMultiply']
    const isComplex = complexOperations.includes(job.operation)
    const hasLargeArgs = job.args.some(arg => typeof arg === 'number' && arg > 1000)

    // Use distributed execution for complex operations or when explicitly requested
    if (job.backend === 'auto' || isComplex || hasLargeArgs || job.priority === 'critical') {
      return 'distributed'
    }

    // Use local multithreading for simpler operations when threading is ready
    return threadingReady ? 'local_multithreading' : 'distributed'
  }, [threadingReady])

  /**
   * Execute job using local advanced multithreading
   */
  const executeLocalMultithreading = useCallback(async (job: FlameJob): Promise<FlameJobResult> => {
    const startTime = performance.now()

    try {
      const task = {
        id: job.id,
        type: 'julia' as const,
        operation: job.operation,
        data: job.args,
        priority: job.priority,
        timeout: 30000,
        retries: 2,
        subagentRequirement: job.subagentRequirement
      }

      const result = await executeAdvancedTask(task)
      const executionTime = performance.now() - startTime
      const threadingMetrics = getThreadingMetrics()

      return {
        jobId: job.id,
        status: 'completed',
        result,
        performance: {
          backend: 'local_multithreading',
          executionTime,
          memoryUsed: threadingMetrics?.memoryUsageMb || 0,
          threadsUsed: threadingMetrics?.activeThreads || 1
        },
        metadata: {
          flamePatternVersion: '1.0.0'
        }
      }
    } catch (error) {
      throw error
    }
  }, [executeAdvancedTask, getThreadingMetrics])

  /**
   * Execute job through Phoenix distributed orchestrator
   */
  const executeDistributedComputation = useCallback(async (job: FlameJob): Promise<FlameJobResult> => {
    if (!phoenixClient) {
      throw new Error('Phoenix client not available')
    }

    try {
      // Submit job to Phoenix orchestrator
      const jobId = await phoenixClient.submitJob({
        id: job.id,
        operation: job.operation,
        args: job.args,
        priority: job.priority,
        backend: job.backend,
        subagentRequirement: job.subagentRequirement
      })

      // Return initial queued status - updates will come via WebSocket
      return {
        jobId,
        status: 'queued',
        metadata: {
          phoenixNodeId: 'distributed',
          flamePatternVersion: '1.0.0'
        }
      }
    } catch (error) {
      throw error
    }
  }, [phoenixClient])

  /**
   * Cancel a running job
   */
  const cancelJob = useCallback(async (jobId: string): Promise<boolean> => {
    if (!phoenixClient || !isConnected) {
      return false
    }

    try {
      await phoenixClient.cancelJob(jobId)

      // Remove from active jobs
      setActiveJobs(prev => {
        const newJobs = new Map(prev)
        newJobs.delete(jobId)
        return newJobs
      })

      // Remove callback
      jobCallbacks.current.delete(jobId)

      return true
    } catch (error) {
      console.error('Failed to cancel job:', error)
      return false
    }
  }, [phoenixClient, isConnected])

  /**
   * Get comprehensive system status
   */
  const getFlameStatus = useCallback(async (): Promise<any> => {
    const status = {
      flame: {
        initialized: !!phoenixClient,
        connected: isConnected,
        threading: threadingReady,
        activeJobs: activeJobs.size
      },
      phoenix: null as any,
      threading: null as any
    }

    if (phoenixClient && isConnected) {
      try {
        status.phoenix = await phoenixClient.getOrchestratorStats()
      } catch (error) {
        status.phoenix = { error: 'Failed to get Phoenix stats' }
      }
    }

    if (threadingReady) {
      status.threading = getThreadingMetrics()
    }

    return status
  }, [phoenixClient, isConnected, threadingReady, activeJobs.size, getThreadingMetrics])

  /**
   * Cleanup FLAME pattern resources
   */
  const cleanupFlame = useCallback(() => {
    if (metricsInterval.current) {
      clearInterval(metricsInterval.current)
    }

    if (phoenixClient) {
      phoenixClient.disconnect()
    }

    jobCallbacks.current.clear()
    setActiveJobs(new Map())
    setIsConnected(false)
    setPhoenixClient(null)
    setMetrics(null)
    setBackendStatus(null)

    if (options.debugMode) {
      console.log('ðŸ”¥ FLAME Pattern cleaned up')
    }
  }, [phoenixClient, options.debugMode])

  // Initialize FLAME pattern on mount
  useEffect(() => {
    initializeFlame()

    return cleanupFlame
  }, [initializeFlame, cleanupFlame])

  // Cleanup on unmount
  useEffect(() => {
    return cleanupFlame
  }, [cleanupFlame])

  return {
    // Core FLAME functionality
    initializeFlame,
    executeFlameJob,
    cancelJob,

    // Status and monitoring
    isConnected,
    connectionError,
    activeJobs: Array.from(activeJobs.values()),
    metrics,
    backendStatus,
    getFlameStatus,

    // Advanced features
    phoenixClient,
    threadingReady,

    // Cleanup
    cleanupFlame
  }
}

toolUseResult.originalFile:
'use client'

import { createContext, useContext, useState, useEffect, ReactNode, useCallback } from 'react'
import { threadController } from '@katalyst/multithreading'
import { useAdvancedMultithreading, useAITaskProcessor, useThreadMonitoring } from '@katalyst/hooks'

interface AdvancedThreadTask {
  id: string
  type: 'cpu' | 'gpu' | 'wasm' | 'julia'
  operation: string
  data: any
  priority: 'low' | 'normal' | 'high' | 'critical'
  timeout?: number
  retries?: number
  dependencies?: string[]
  subagentRequirement?: string
}

interface MultithreadingContextType {
  wasmModule: any
  isLoading: boolean
  isReady: boolean
  error: string | null
  threadingReady: boolean
  loadWasm: () => Promise<void>
  executeComputation: (fn: string, args: any[]) => Promise<any>
  executeAdvancedTask: (task: AdvancedThreadTask) => Promise<any>
  getPerformanceMetrics: () => any
  getThreadingMetrics: () => any
  createComputePool: (name: string, config?: any) => any
}

const MultithreadingContext = createContext<MultithreadingContextType | undefined>(undefined)

export function MultithreadingProvider({ children }: { children: ReactNode }) {
  const [wasmModule, setWasmModule] = useState<any>(null)
  const [isLoading, setIsLoading] = useState(false)
  const [isReady, setIsReady] = useState(false)
  const [threadingReady, setThreadingReady] = useState(false)
  const [error, setError] = useState<string | null>(null)

  // Initialize advanced multithreading capabilities
  const { 
    initializeThreading, 
    executeTask, 
    createThreadPool,
    isThreadingInitialized 
  } = useAdvancedMultithreading()

  const { processAITask, isAIReady } = useAITaskProcessor()
  const { getMetrics, startMonitoring } = useThreadMonitoring()

  const loadWasm = useCallback(async () => {
    if (isLoading || isReady) return

    setIsLoading(true)
    setError(null)

    try {
      // Initialize advanced threading system first
      await initializeThreading({
        rayonThreads: navigator.hardwareConcurrency || 4,
        tokioWorkerThreads: Math.max(2, Math.floor(navigator.hardwareConcurrency / 2)),
        tokioEnableIo: true,
        tokioEnableTime: true
      })

      // Start performance monitoring
      startMonitoring({
        interval: 1000,
        trackMemory: true,
        trackCPU: true,
        trackThreads: true
      })

      // Dynamically import the Julia WASM module
      const { default: JuliaWasm } = await import('/wasm/julia_wasm_bindings.js')
      const module = new JuliaWasm()

      await module.initialize()

      setWasmModule(module)
      setIsReady(true)
      setThreadingReady(isThreadingInitialized)

      console.log('ðŸš€ Julia WASM module loaded successfully')
      console.log('ðŸ§µ Advanced threading system initialized')
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Unknown error'
      setError(`Failed to load WASM module: ${errorMessage}`)
      console.error('âŒ Failed to load Julia WASM:', err)
    } finally {
      setIsLoading(false)
    }
  }, [isLoading, isReady, initializeThreading, startMonitoring, isThreadingInitialized])

  const executeComputation = useCallback(async (fn: string, args: any[]) => {
    if (!wasmModule || !isReady) {
      throw new Error('WASM module not ready')
    }

    const startTime = performance.now()

    try {
      let result
      switch (fn) {
        case 'fibonacci':
          result = wasmModule.fibonacci(args[0])
          break
        case 'mandelbrot':
          result = wasmModule.mandelbrot(args[0], args[1], args[2])
          break
        case 'matrixMultiply':
          result = wasmModule.matrixMultiply(args[0])
          break
        case 'primeSieve':
          result = wasmModule.primeSieve(args[0])
          break
        case 'monteCarloPI':
          result = wasmModule.monteCarloPI(args[0])
          break
        case 'benchmarkBasicOps':
          result = wasmModule.benchmarkBasicOps(args[0])
          break
        default:
          throw new Error(`Unknown function: ${fn}`)
      }

      const endTime = performance.now()

      return {
        result,
        executionTime: endTime - startTime,
        timestamp: new Date().toISOString()
      }
    } catch (err) {
      const endTime = performance.now()
      throw new Error(`Computation failed: ${err instanceof Error ? err.message : 'Unknown error'}`)
    }
  }, [wasmModule, isReady])

  const executeAdvancedTask = useCallback(async (task: AdvancedThreadTask) => {
    if (!wasmModule || !isReady || !threadingReady) {
      throw new Error('Threading system not ready')
    }

    try {
      // Route task through AI processor if it's a complex computational task
      if (task.type === 'julia' && task.subagentRequirement) {
        return await processAITask({
          ...task,
          processor: 'numerical-computation',
          backend: 'julia-wasm'
        })
      }

      // Use advanced threading for parallel computation
      return await executeTask(task)
    } catch (err) {
      throw new Error(`Advanced task failed: ${err instanceof Error ? err.message : 'Unknown error'}`)
    }
  }, [wasmModule, isReady, threadingReady, executeTask, processAITask])

  const createComputePool = useCallback((name: string, config?: any) => {
    if (!threadingReady) {
      throw new Error('Threading system not ready')
    }

    return createThreadPool(name, {
      threads: config?.threads || navigator.hardwareConcurrency || 4,
      threadName: `julia-compute-${name}`,
      stackSize: 1024 * 1024, // 1MB stack size
      panicHandler: true,
      ...config
    })
  }, [threadingReady, createThreadPool])

  const getPerformanceMetrics = useCallback(() => {
    if (!wasmModule || !isReady) {
      return null
    }

    return {
      memoryUsage: {
        used: performance.memory?.usedJSHeapSize || 0,
        total: performance.memory?.totalJSHeapSize || 0,
        limit: performance.memory?.jsHeapSizeLimit || 0
      },
      timing: {
        navigationStart: performance.timeOrigin,
        loadEventEnd: performance.timing?.loadEventEnd || 0
      },
      wasmReady: isReady,
      moduleLoaded: wasmModule !== null
    }
  }, [wasmModule, isReady])

  const getThreadingMetrics = useCallback(() => {
    if (!threadingReady) {
      return null
    }

    const metrics = getMetrics()
    return {
      ...metrics,
      threadingReady,
      aiReady: isAIReady,
      totalCores: navigator.hardwareConcurrency || 4,
      activeThreads: metrics?.activeThreads || 0,
      completedTasks: metrics?.completedTasks || 0,
      failedTasks: metrics?.failedTasks || 0
    }
  }, [threadingReady, isAIReady, getMetrics])

  useEffect(() => {
    // Auto-load WASM module on mount
    loadWasm()
  }, [loadWasm])

  const value: MultithreadingContextType = {
    wasmModule,
    isLoading,
    isReady,
    threadingReady,
    error,
    loadWasm,
    executeComputation,
    executeAdvancedTask,
    getPerformanceMetrics,
    getThreadingMetrics,
    createComputePool
  }

  return (
    <MultithreadingContext.Provider value={value}>
      {children}
    </MultithreadingContext.Provider>
  )
}

export function useWasm() {
  const context = useContext(MultithreadingContext)
  if (context === undefined) {
    throw new Error('useWasm must be used within a MultithreadingProvider')
  }
  return context
}

export function useAdvancedCompute() {
  const context = useContext(MultithreadingContext)
  if (context === undefined) {
    throw new Error('useAdvancedCompute must be used within a MultithreadingProvider')
  }
  return {
    executeAdvancedTask: context.executeAdvancedTask,
    createComputePool: context.createComputePool,
    getThreadingMetrics: context.getThreadingMetrics,
    threadingReady: context.threadingReady
  }
}

// Legacy export for backward compatibility
export { MultithreadingProvider as WasmProvider }

'use client'

import { createContext, useContext, useState, useEffect, ReactNode, useCallback } from 'react'
import { threadController } from '@katalyst/multithreading'
import { useAdvancedMultithreading, useAITaskProcessor, useThreadMonitoring } from '@katalyst/hooks'
import { useFlamePattern } from '@/hooks/useFlamePattern'

interface AdvancedThreadTask {
  id: string
  type: 'cpu' | 'gpu' | 'wasm' | 'julia'
  operation: string
  data: any
  priority: 'low' | 'normal' | 'high' | 'critical'
  timeout?: number
  retries?: number
  dependencies?: string[]
  subagentRequirement?: string
}

interface MultithreadingContextType {
  wasmModule: any
  isLoading: boolean
  isReady: boolean
  error: string | null
  threadingReady: boolean
  loadWasm: () => Promise<void>
  executeComputation: (fn: string, args: any[]) => Promise<any>
  executeAdvancedTask: (task: AdvancedThreadTask) => Promise<any>
  getPerformanceMetrics: () => any
  getThreadingMetrics: () => any
  createComputePool: (name: string, config?: any) => any
}

const MultithreadingContext = createContext<MultithreadingContextType | undefined>(undefined)

export function MultithreadingProvider({ children }: { children: ReactNode }) {
  const [wasmModule, setWasmModule] = useState<any>(null)
  const [isLoading, setIsLoading] = useState(false)
  const [isReady, setIsReady] = useState(false)
  const [threadingReady, setThreadingReady] = useState(false)
  const [error, setError] = useState<string | null>(null)

  // Initialize advanced multithreading capabilities
  const { 
    initializeThreading, 
    executeTask, 
    createThreadPool,
    isThreadingInitialized 
  } = useAdvancedMultithreading()

  const { processAITask, isAIReady } = useAITaskProcessor()
  const { getMetrics, startMonitoring } = useThreadMonitoring()

  const loadWasm = useCallback(async () => {
    if (isLoading || isReady) return

    setIsLoading(true)
    setError(null)

    try {
      // Initialize advanced threading system first
      await initializeThreading({
        rayonThreads: navigator.hardwareConcurrency || 4,
        tokioWorkerThreads: Math.max(2, Math.floor(navigator.hardwareConcurrency / 2)),
        tokioEnableIo: true,
        tokioEnableTime: true
      })

      // Start performance monitoring
      startMonitoring({
        interval: 1000,
        trackMemory: true,
        trackCPU: true,
        trackThreads: true
      })

      // Dynamically import the Julia WASM module
      const { default: JuliaWasm } = await import('/wasm/julia_wasm_bindings.js')
      const module = new JuliaWasm()

      await module.initialize()

      setWasmModule(module)
      setIsReady(true)
      setThreadingReady(isThreadingInitialized)

      console.log('ðŸš€ Julia WASM module loaded successfully')
      console.log('ðŸ§µ Advanced threading system initialized')
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Unknown error'
      setError(`Failed to load WASM module: ${errorMessage}`)
      console.error('âŒ Failed to load Julia WASM:', err)
    } finally {
      setIsLoading(false)
    }
  }, [isLoading, isReady, initializeThreading, startMonitoring, isThreadingInitialized])

  const executeComputation = useCallback(async (fn: string, args: any[]) => {
    if (!wasmModule || !isReady) {
      throw new Error('WASM module not ready')
    }

    const startTime = performance.now()

    try {
      let result
      switch (fn) {
        case 'fibonacci':
          result = wasmModule.fibonacci(args[0])
          break
        case 'mandelbrot':
          result = wasmModule.mandelbrot(args[0], args[1], args[2])
          break
        case 'matrixMultiply':
          result = wasmModule.matrixMultiply(args[0])
          break
        case 'primeSieve':
          result = wasmModule.primeSieve(args[0])
          break
        case 'monteCarloPI':
          result = wasmModule.monteCarloPI(args[0])
          break
        case 'benchmarkBasicOps':
          result = wasmModule.benchmarkBasicOps(args[0])
          break
        default:
          throw new Error(`Unknown function: ${fn}`)
      }

      const endTime = performance.now()

      return {
        result,
        executionTime: endTime - startTime,
        timestamp: new Date().toISOString()
      }
    } catch (err) {
      const endTime = performance.now()
      throw new Error(`Computation failed: ${err instanceof Error ? err.message : 'Unknown error'}`)
    }
  }, [wasmModule, isReady])

  const executeAdvancedTask = useCallback(async (task: AdvancedThreadTask) => {
    if (!wasmModule || !isReady || !threadingReady) {
      throw new Error('Threading system not ready')
    }

    try {
      // Route task through AI processor if it's a complex computational task
      if (task.type === 'julia' && task.subagentRequirement) {
        return await processAITask({
          ...task,
          processor: 'numerical-computation',
          backend: 'julia-wasm'
        })
      }

      // Use advanced threading for parallel computation
      return await executeTask(task)
    } catch (err) {
      throw new Error(`Advanced task failed: ${err instanceof Error ? err.message : 'Unknown error'}`)
    }
  }, [wasmModule, isReady, threadingReady, executeTask, processAITask])

  const createComputePool = useCallback((name: string, config?: any) => {
    if (!threadingReady) {
      throw new Error('Threading system not ready')
    }

    return createThreadPool(name, {
      threads: config?.threads || navigator.hardwareConcurrency || 4,
      threadName: `julia-compute-${name}`,
      stackSize: 1024 * 1024, // 1MB stack size
      panicHandler: true,
      ...config
    })
  }, [threadingReady, createThreadPool])

  const getPerformanceMetrics = useCallback(() => {
    if (!wasmModule || !isReady) {
      return null
    }

    return {
      memoryUsage: {
        used: performance.memory?.usedJSHeapSize || 0,
        total: performance.memory?.totalJSHeapSize || 0,
        limit: performance.memory?.jsHeapSizeLimit || 0
      },
      timing: {
        navigationStart: performance.timeOrigin,
        loadEventEnd: performance.timing?.loadEventEnd || 0
      },
      wasmReady: isReady,
      moduleLoaded: wasmModule !== null
    }
  }, [wasmModule, isReady])

  const getThreadingMetrics = useCallback(() => {
    if (!threadingReady) {
      return null
    }

    const metrics = getMetrics()
    return {
      ...metrics,
      threadingReady,
      aiReady: isAIReady,
      totalCores: navigator.hardwareConcurrency || 4,
      activeThreads: metrics?.activeThreads || 0,
      completedTasks: metrics?.completedTasks || 0,
      failedTasks: metrics?.failedTasks || 0
    }
  }, [threadingReady, isAIReady, getMetrics])

  useEffect(() => {
    // Auto-load WASM module on mount
    loadWasm()
  }, [loadWasm])

  const value: MultithreadingContextType = {
    wasmModule,
    isLoading,
    isReady,
    threadingReady,
    error,
    loadWasm,
    executeComputation,
    executeAdvancedTask,
    getPerformanceMetrics,
    getThreadingMetrics,
    createComputePool
  }

  return (
    <MultithreadingContext.Provider value={value}>
      {children}
    </MultithreadingContext.Provider>
  )
}

export function useWasm() {
  const context = useContext(MultithreadingContext)
  if (context === undefined) {
    throw new Error('useWasm must be used within a MultithreadingProvider')
  }
  return context
}

export function useAdvancedCompute() {
  const context = useContext(MultithreadingContext)
  if (context === undefined) {
    throw new Error('useAdvancedCompute must be used within a MultithreadingProvider')
  }
  return {
    executeAdvancedTask: context.executeAdvancedTask,
    createComputePool: context.createComputePool,
    getThreadingMetrics: context.getThreadingMetrics,
    threadingReady: context.threadingReady
  }
}

// Legacy export for backward compatibility
export { MultithreadingProvider as WasmProvider }

