'use client'

/**
 * Phoenix Development Mock
 * Simulates Phoenix WebSocket channels for development when actual Phoenix server isn't running
 * This enables FLAME pattern development without requiring full REASSEMBLY orchestrator setup
 */

interface MockChannel {
  topic: string
  params: any
  callbacks: { [event: string]: Function[] }
  pushCallbacks: Map<string, { receive: Function; timeout?: NodeJS.Timeout }>
  isJoined: boolean
}

interface MockSocket {
  endpoint: string
  params: any
  channels: Map<string, MockChannel>
  isConnected: boolean
  onOpenCallback?: Function
  onErrorCallback?: Function
  onCloseCallback?: Function
  heartbeatInterval?: NodeJS.Timeout
}

export class PhoenixMockSocket {
  private socket: MockSocket
  private jobQueue: Map<string, any> = new Map()
  private simulatedJobs: Map<string, any> = new Map()
  private metricsTimer: NodeJS.Timeout | null = null
  private backendStatus = {
    julia: 'healthy',
    rust_native: 'healthy', 
    wasm: 'healthy',
    gpu: 'healthy'
  }

  constructor(endpoint: string, params: any = {}) {
    this.socket = {
      endpoint,
      params,
      channels: new Map(),
      isConnected: false
    }
  }

  connect() {
    console.log('ðŸ”¥ FLAME: Connecting to Phoenix mock (development mode)')

    // Simulate connection delay
    setTimeout(() => {
      this.socket.isConnected = true
      if (this.socket.onOpenCallback) {
        this.socket.onOpenCallback()
      }
      this.startSimulation()
    }, 500)
  }

  disconnect() {
    this.socket.isConnected = false
    if (this.metricsTimer) {
      clearInterval(this.metricsTimer)
    }
    if (this.socket.heartbeatInterval) {
      clearInterval(this.socket.heartbeatInterval)
    }
    if (this.socket.onCloseCallback) {
      this.socket.onCloseCallback()
    }
    console.log('ðŸ”¥ FLAME: Disconnected from Phoenix mock')
  }

  onOpen(callback: Function) {
    this.socket.onOpenCallback = callback
  }

  onError(callback: Function) {
    this.socket.onErrorCallback = callback
  }

  onClose(callback: Function) {
    this.socket.onCloseCallback = callback
  }

  channel(topic: string, params: any = {}) {
    const channel = new PhoenixMockChannel(topic, params, this)
    this.socket.channels.set(topic, {
      topic,
      params,
      callbacks: {},
      pushCallbacks: new Map(),
      isJoined: false
    })
    return channel
  }

  private startSimulation() {
    // Simulate heartbeat
    this.socket.heartbeatInterval = setInterval(() => {
      // Phoenix heartbeat simulation
    }, 30000)

    // Start metrics simulation
    this.metricsTimer = setInterval(() => {
      this.simulateMetrics()
    }, 1000)

    // Simulate random backend health changes
    setInterval(() => {
      this.simulateBackendHealthChanges()
    }, 30000)
  }

  private simulateMetrics() {
    const metricsChannel = this.socket.channels.get('metrics:performance')
    if (metricsChannel && metricsChannel.isJoined) {
      const metrics = {
        timestamp: Date.now(),
        cpu_usage: Math.random() * 80 + 10,
        memory_usage: Math.random() * 70 + 20,
        queue_length: Array.from(this.jobQueue.values()).filter(j => j.status === 'queued').length,
        active_jobs: Array.from(this.simulatedJobs.values()).filter(j => j.status === 'running').length,
        completed_jobs: Array.from(this.simulatedJobs.values()).filter(j => j.status === 'completed').length,
        failed_jobs: Array.from(this.simulatedJobs.values()).filter(j => j.status === 'failed').length,
        throughput: Math.random() * 50 + 25,
        avg_execution_time: Math.random() * 1000 + 500,
        backend_health: this.backendStatus,
        websocket_latency: Math.random() * 50 + 10,
        messages_sent: Math.floor(Math.random() * 1000),
        messages_received: Math.floor(Math.random() * 1000)
      }

      const callbacks = metricsChannel.callbacks['performance_update'] || []
      callbacks.forEach(callback => callback(metrics))
    }
  }

  private simulateBackendHealthChanges() {
    const backends = ['julia', 'rust_native', 'wasm', 'gpu']
    const statuses = ['healthy', 'degraded']

    backends.forEach(backend => {
      if (Math.random() > 0.9) { // 10% chance of status change
        this.backendStatus[backend] = statuses[Math.floor(Math.random() * statuses.length)]
      }
    })

    const backendChannel = this.socket.channels.get('backend:status')
    if (backendChannel && backendChannel.isJoined) {
      const callbacks = backendChannel.callbacks['backend_status'] || []
      callbacks.forEach(callback => callback({
        backend_status: this.backendStatus,
        timestamp: Date.now()
      }))
    }
  }

  // Simulate job processing
  processJob(jobId: string, jobSpec: any) {
    const job = {
      id: jobId,
      ...jobSpec,
      status: 'queued',
      submitted_at: Date.now()
    }

    this.jobQueue.set(jobId, job)
    this.simulatedJobs.set(jobId, job)

    // Simulate job progression
    setTimeout(() => {
      job.status = 'running'
      this.notifyJobStatus(job)

      // Simulate execution time based on operation
      const executionTime = this.getExecutionTime(jobSpec.operation)

      setTimeout(() => {
        if (Math.random() > 0.95) { // 5% failure rate
          job.status = 'failed'
          job.error = 'Simulated computation error'
        } else {
          job.status = 'completed'
          job.result = this.generateMockResult(jobSpec.operation, jobSpec.args)
          job.performance = {
            backend: this.selectBackend(jobSpec.operation),
            execution_time: executionTime,
            memory_used: Math.random() * 100 + 50,
            threads_used: Math.floor(Math.random() * 8) + 1
          }
        }

        this.notifyJobStatus(job)
        this.jobQueue.delete(jobId)
      }, executionTime)
    }, Math.random() * 1000 + 100) // Queue delay
  }

  private notifyJobStatus(job: any) {
    const orchestratorChannel = this.socket.channels.get('orchestrator:main')
    if (orchestratorChannel && orchestratorChannel.isJoined) {
      const callbacks = orchestratorChannel.callbacks['job_status'] || []
      callbacks.forEach(callback => callback({
        job_id: job.id,
        status: job.status,
        progress: job.status === 'running' ? Math.random() * 100 : undefined,
        result: job.result,
        error: job.error,
        performance: job.performance
      }))

      if (job.status === 'completed') {
        const completedCallbacks = orchestratorChannel.callbacks['job_completed'] || []
        completedCallbacks.forEach(callback => callback(job))
      } else if (job.status === 'failed') {
        const failedCallbacks = orchestratorChannel.callbacks['job_failed'] || []
        failedCallbacks.forEach(callback => callback(job))
      }
    }
  }

  private getExecutionTime(operation: string): number {
    const times = {
      fibonacci: 200,
      mandelbrot: 1500,
      matrixMultiply: 1000,
      primeSieve: 800,
      monteCarloPI: 1200
    }
    return times[operation as keyof typeof times] || 500
  }

  private selectBackend(operation: string): string {
    const backends = {
      fibonacci: 'julia',
      mandelbrot: 'rust_native',
      matrixMultiply: 'julia',
      primeSieve: 'rust_native',
      monteCarloPI: 'julia'
    }
    return backends[operation as keyof typeof backends] || 'wasm'
  }

  private generateMockResult(operation: string, args: any[]): any {
    switch (operation) {
      case 'fibonacci':
        return Array.from({ length: args[0] || 10 }, (_, i) => i <= 1 ? 1 : i)
      case 'mandelbrot':
        return Array.from({ length: args[1] || 100 }, () => 
          Array.from({ length: args[0] || 100 }, () => Math.floor(Math.random() * (args[2] || 100)))
        )
      case 'matrixMultiply':
        const size = args[0] || 10
        return {
          matrix: Array.from({ length: size }, () => 
            Array.from({ length: size }, () => Math.random() * 2 - 1)
          ),
          size
        }
      case 'primeSieve':
        const limit = args[0] || 100
        return Array.from({ length: Math.floor(limit / 10) }, (_, i) => (i + 1) * 10 + 7)
      case 'monteCarloPI':
        return {
          piEstimate: 3.14159 + (Math.random() - 0.5) * 0.01,
          samples: args[0] || 10000
        }
      default:
        return { mock: true, operation, args }
    }
  }
}

export class PhoenixMockChannel {
  private channel: MockChannel
  private socket: PhoenixMockSocket

  constructor(topic: string, params: any, socket: PhoenixMockSocket) {
    this.socket = socket
    this.channel = socket['socket'].channels.get(topic)!
  }

  join(timeout = 10000) {
    console.log(`ðŸ”¥ FLAME: Joining mock channel: ${this.channel.topic}`)

    return {
      receive: (status: string, callback: Function) => {
        if (status === 'ok') {
          setTimeout(() => {
            this.channel.isJoined = true
            callback({ status: 'joined', topic: this.channel.topic })
          }, 100)
        }
        return this
      }
    }
  }

  leave() {
    this.channel.isJoined = false
    console.log(`ðŸ”¥ FLAME: Left mock channel: ${this.channel.topic}`)
  }

  on(event: string, callback: Function) {
    if (!this.channel.callbacks[event]) {
      this.channel.callbacks[event] = []
    }
    this.channel.callbacks[event].push(callback)
  }

  off(event: string, callback?: Function) {
    if (callback) {
      const callbacks = this.channel.callbacks[event] || []
      const index = callbacks.indexOf(callback)
      if (index > -1) {
        callbacks.splice(index, 1)
      }
    } else {
      this.channel.callbacks[event] = []
    }
  }

  push(event: string, payload: any, timeout = 10000) {
    console.log(`ðŸ”¥ FLAME: Mock push to ${this.channel.topic}:${event}`, payload)

    const pushId = `${Date.now()}_${Math.random()}`

    return {
      receive: (status: string, callback: Function) => {
        setTimeout(() => {
          if (status === 'ok') {
            this.handleMockPush(event, payload, callback)
          }
        }, 50)
        return this
      }
    }
  }

  private handleMockPush(event: string, payload: any, callback: Function) {
    switch (event) {
      case 'submit_job':
        const jobId = `mock_job_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
        callback({ job_id: jobId, status: 'queued' })
        this.socket.processJob(jobId, payload.job_spec)
        break

      case 'get_job_status':
        const job = this.socket['simulatedJobs'].get(payload.job_id)
        if (job) {
          callback({ job_status: job })
        } else {
          callback({ error: 'Job not found' })
        }
        break

      case 'cancel_job':
        callback({ success: true })
        break

      case 'get_stats':
        callback({
          stats: {
            orchestrator_status: 'running',
            uptime_seconds: 3600,
            jobs_submitted: 150,
            jobs_completed: 140,
            jobs_failed: 8,
            active_jobs: 2,
            queue_length: 1,
            workers: 8,
            backends: 4
          }
        })
        break

      case 'get_status':
        callback({
          backend_status: this.socket['backendStatus']
        })
        break

      case 'subscribe_metrics':
      case 'subscribe_status':
        callback({ subscribed: true })
        break

      default:
        callback({ success: true })
    }
  }
}

// Export singleton for development
let mockSocket: PhoenixMockSocket | null = null

export function createMockSocket(endpoint: string, params: any = {}): PhoenixMockSocket {
  if (!mockSocket) {
    mockSocket = new PhoenixMockSocket(endpoint, params)
  }
  return mockSocket
}

export function getMockSocket(): PhoenixMockSocket | null {
  return mockSocket
}

