  status: 'queued' | 'running' | 'completed' | 'failed'
  progress?: number
  result?: any
  error?: string
  performance?: {
    backend: string
    execution_time: number
    memory_used: number
    threads_used: number
  }
}

type JobUpdateCallback = (update: JobUpdate) => void
type MetricsCallback = (metrics: any) => void
type BackendStatusCallback = (status: any) => void

/**
 * FLAME Pattern Phoenix WebSocket Integration
 * Direct real-time connection to REASSEMBLY orchestrator bypassing REST APIs
 */
export class PhoenixRealtimeClient {
  private socket: Socket | null = null
  private orchestratorChannel: Channel | null = null
  private metricsChannel: Channel | null = null
  private backendChannel: Channel | null = null
  private isConnected: boolean = false
  private jobCallbacks: Map<string, JobUpdateCallback> = new Map()
  private globalJobCallback: JobUpdateCallback | null = null
  private metricsCallback: MetricsCallback | null = null
  private backendStatusCallback: BackendStatusCallback | null = null
  private reconnectAttempts: number = 0
  private maxReconnectAttempts: number = 10

  constructor(private config: PhoenixChannelConfig) {}

  /**
   * Establish real-time WebSocket connection to Phoenix orchestrator
   * This bypasses all REST API limitations and enables true real-time coordination
   */
  async connect(): Promise<boolean> {
    try {
      // Create Phoenix WebSocket connection
      this.socket = new Socket(this.config.endpoint, {
        params: {
          token: this.config.token,
          user_agent: 'FLAME-Katalyst-Client/1.0',
          ...this.config.params
        },
        logger: this.config.logger || false,
        reconnectAfterMs: (tries) => {
          // Exponential backoff with jitter
          const baseDelay = Math.min(1000 * Math.pow(2, tries), 30000)
          return baseDelay + Math.random() * 1000
        }
      })

      // Setup connection event handlers
      this.socket.onOpen(() => {
        console.log('ðŸ”¥ FLAME: Connected to REASSEMBLY Phoenix orchestrator')
        this.isConnected = true
        this.reconnectAttempts = 0
        this.joinChannels()
      })

      this.socket.onError((error) => {
        console.error('ðŸ”¥ FLAME: Phoenix connection error:', error)
        this.isConnected = false
      })

      this.socket.onClose(() => {
        console.log('ðŸ”¥ FLAME: Phoenix connection closed')
        this.isConnected = false
        this.handleReconnection()
      })

      // Connect to Phoenix
      this.socket.connect()

      // Wait for connection or timeout
      return new Promise((resolve) => {
        const timeout = setTimeout(() => resolve(false), 10000)

        const checkConnection = () => {
          if (this.isConnected) {
            clearTimeout(timeout)
            resolve(true)
          } else {
            setTimeout(checkConnection, 100)
          }
        }

        checkConnection()
      })

    } catch (error) {
      console.error('ðŸ”¥ FLAME: Failed to connect to Phoenix:', error)
      return false
    }
  }

  /**
   * Join Phoenix channels for different aspects of the orchestrator
   */
  private async joinChannels() {
    if (!this.socket) return

    try {
      // Join orchestrator channel for job management
      this.orchestratorChannel = this.socket.channel('orchestrator:main', {
        client_type: 'flame_katalyst',
        capabilities: ['julia', 'wasm', 'multithreading', 'ai_subagents']
      })

      this.orchestratorChannel.join()
        .receive('ok', (resp) => {
          console.log('ðŸ”¥ FLAME: Joined orchestrator channel', resp)
          this.setupOrchestratorHandlers()
        })
        .receive('error', (resp) => {
          console.error('ðŸ”¥ FLAME: Failed to join orchestrator channel', resp)
        })

      // Join metrics channel for real-time performance data
      this.metricsChannel = this.socket.channel('metrics:performance', {})

      this.metricsChannel.join()
        .receive('ok', (resp) => {
          console.log('ðŸ”¥ FLAME: Joined metrics channel', resp)
          this.setupMetricsHandlers()
        })
        .receive('error', (resp) => {
          console.error('ðŸ”¥ FLAME: Failed to join metrics channel', resp)
        })

      // Join backend status channel
      this.backendChannel = this.socket.channel('backend:status', {})

      this.backendChannel.join()
        .receive('ok', (resp) => {
          console.log('ðŸ”¥ FLAME: Joined backend status channel', resp)
          this.setupBackendHandlers()
        })
        .receive('error', (resp) => {
          console.error('ðŸ”¥ FLAME: Failed to join backend status channel', resp)
        })

    } catch (error) {
      console.error('ðŸ”¥ FLAME: Error joining channels:', error)
    }
  }

  /**
   * Setup orchestrator event handlers for job lifecycle management
   */
  private setupOrchestratorHandlers() {
    if (!this.orchestratorChannel) return

    // Job status updates via Phoenix PubSub
    this.orchestratorChannel.on('job_status', (payload: JobUpdate) => {
      console.log('ðŸ”¥ FLAME: Job status update:', payload)

      // Call specific job callback
      const callback = this.jobCallbacks.get(payload.job_id)
      if (callback) {
        callback(payload)
      }

      // Call global job callback
      if (this.globalJobCallback) {
        this.globalJobCallback(payload)
      }
    })

    // Job completion notifications
    this.orchestratorChannel.on('job_completed', (payload) => {
      console.log('ðŸ”¥ FLAME: Job completed:', payload)
      this.handleJobCompletion(payload)
    })

    // Job failure notifications
    this.orchestratorChannel.on('job_failed', (payload) => {
      console.log('ðŸ”¥ FLAME: Job failed:', payload)
      this.handleJobFailure(payload)
    })

    // Queue status updates
    this.orchestratorChannel.on('queue_update', (payload) => {
      console.log('ðŸ”¥ FLAME: Queue update:', payload)
      // Handle queue status changes
    })
  }

  /**
   * Setup metrics handlers for real-time performance monitoring
   */
  private setupMetricsHandlers() {
    if (!this.metricsChannel) return

    // Real-time performance metrics via Phoenix LiveView updates
    this.metricsChannel.on('performance_update', (payload) => {
      if (this.metricsCallback) {
        this.metricsCallback(payload)
      }
    })

    // System health updates
    this.metricsChannel.on('health_check', (payload) => {
      console.log('ðŸ”¥ FLAME: System health update:', payload)
    })
  }

  /**
   * Setup backend status handlers
   */
  private setupBackendHandlers() {
    if (!this.backendChannel) return

    // Backend availability changes
    this.backendChannel.on('backend_status', (payload) => {
      if (this.backendStatusCallback) {
        this.backendStatusCallback(payload)
      }
    })

    // Backend health updates
    this.backendChannel.on('health_update', (payload) => {
      console.log('ðŸ”¥ FLAME: Backend health update:', payload)
    })
  }

  /**
   * Submit computation job directly through Phoenix channels
   * This bypasses REST API overhead and enables immediate queuing
   */
  async submitJob(job: ComputeJob, callback?: JobUpdateCallback): Promise<string> {
    if (!this.orchestratorChannel || !this.isConnected) {
      throw new Error('Phoenix orchestrator not connected')
    }

    // Register callback for this specific job
    if (callback) {
      this.jobCallbacks.set(job.id, callback)
    }

    return new Promise((resolve, reject) => {
      this.orchestratorChannel!.push('submit_job', {
        job_spec: {
          id: job.id,
          operation: job.operation,
          args: job.args,
          priority: job.priority,
          backend: job.backend,
          subagent_requirement: job.subagentRequirement,
          client_info: {
            type: 'flame_katalyst',
            timestamp: Date.now(),
            user_agent: navigator?.userAgent
          }
        }
      })
      .receive('ok', (response) => {
        console.log('ðŸ”¥ FLAME: Job submitted successfully:', response)
        resolve(response.job_id)
      })
      .receive('error', (response) => {
        console.error('ðŸ”¥ FLAME: Job submission failed:', response)
        reject(new Error(response.reason || 'Job submission failed'))
      })
      .receive('timeout', () => {
        reject(new Error('Job submission timeout'))
      })
    })
  }

  /**
   * Request real-time job status through Phoenix channels
   */
  async getJobStatus(jobId: string): Promise<JobUpdate> {
    if (!this.orchestratorChannel || !this.isConnected) {
      throw new Error('Phoenix orchestrator not connected')
    }

    return new Promise((resolve, reject) => {
      this.orchestratorChannel!.push('get_job_status', { job_id: jobId })
        .receive('ok', (response) => {
          resolve(response.job_status)
        })
        .receive('error', (response) => {
          reject(new Error(response.reason || 'Status check failed'))
        })
        .receive('timeout', () => {
          reject(new Error('Status check timeout'))
        })
    })
  }

  /**
   * Cancel a running job through Phoenix channels
   */
  async cancelJob(jobId: string): Promise<boolean> {
    if (!this.orchestratorChannel || !this.isConnected) {
      throw new Error('Phoenix orchestrator not connected')
    }

    return new Promise((resolve, reject) => {
      this.orchestratorChannel!.push('cancel_job', { job_id: jobId })
        .receive('ok', () => {
          console.log('ðŸ”¥ FLAME: Job cancelled:', jobId)
          resolve(true)
        })
        .receive('error', (response) => {
          reject(new Error(response.reason || 'Job cancellation failed'))
        })
        .receive('timeout', () => {
          reject(new Error('Job cancellation timeout'))
        })
    })
  }

  /**
   * Get real-time backend status
   */
  async getBackendStatus(): Promise<any> {
    if (!this.backendChannel || !this.isConnected) {
      throw new Error('Phoenix backend channel not connected')
    }

    return new Promise((resolve, reject) => {
      this.backendChannel!.push('get_status', {})
        .receive('ok', (response) => {
          resolve(response.backend_status)
        })
        .receive('error', (response) => {
          reject(new Error(response.reason || 'Backend status check failed'))
        })
        .receive('timeout', () => {
          reject(new Error('Backend status timeout'))
        })
    })
  }

  /**
   * Subscribe to real-time performance metrics
   */
  subscribeToMetrics(callback: MetricsCallback) {
    this.metricsCallback = callback

    if (this.metricsChannel && this.isConnected) {
      // Request immediate metrics update
      this.metricsChannel.push('subscribe_metrics', {
        interval: 1000,
        detailed: true
      })
    }
  }

  /**
   * Subscribe to backend status updates
   */
  subscribeToBackendStatus(callback: BackendStatusCallback) {
    this.backendStatusCallback = callback

    if (this.backendChannel && this.isConnected) {
      this.backendChannel.push('subscribe_status', {})
    }
  }

  /**
   * Subscribe to all job updates
   */
  subscribeToJobs(callback: JobUpdateCallback) {
    this.globalJobCallback = callback
  }

  /**
   * Handle job completion
   */
  private handleJobCompletion(payload: any) {
    // Clean up job-specific callback
    this.jobCallbacks.delete(payload.job_id)
  }

  /**
   * Handle job failure
   */
  private handleJobFailure(payload: any) {
    // Clean up job-specific callback
    this.jobCallbacks.delete(payload.job_id)
  }

  /**
   * Handle reconnection with exponential backoff
   */
  private handleReconnection() {
    if (this.reconnectAttempts >= this.maxReconnectAttempts) {
      console.error('ðŸ”¥ FLAME: Max reconnection attempts reached')
      return
    }

    this.reconnectAttempts++
    const delay = Math.min(1000 * Math.pow(2, this.reconnectAttempts), 30000)

    console.log(`ðŸ”¥ FLAME: Attempting reconnection in ${delay}ms (attempt ${this.reconnectAttempts})`)

    setTimeout(() => {
      this.connect()
    }, delay)
  }

  /**
   * Disconnect from Phoenix
   */
  disconnect() {
    if (this.socket) {
      this.socket.disconnect()
      this.socket = null
    }

    this.isConnected = false
    this.orchestratorChannel = null
    this.metricsChannel = null
    this.backendChannel = null
    this.jobCallbacks.clear()
    this.globalJobCallback = null
    this.metricsCallback = null
    this.backendStatusCallback = null

    console.log('ðŸ”¥ FLAME: Disconnected from Phoenix orchestrator')
  }

  /**
   * Get connection status
   */
  isSocketConnected(): boolean {
    return this.isConnected && this.socket !== null
  }

  /**
   * Get orchestrator statistics through Phoenix channels
   */
  async getOrchestratorStats(): Promise<any> {
    if (!this.orchestratorChannel || !this.isConnected) {
      throw new Error('Phoenix orchestrator not connected')
    }

    return new Promise((resolve, reject) => {
      this.orchestratorChannel!.push('get_stats', {})
        .receive('ok', (response) => {
          resolve(response.stats)
        })
        .receive('error', (response) => {
          reject(new Error(response.reason || 'Stats retrieval failed'))
        })
        .receive('timeout', () => {
          reject(new Error('Stats retrieval timeout'))
        })
    })
  }
}

// Singleton instance for FLAME pattern
let phoenixClient: PhoenixRealtimeClient | null = null

export function getPhoenixClient(config?: PhoenixChannelConfig): PhoenixRealtimeClient {
  if (!phoenixClient && config) {
    phoenixClient = new PhoenixRealtimeClient(config)
  }

  if (!phoenixClient) {
    throw new Error('Phoenix client not initialized. Provide config on first call.')
  }

  return phoenixClient
}

export function initializePhoenixClient(config: PhoenixChannelConfig): PhoenixRealtimeClient {
  phoenixClient = new PhoenixRealtimeClient(config)
  return phoenixClient
}

