'use client'

/**
 * Julia Compute WebWorker - FLAME Pattern Integration
 * 
 * This worker provides isolated Julia computation execution with:
 * - WASM Julia module loading and execution
 * - Real-time progress reporting via postMessage
 * - Error handling and timeout management
 * - Memory usage monitoring and cleanup
 * - Phoenix WebSocket communication coordination
 */

interface WorkerMessage {
  id: string
  type: 'compute' | 'cancel' | 'status' | 'cleanup'
  operation?: string
  args?: any[]
  options?: {
    timeout?: number
    priority?: 'low' | 'normal' | 'high' | 'critical'
    phoenixJobId?: string
