    memoryUsed: number
    threadsUsed: number
    wasmOptimized: boolean
  }
}

class JuliaComputeWorker {
  private wasmModule: any = null
  private isInitialized: boolean = false
  private activeComputations: Map<string, AbortController> = new Map()
  private lastMemoryUsage: number = 0

  constructor() {
    this.initialize()
  }

  async initialize() {
    try {
      console.log('ðŸ”¥ FLAME Worker: Initializing Julia WASM module')

      // Load Julia WASM module in worker context
      const { default: JuliaWasm } = await import('/wasm/julia_wasm_bindings.js')
      this.wasmModule = new JuliaWasm()

      await this.wasmModule.initialize()
      this.isInitialized = true

      this.postMessage({
        id: 'init',
        type: 'status',
        data: { initialized: true, ready: true }
      })

      console.log('ðŸ”¥ FLAME Worker: Julia WASM initialized successfully')
    } catch (error) {
      console.error('ðŸ”¥ FLAME Worker: Failed to initialize:', error)
      this.postMessage({
        id: 'init',
        type: 'error',
        error: `Worker initialization failed: ${error instanceof Error ? error.message : 'Unknown error'}`
      })
    }
  }

  async handleMessage(message: WorkerMessage) {
    const { id, type, operation, args, options } = message

    try {
      switch (type) {
        case 'compute':
          await this.executeComputation(id, operation!, args!, options)
          break

        case 'cancel':
          this.cancelComputation(id)
          break

        case 'status':
          this.sendStatus(id)
          break

        case 'cleanup':
          this.cleanup()
          break

        default:
          throw new Error(`Unknown message type: ${type}`)
      }
    } catch (error) {
      this.postMessage({
        id,
        type: 'error',
        error: error instanceof Error ? error.message : 'Unknown error'
      })
    }
  }

  async executeComputation(
    id: string, 
    operation: string, 
    args: any[], 
    options: WorkerMessage['options'] = {}
  ) {
    if (!this.isInitialized || !this.wasmModule) {
      throw new Error('Worker not initialized')
    }

    const abortController = new AbortController()
    this.activeComputations.set(id, abortController)

    const startTime = performance.now()
    const timeout = options.timeout || 30000

    try {
      // Set up timeout
      const timeoutId = setTimeout(() => {
        abortController.abort()
        this.postMessage({
          id,
          type: 'error',
          error: 'Computation timeout'
        })
      }, timeout)

      // Send initial progress
      this.postMessage({
        id,
        type: 'progress',
        progress: 0
      })

      // Execute computation with progress monitoring
      const result = await this.executeWithProgress(
        id, 
        operation, 
        args, 
        abortController.signal
      )

      clearTimeout(timeoutId)

      if (abortController.signal.aborted) {
        return
      }

      const executionTime = performance.now() - startTime
      const memoryUsed = this.estimateMemoryUsage()

      // Send final result
      this.postMessage({
        id,
        type: 'result',
        data: result,
        performance: {
          executionTime,
          memoryUsed,
          threadsUsed: 1, // WASM is single-threaded
          wasmOptimized: true
        }
      })

    } catch (error) {
      if (!abortController.signal.aborted) {
        this.postMessage({
          id,
          type: 'error',
          error: error instanceof Error ? error.message : 'Computation failed'
        })
      }
    } finally {
      this.activeComputations.delete(id)
    }
  }

  async executeWithProgress(
    id: string,
    operation: string,
    args: any[],
    signal: AbortSignal
  ): Promise<any> {
    // Check if computation is aborted
    if (signal.aborted) {
      throw new Error('Computation aborted')
    }

    let result: any

    switch (operation) {
      case 'fibonacci':
        result = await this.computeFibonacciWithProgress(id, args[0], signal)
        break

      case 'mandelbrot':
        result = await this.computeMandelbrotWithProgress(id, args[0], args[1], args[2], signal)
        break

      case 'matrixMultiply':
        result = await this.computeMatrixMultiplyWithProgress(id, args[0], signal)
        break

      case 'primeSieve':
        result = await this.computePrimeSieveWithProgress(id, args[0], signal)
        break

      case 'monteCarloPI':
        result = await this.computeMonteCarloWithProgress(id, args[0], signal)
        break

      default:
        // Fallback to direct WASM call
        result = this.wasmModule[operation](...args)
    }

    return result
  }

  async computeFibonacciWithProgress(id: string, n: number, signal: AbortSignal): Promise<number[]> {
    const result = new Array(n)
    if (n <= 0) return []
    if (n === 1) return [1]

    result[0] = 1
    result[1] = 1

    for (let i = 2; i < n; i++) {
      if (signal.aborted) throw new Error('Aborted')

      result[i] = result[i-1] + result[i-2]

      // Report progress every 100 iterations
      if (i % 100 === 0) {
        this.postMessage({
          id,
          type: 'progress',
          progress: (i / n) * 100
        })
      }
    }

    return result
  }

  async computeMandelbrotWithProgress(
    id: string, 
    width: number, 
    height: number, 
    maxIter: number, 
    signal: AbortSignal
  ): Promise<number[][]> {
    const result = new Array(height)
    const xMin = -2.5, xMax = 1.0
    const yMin = -1.25, yMax = 1.25
    const dx = (xMax - xMin) / width
    const dy = (yMax - yMin) / height

    for (let i = 0; i < height; i++) {
      if (signal.aborted) throw new Error('Aborted')

      result[i] = new Array(width)
      const y = yMin + i * dy

      for (let j = 0; j < width; j++) {
        const x = xMin + j * dx
        let zx = 0, zy = 0, iter = 0

        while (iter < maxIter && zx*zx + zy*zy < 4) {
          const tempX = zx*zx - zy*zy + x
          zy = 2*zx*zy + y
          zx = tempX
          iter++
        }

        result[i][j] = iter
      }

      // Report progress every row
      this.postMessage({
        id,
        type: 'progress',
        progress: (i / height) * 100
      })
    }

    return result
  }

  async computeMatrixMultiplyWithProgress(
    id: string, 
    size: number, 
    signal: AbortSignal
  ): Promise<any> {
    const A = this.randomMatrix(size, size)
    const B = this.randomMatrix(size, size)
    const C = new Array(size)

    for (let i = 0; i < size; i++) {
      if (signal.aborted) throw new Error('Aborted')

      C[i] = new Array(size)
      for (let j = 0; j < size; j++) {
        C[i][j] = 0
        for (let k = 0; k < size; k++) {
          C[i][j] += A[i][k] * B[k][j]
        }
      }

      // Report progress
      this.postMessage({
        id,
        type: 'progress',
        progress: (i / size) * 100
      })
    }

    return {
      matrix: C,
      size,
      flops: 2 * size * size * size
    }
  }

  async computePrimeSieveWithProgress(
    id: string, 
    limit: number, 
    signal: AbortSignal
  ): Promise<number[]> {
    if (limit < 2) return []

    const sieve = new Array(limit + 1).fill(true)
    sieve[0] = sieve[1] = false

    for (let i = 2; i * i <= limit; i++) {
      if (signal.aborted) throw new Error('Aborted')

      if (sieve[i]) {
        for (let j = i * i; j <= limit; j += i) {
          sieve[j] = false
        }
      }

      // Report progress
      if (i % 100 === 0) {
        this.postMessage({
          id,
          type: 'progress',
          progress: (i / Math.sqrt(limit)) * 100
        })
      }
    }

    const primes = []
    for (let i = 2; i <= limit; i++) {
      if (sieve[i]) primes.push(i)
    }

    return primes
  }

  async computeMonteCarloWithProgress(
    id: string, 
    samples: number, 
    signal: AbortSignal
  ): Promise<any> {
    let hits = 0
    const batchSize = 10000

    for (let batch = 0; batch < samples; batch += batchSize) {
      if (signal.aborted) throw new Error('Aborted')

      const currentBatch = Math.min(batchSize, samples - batch)

      for (let i = 0; i < currentBatch; i++) {
        const x = Math.random() * 2 - 1
        const y = Math.random() * 2 - 1
        if (x*x + y*y <= 1) hits++
      }

      // Report progress every batch
      this.postMessage({
        id,
        type: 'progress',
        progress: (batch / samples) * 100
      })
    }

    const piEstimate = 4 * hits / samples
    return {
      piEstimate,
      samples,
      accuracy: Math.abs(piEstimate - Math.PI)
    }
  }

  cancelComputation(id: string) {
    const controller = this.activeComputations.get(id)
    if (controller) {
      controller.abort()
      this.activeComputations.delete(id)
    }
  }

  sendStatus(id: string) {
    this.postMessage({
      id,
      type: 'status',
      data: {
        initialized: this.isInitialized,
        activeComputations: this.activeComputations.size,
        memoryUsage: this.estimateMemoryUsage()
      }
    })
  }

  cleanup() {
    this.activeComputations.forEach(controller => controller.abort())
    this.activeComputations.clear()

    if (this.wasmModule) {
      this.wasmModule.cleanup?.()
    }
  }

  private randomMatrix(rows: number, cols: number): number[][] {
    const matrix = new Array(rows)
    for (let i = 0; i < rows; i++) {
      matrix[i] = new Array(cols)
      for (let j = 0; j < cols; j++) {
        matrix[i][j] = Math.random() * 2 - 1
      }
    }
    return matrix
  }

  private estimateMemoryUsage(): number {
    // Estimate memory usage based on performance.memory if available
    if (typeof performance !== 'undefined' && performance.memory) {
      const current = performance.memory.usedJSHeapSize / 1024 / 1024 // MB
      const delta = current - this.lastMemoryUsage
      this.lastMemoryUsage = current
      return current
    }
    return 0
  }

  private postMessage(response: WorkerResponse) {
    self.postMessage(response)
  }
}

// Initialize worker
const worker = new JuliaComputeWorker()

// Handle incoming messages
self.onmessage = (event: MessageEvent<WorkerMessage>) => {
  worker.handleMessage(event.data)
}

// Handle worker errors
self.onerror = (error) => {
  console.error('ðŸ”¥ FLAME Worker Error:', error)
  self.postMessage({
    id: 'error',
    type: 'error',
    error: 'Worker runtime error'
  })
}

export {}

