'use client'

/**
 * WebWorker Pool Manager - FLAME Pattern
 * 
 * Manages a pool of Julia compute workers for parallel processing:
 * - Dynamic worker allocation based on hardware capabilities
 * - Load balancing across available workers
 * - Job queuing and priority scheduling
 * - Worker lifecycle management
 * - Performance monitoring and optimization
 */

interface WorkerTask {
  id: string
  operation: string
  args: any[]
  priority: 'low' | 'normal' | 'high' | 'critical'
  timeout?: number
  phoenixJobId?: string
  resolve: (result: any) => void
  reject: (error: Error) => void
  onProgress?: (progress: number) => void
}

interface WorkerInstance {
  id: string
  worker: Worker
  busy: boolean
  currentTask: string | null
  tasksCompleted: number
  lastActiveTime: number
  performanceScore: number
}

interface PoolMetrics {
  totalWorkers: number
  activeWorkers: number
  queueLength: number
  tasksCompleted: number
  averageExecutionTime: number
  throughput: number
  memoryUsage: number
}

export class JuliaWorkerPool {
  private workers: Map<string, WorkerInstance> = new Map()
  private taskQueue: WorkerTask[] = []
  private activeTasks: Map<string, WorkerTask> = new Map()
  private metrics: PoolMetrics = {
    totalWorkers: 0,
    activeWorkers: 0,
    queueLength: 0,
    tasksCompleted: 0,
    averageExecutionTime: 0,
    throughput: 0,
    memoryUsage: 0
  }
  private metricsUpdateInterval: NodeJS.Timeout | null = null
  private isInitialized: boolean = false

  constructor(
    private maxWorkers: number = navigator.hardwareConcurrency || 4,
    private minWorkers: number = 2
  ) {
    this.initialize()
  }

  async initialize(): Promise<void> {
    if (this.isInitialized) return

    console.log(`ðŸ”¥ FLAME Pool: Initializing worker pool with ${this.maxWorkers} max workers`)

    // Create initial set of workers
    const initialWorkers = Math.min(this.minWorkers, this.maxWorkers)
    for (let i = 0; i < initialWorkers; i++) {
      await this.createWorker()
    }

    // Start metrics collection
    this.startMetricsCollection()

    this.isInitialized = true
    console.log(`ðŸ”¥ FLAME Pool: Initialized with ${this.workers.size} workers`)
  }

  async executeTask(
    operation: string,
    args: any[],
    options: {
      priority?: 'low' | 'normal' | 'high' | 'critical'
      timeout?: number
      phoenixJobId?: string
      onProgress?: (progress: number) => void
    } = {}
  ): Promise<any> {
    const taskId = `task_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`

    return new Promise<any>((resolve, reject) => {
      const task: WorkerTask = {
        id: taskId,
        operation,
        args,
        priority: options.priority || 'normal',
        timeout: options.timeout,
        phoenixJobId: options.phoenixJobId,
        resolve,
        reject,
        onProgress: options.onProgress
      }

      // Add to queue based on priority
      this.enqueueTask(task)

      // Try to process immediately
      this.processQueue()
    })
  }

  private enqueueTask(task: WorkerTask): void {
    // Insert task based on priority
    const priorities = { critical: 0, high: 1, normal: 2, low: 3 }
    const taskPriority = priorities[task.priority]

    let insertIndex = this.taskQueue.length
    for (let i = 0; i < this.taskQueue.length; i++) {
      if (priorities[this.taskQueue[i].priority] > taskPriority) {
        insertIndex = i
        break
      }
    }

    this.taskQueue.splice(insertIndex, 0, task)
    this.updateMetrics()
  }

  private async processQueue(): Promise<void> {
    if (this.taskQueue.length === 0) return

    // Find available worker or create new one if needed
    let worker = this.getAvailableWorker()

    if (!worker && this.workers.size < this.maxWorkers) {
      worker = await this.createWorker()
    }

    if (worker) {
      const task = this.taskQueue.shift()!
      await this.assignTaskToWorker(worker, task)

      // Continue processing queue
      if (this.taskQueue.length > 0) {
        setTimeout(() => this.processQueue(), 10)
      }
    }
  }

  private getAvailableWorker(): WorkerInstance | null {
    // Find least busy worker
    let bestWorker: WorkerInstance | null = null
    let bestScore = -1

    for (const worker of this.workers.values()) {
      if (!worker.busy) {
        // Calculate worker score based on performance and recency
        const timeSinceActive = Date.now() - worker.lastActiveTime
        const score = worker.performanceScore - (timeSinceActive / 1000) * 0.1

        if (score > bestScore) {
          bestScore = score
          bestWorker = worker
        }
      }
    }

    return bestWorker
  }

  private async createWorker(): Promise<WorkerInstance> {
    const workerId = `worker_${this.workers.size + 1}_${Date.now()}`

    // Create worker from our Julia compute worker
    const worker = new Worker(new URL('./julia-compute-worker.ts', import.meta.url))

    const workerInstance: WorkerInstance = {
      id: workerId,
      worker,
      busy: false,
      currentTask: null,
      tasksCompleted: 0,
      lastActiveTime: Date.now(),
      performanceScore: 1.0
    }

    // Set up worker message handling
    worker.onmessage = (event) => {
      this.handleWorkerMessage(workerInstance, event)
    }

    worker.onerror = (error) => {
      console.error(`ðŸ”¥ FLAME Pool: Worker ${workerId} error:`, error)
      this.handleWorkerError(workerInstance, error)
    }

    // Wait for worker initialization
    await new Promise<void>((resolve) => {
      const initHandler = (event: MessageEvent) => {
        if (event.data.id === 'init' && event.data.type === 'status') {
          worker.removeEventListener('message', initHandler)
          resolve()
        }
      }
      worker.addEventListener('message', initHandler)
    })

    this.workers.set(workerId, workerInstance)
    this.updateMetrics()

    console.log(`ðŸ”¥ FLAME Pool: Created worker ${workerId}`)
    return workerInstance
  }

  private async assignTaskToWorker(worker: WorkerInstance, task: WorkerTask): Promise<void> {
    worker.busy = true
    worker.currentTask = task.id
    worker.lastActiveTime = Date.now()

    this.activeTasks.set(task.id, task)

    try {
      // Send task to worker
      worker.worker.postMessage({
        id: task.id,
        type: 'compute',
        operation: task.operation,
        args: task.args,
        options: {
          timeout: task.timeout,
          priority: task.priority,
          phoenixJobId: task.phoenixJobId
        }
      })
    } catch (error) {
      // Handle worker communication error
      worker.busy = false
      worker.currentTask = null
      this.activeTasks.delete(task.id)
      task.reject(new Error(`Worker communication failed: ${error}`))
    }
  }

  private handleWorkerMessage(worker: WorkerInstance, event: MessageEvent): void {
    const { id, type, data, error, progress, performance } = event.data
    const task = this.activeTasks.get(id)

    if (!task) return

    switch (type) {
      case 'result':
        // Task completed successfully
        worker.busy = false
        worker.currentTask = null
        worker.tasksCompleted++

        // Update performance score based on execution time
        if (performance && performance.executionTime) {
          const normalizedTime = Math.min(performance.executionTime / 1000, 10) // Cap at 10 seconds
          worker.performanceScore = 0.8 * worker.performanceScore + 0.2 * (1 / (1 + normalizedTime))
        }

        this.activeTasks.delete(id)
        task.resolve(data)

        // Continue processing queue
        this.processQueue()
        break

      case 'error':
        // Task failed
        worker.busy = false
        worker.currentTask = null
        worker.performanceScore *= 0.9 // Reduce performance score on error

        this.activeTasks.delete(id)
        task.reject(new Error(error))

        // Continue processing queue
        this.processQueue()
        break

      case 'progress':
        // Progress update
        if (task.onProgress && typeof progress === 'number') {
          task.onProgress(progress)
        }
        break

      case 'status':
        // Worker status update
        break
    }

    this.updateMetrics()
  }

  private handleWorkerError(worker: WorkerInstance, error: ErrorEvent): void {
    console.error(`ðŸ”¥ FLAME Pool: Worker ${worker.id} error:`, error)

    // Clean up failed worker
    if (worker.currentTask) {
      const task = this.activeTasks.get(worker.currentTask)
      if (task) {
        task.reject(new Error('Worker failed'))
        this.activeTasks.delete(worker.currentTask)
      }
    }

    // Remove worker from pool
    this.workers.delete(worker.id)
    worker.worker.terminate()

    // Create replacement worker if below minimum
    if (this.workers.size < this.minWorkers) {
      this.createWorker()
    }

    this.updateMetrics()
  }

  cancelTask(taskId: string): boolean {
    // Check if task is in queue
    const queueIndex = this.taskQueue.findIndex(task => task.id === taskId)
    if (queueIndex !== -1) {
      const task = this.taskQueue.splice(queueIndex, 1)[0]
      task.reject(new Error('Task cancelled'))
      this.updateMetrics()
      return true
    }

    // Check if task is active
    const task = this.activeTasks.get(taskId)
    if (task) {
      // Find worker and send cancel message
      for (const worker of this.workers.values()) {
        if (worker.currentTask === taskId) {
          worker.worker.postMessage({
            id: taskId,
            type: 'cancel'
          })
          break
        }
      }
      return true
    }

    return false
  }

  getMetrics(): PoolMetrics {
    return { ...this.metrics }
  }

  private updateMetrics(): void {
    this.metrics.totalWorkers = this.workers.size
    this.metrics.activeWorkers = Array.from(this.workers.values()).filter(w => w.busy).length
    this.metrics.queueLength = this.taskQueue.length
    this.metrics.tasksCompleted = Array.from(this.workers.values())
      .reduce((sum, w) => sum + w.tasksCompleted, 0)
  }

  private startMetricsCollection(): void {
    this.metricsUpdateInterval = setInterval(() => {
      this.updateMetrics()

      // Calculate throughput (tasks per second over last minute)
      const activeWorkers = Array.from(this.workers.values()).filter(w => w.busy)
      this.metrics.throughput = activeWorkers.length > 0 ? 
        this.metrics.tasksCompleted / 60 : 0

      // Estimate memory usage
      this.metrics.memoryUsage = this.estimatePoolMemoryUsage()
    }, 1000)
  }

  private estimatePoolMemoryUsage(): number {
    if (typeof performance !== 'undefined' && performance.memory) {
      return performance.memory.usedJSHeapSize / 1024 / 1024 // MB
    }
    return this.workers.size * 10 // Rough estimate: 10MB per worker
  }

  async shutdown(): Promise<void> {
    console.log('ðŸ”¥ FLAME Pool: Shutting down worker pool')

    // Clear metrics interval
    if (this.metricsUpdateInterval) {
      clearInterval(this.metricsUpdateInterval)
    }

    // Cancel all queued tasks
    this.taskQueue.forEach(task => {
      task.reject(new Error('Pool shutdown'))
    })
    this.taskQueue = []

    // Cancel all active tasks and terminate workers
    for (const worker of this.workers.values()) {
      if (worker.currentTask) {
        const task = this.activeTasks.get(worker.currentTask)
        if (task) {
          task.reject(new Error('Pool shutdown'))
        }
      }

      worker.worker.postMessage({ id: 'cleanup', type: 'cleanup' })
      worker.worker.terminate()
    }

    this.workers.clear()
    this.activeTasks.clear()
    this.isInitialized = false

    console.log('ðŸ”¥ FLAME Pool: Shutdown complete')
  }
}

// Singleton worker pool instance
let workerPool: JuliaWorkerPool | null = null

export function getWorkerPool(): JuliaWorkerPool {
  if (!workerPool) {
    workerPool = new JuliaWorkerPool()
  }
  return workerPool
}

export async function initializeWorkerPool(
  maxWorkers?: number, 
  minWorkers?: number
): Promise<JuliaWorkerPool> {
  if (workerPool) {
    await workerPool.shutdown()
  }

  workerPool = new JuliaWorkerPool(maxWorkers, minWorkers)
  await workerPool.initialize()

  return workerPool
}

