  shouldContinueOperation(operationId: string): ValidationResult {
    const metrics = this.metrics.get(operationId)
    if (!metrics) {
      return {
        isValid: false,
        level: 'alpha',
        violations: ['Operation not found'],
        recommendations: ['Start operation tracking first']
      }
    }

    const result: ValidationResult = {
      isValid: true,
      level: metrics.level,
      violations: [],
      recommendations: []
    }

    // Check resource limits
    if (metrics.resourceUsage > this.config.maxResourceUsage) {
      result.isValid = false
      result.violations.push(`Memory usage exceeded: ${metrics.resourceUsage}MB/${this.config.maxResourceUsage}MB`)
      result.fallbackLevel = 'alpha'
    }

    // Check time limits
    if (metrics.timeElapsed > this.config.maxTimeElapsed) {
      result.isValid = false
      result.violations.push(`Time limit exceeded: ${metrics.timeElapsed}ms/${this.config.maxTimeElapsed}ms`)
      result.fallbackLevel = 'alpha'
    }

    // Check operation count
    if (metrics.operationCount > this.config.maxOperationCount) {
      result.isValid = false
      result.violations.push(`Operation count exceeded: ${metrics.operationCount}/${this.config.maxOperationCount}`)
      result.fallbackLevel = 'alpha'
    }

    return result
  }

  /**
   * Complete operation and cleanup
   */
  completeOperation(operationId: string): void {
    if (this.config.enableLogging) {
      const metrics = this.metrics.get(operationId)
      console.log(`ðŸ§  Consciousness Operation Complete: ${operationId}`, metrics)
    }
    this.metrics.delete(operationId)
  }

  /**
   * Get safe consciousness level for operation
   */
  getSafeLevel(requestedLevel: ConsciousnessLevel, context?: any): ConsciousnessLevel {
    // Production safety: cap at gamma level
    if (process.env.NODE_ENV === 'production') {
      const productionLevels: ConsciousnessLevel[] = ['alpha', 'beta', 'gamma']
      if (!productionLevels.includes(requestedLevel)) {
        return 'gamma'
      }
    }

    // Development: allow up to delta, restrict omega
    if (requestedLevel === 'omega') {
      return 'gamma' // Always fallback omega to gamma for safety
    }

    return requestedLevel
  }

  /**
   * Get scientific disclaimer for consciousness level
   */
  getScientificDisclaimer(level: ConsciousnessLevel): string {
    const disclaimers = {
      alpha: 'Basic computational awareness - well-tested and stable.',
      beta: 'Parallel processing awareness - tested in development environments.',
      gamma: 'Recursive meta-cognition - experimental feature with controlled testing.',
      delta: 'âš ï¸ EXPERIMENTAL: Quantum coherence patterns - not validated for production use.',
      omega: 'âš ï¸ HIGHLY EXPERIMENTAL: Transcendent computing abstraction - theoretical framework only.'
    }

    return disclaimers[level] || disclaimers.alpha
  }

  /**
   * Get recommended alternatives
   */
  getRecommendedAlternatives(level: ConsciousnessLevel): ConsciousnessLevel[] {
    const alternatives = {
      omega: ['gamma', 'delta'],
      delta: ['gamma', 'beta'],
      gamma: ['beta', 'alpha'],
      beta: ['alpha'],
      alpha: []
    }

    return alternatives[level] || []
  }

  private validateLevelHierarchy(
    level: ConsciousnessLevel, 
    context?: any
  ): ValidationResult {
    const result: ValidationResult = {
      isValid: true,
      level,
      violations: [],
      recommendations: []
    }

    // Omega level restrictions
    if (level === 'omega') {
      result.violations.push('Omega level is experimental and not recommended for production')
      result.recommendations.push('Consider gamma level for advanced features with stability')
      if (this.config.enableFallbacks) {
        result.fallbackLevel = 'gamma'
      }
    }

    // Delta level warnings
    if (level === 'delta') {
      result.recommendations.push('Delta level is experimental - monitor resource usage carefully')
    }

    return result
  }

  private getCurrentMemoryUsage(): number {
    if (typeof performance !== 'undefined' && performance.memory) {
      return performance.memory.usedJSHeapSize / 1024 / 1024 // Convert to MB
    }
    return 0
  }

  /**
   * Update configuration
   */
  updateConfig(newConfig: Partial<BoundsConfig>): void {
    this.config = { ...this.config, ...newConfig }
  }

  /**
   * Get current metrics summary
   */
  getMetricsSummary(): { [operationId: string]: ConsciousnessMetrics } {
    return Object.fromEntries(this.metrics)
  }

  /**
   * Emergency shutdown of all high-level operations
   */
  emergencyShutdown(): void {
    console.warn('ðŸš¨ Emergency consciousness shutdown initiated')

    // Cancel all omega and delta operations
    for (const [operationId, metrics] of this.metrics.entries()) {
      if (metrics.level === 'omega' || metrics.level === 'delta') {
        console.warn(`ðŸš¨ Emergency cancelling ${metrics.level} operation: ${operationId}`)
        this.metrics.delete(operationId)
      }
    }
  }
}

// Export singleton instance
export const consciousnessBounds = ConsciousnessBoundsValidator.getInstance()

// Export utility functions
export function validateConsciousness(
  level: ConsciousnessLevel,
  operationId: string,
  context?: any
): ValidationResult {
  return consciousnessBounds.validateConsciousnessLevel(level, operationId, context)
}

export function getSafeConsciousnessLevel(
  requested: ConsciousnessLevel,
  context?: any
): ConsciousnessLevel {
  return consciousnessBounds.getSafeLevel(requested, context)
}

export function getConsciousnessDisclaimer(level: ConsciousnessLevel): string {
  return consciousnessBounds.getScientificDisclaimer(level)
}

export function startConsciousnessOperation(
  operationId: string, 
  level: ConsciousnessLevel
): void {
  consciousnessBounds.startOperation(operationId, level)
}

export function completeConsciousnessOperation(operationId: string): void {
  consciousnessBounds.completeOperation(operationId)
}

