'use client'

import React, { useState, useEffect } from 'react'
import { AlertTriangle, Info, X, BookOpen, Zap } from 'lucide-react'
import { ConsciousnessLevel, getConsciousnessDisclaimer } from '@/lib/consciousness/bounds-validator'

interface ScientificDisclaimerProps {
  level?: ConsciousnessLevel
  feature?: string
  visible?: boolean
  onDismiss?: () => void
  persistent?: boolean
  className?: string
}

const DISCLAIMER_STORAGE_KEY = 'supercompute-disclaimers-seen'

export function ScientificDisclaimer({
  level = 'gamma',
  feature = 'consciousness computing',
  visible = true,
  onDismiss,
  persistent = false,
  className = ''
}: ScientificDisclaimerProps) {
  const [isVisible, setIsVisible] = useState(visible)
  const [hasBeenSeen, setHasBeenSeen] = useState(false)

  useEffect(() => {
    // Check if disclaimer has been seen before
    const seenDisclaimers = JSON.parse(
      localStorage.getItem(DISCLAIMER_STORAGE_KEY) || '{}'
    )

    const disclaimerKey = `${level}-${feature}`
    if (seenDisclaimers[disclaimerKey] && !persistent) {
      setHasBeenSeen(true)
      setIsVisible(false)
    }
  }, [level, feature, persistent])

  const handleDismiss = () => {
    setIsVisible(false)

    // Mark as seen in localStorage
    const seenDisclaimers = JSON.parse(
      localStorage.getItem(DISCLAIMER_STORAGE_KEY) || '{}'
    )
    const disclaimerKey = `${level}-${feature}`
    seenDisclaimers[disclaimerKey] = Date.now()
    localStorage.setItem(DISCLAIMER_STORAGE_KEY, JSON.stringify(seenDisclaimers))

    if (onDismiss) {
      onDismiss()
    }
  }

  const getIconForLevel = (level: ConsciousnessLevel) => {
    switch (level) {
      case 'omega':
      case 'delta':
        return <AlertTriangle className="w-5 h-5 text-red-500" />
      case 'gamma':
        return <Zap className="w-5 h-5 text-yellow-500" />
      case 'beta':
      case 'alpha':
      default:
        return <Info className="w-5 h-5 text-blue-500" />
    }
  }

  const getColorScheme = (level: ConsciousnessLevel) => {
    switch (level) {
      case 'omega':
        return 'border-red-600 bg-red-50 text-red-900'
      case 'delta':
        return 'border-orange-500 bg-orange-50 text-orange-900'
      case 'gamma':
        return 'border-yellow-500 bg-yellow-50 text-yellow-900'
      case 'beta':
        return 'border-blue-500 bg-blue-50 text-blue-900'
      case 'alpha':
      default:
        return 'border-gray-500 bg-gray-50 text-gray-900'
    }
  }

  const getExperimentalNotice = () => {
    if (level === 'omega' || level === 'delta') {
      return (
        <div className="mt-3 p-3 bg-red-100 border border-red-300 rounded-md">
          <h4 className="font-semibold text-red-800 flex items-center">
            <AlertTriangle className="w-4 h-4 mr-2" />
            Experimental Technology Notice
          </h4>
          <p className="text-sm text-red-700 mt-1">
            This feature uses experimental computational abstractions that are not validated for production use. 
            The &quot;consciousness&quot; terminology is metaphorical and refers to computational complexity levels, 
            not actual artificial consciousness.
          </p>
        </div>
      )
    }
    return null
  }

  const getEducationalContext = () => {
    return (
      <div className="mt-3 p-3 bg-blue-100 border border-blue-300 rounded-md">
        <h4 className="font-semibold text-blue-800 flex items-center">
          <BookOpen className="w-4 h-4 mr-2" />
          Educational Context
        </h4>
        <p className="text-sm text-blue-700 mt-1">
          Supercompute.me demonstrates advanced distributed computing patterns using Julia, WebAssembly, 
          and real-time orchestration. The consciousness-level abstractions are computational metaphors 
          designed to organize system complexity, not claims about artificial intelligence consciousness.
        </p>
      </div>
    )
  }

  if (!isVisible) return null

  return (
    <div className={`
      fixed top-4 right-4 max-w-md z-50 
      border-2 rounded-lg p-4 shadow-lg backdrop-blur-sm
      ${getColorScheme(level)}
      ${className}
    `}>
      <div className="flex items-start justify-between">
        <div className="flex items-start space-x-3">
          {getIconForLevel(level)}
          <div className="flex-1">
            <h3 className="font-semibold text-lg">
              Scientific Disclaimer
            </h3>
            <p className="text-sm mt-1">
              {getConsciousnessDisclaimer(level)}
            </p>

            {feature !== 'consciousness computing' && (
              <p className="text-sm mt-2">
                <strong>Feature:</strong> {feature}
              </p>
            )}
          </div>
        </div>

        {!persistent && (
          <button
            onClick={handleDismiss}
            className="ml-2 p-1 rounded hover:bg-black/10 transition-colors"
            aria-label="Dismiss disclaimer"
          >
            <X className="w-4 h-4" />
          </button>
        )}
      </div>

      {getExperimentalNotice()}
      {getEducationalContext()}

      {persistent && (
        <div className="mt-4 text-xs opacity-75">
          This disclaimer will appear each time you use this feature.
        </div>
      )}
    </div>
  )
}

// Hook for managing disclaimer state
export function useScientificDisclaimer(
  level: ConsciousnessLevel = 'gamma',
  feature: string = 'consciousness computing'
) {
  const [showDisclaimer, setShowDisclaimer] = useState(false)

  useEffect(() => {
    // Show disclaimer for experimental features
    if (level === 'omega' || level === 'delta') {
      setShowDisclaimer(true)
    }
  }, [level])

  const dismissDisclaimer = () => setShowDisclaimer(false)
  const showManualDisclaimer = () => setShowDisclaimer(true)

  return {
    showDisclaimer,
    dismissDisclaimer,
    showManualDisclaimer
  }
}

// Wrapper component for experimental features
export function ExperimentalFeatureWrapper({
  level = 'gamma',
  feature,
  children,
  className = ''
}: {
  level?: ConsciousnessLevel
  feature: string
  children: React.ReactNode
  className?: string
}) {
  const { showDisclaimer, dismissDisclaimer } = useScientificDisclaimer(level, feature)

  return (
    <div className={`relative ${className}`}>
      {children}

      <ScientificDisclaimer
        level={level}
        feature={feature}
        visible={showDisclaimer}
        onDismiss={dismissDisclaimer}
        persistent={level === 'omega'}
      />
    </div>
  )
}

