# Vimmacs Master Makefile
# Production-ready hybrid NeoVim/Doom Emacs IDE with AI integration
# Author: Generated with Claude Code
# Version: 1.0.0

.PHONY: help all build install clean test run setup deps check security dev prod
.DEFAULT_GOAL := help

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Configuration Variables
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

# Project paths
PROJECT_ROOT := $(shell pwd)
TARGET_DIR := $(PROJECT_ROOT)/target
RELEASE_DIR := $(TARGET_DIR)/release
DEBUG_DIR := $(TARGET_DIR)/debug

# Installation paths
INSTALL_PREFIX ?= /usr/local
BIN_DIR := $(INSTALL_PREFIX)/bin
CONFIG_DIR := $(HOME)/.config/vimmacs
DATA_DIR := $(HOME)/.local/share/vimmacs
CACHE_DIR := $(HOME)/.cache/vimmacs

# Build configuration
RUST_TARGET ?= 
CARGO_FLAGS ?= --workspace
BUILD_TYPE ?= release
FEATURES ?= 

# Runtime configuration
VIMMACS_BIN := $(RELEASE_DIR)/vimmacs
WRAPPER_SCRIPT := $(PROJECT_ROOT)/vimmacs

# Colors for output
GREEN := \033[0;32m
YELLOW := \033[1;33m
BLUE := \033[0;34m
RED := \033[0;31m
NC := \033[0m

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Help Target
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

help: ## Show this help message
	@echo ""
	@echo "$(BLUE)Vimmacs Build System$(NC)"
	@echo "$(YELLOW)â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•$(NC)"
	@echo ""
	@echo "$(GREEN)Primary Targets:$(NC)"
	@grep -E '^[a-zA-Z_-]+:.*?## .*$$' $(MAKEFILE_LIST) | grep -E '(build|install|run|setup)' | awk 'BEGIN {FS = ":.*?## "}; {printf "  $(BLUE)%-15s$(NC) %s\n", $$1, $$2}'
	@echo ""
	@echo "$(GREEN)Development Targets:$(NC)"
	@grep -E '^[a-zA-Z_-]+:.*?## .*$$' $(MAKEFILE_LIST) | grep -E '(dev|test|check|clean)' | awk 'BEGIN {FS = ":.*?## "}; {printf "  $(BLUE)%-15s$(NC) %s\n", $$1, $$2}'
	@echo ""
	@echo "$(GREEN)Specialized Targets:$(NC)"
	@grep -E '^[a-zA-Z_-]+:.*?## .*$$' $(MAKEFILE_LIST) | grep -vE '(build|install|run|setup|dev|test|check|clean)' | awk 'BEGIN {FS = ":.*?## "}; {printf "  $(BLUE)%-15s$(NC) %s\n", $$1, $$2}'
	@echo ""
	@echo "$(YELLOW)Environment Variables:$(NC)"
	@echo "  $(BLUE)BUILD_TYPE$(NC)      Build type: debug, release (default: release)"
	@echo "  $(BLUE)FEATURES$(NC)        Cargo features to enable"
	@echo "  $(BLUE)INSTALL_PREFIX$(NC)  Installation prefix (default: /usr/local)"
	@echo ""

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Primary Build Targets
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

all: build ## Build all components (default)

build: deps build-rust build-wrapper ## Build the complete vimmacs system
	@echo "$(GREEN)âœ“ Vimmacs build completed successfully$(NC)"
	@echo "$(YELLOW)  Binary location: $(VIMMACS_BIN)$(NC)"
	@echo "$(YELLOW)  Wrapper script:  $(WRAPPER_SCRIPT)$(NC)"

build-rust: ## Build Rust workspace components
	@echo "$(BLUE)Building Rust workspace...$(NC)"
	@cargo build --profile $(BUILD_TYPE) $(CARGO_FLAGS) $(if $(FEATURES),--features $(FEATURES))
	@echo "$(GREEN)âœ“ Rust build completed$(NC)"

build-wrapper: $(VIMMACS_BIN) ## Ensure wrapper script is executable
	@echo "$(BLUE)Setting up wrapper script...$(NC)"
	@chmod +x $(WRAPPER_SCRIPT)
	@echo "$(GREEN)âœ“ Wrapper script ready$(NC)"

build-debug: ## Build in debug mode
	$(MAKE) build BUILD_TYPE=debug

build-optimized: ## Build with maximum optimization
	@echo "$(BLUE)Building optimized release...$(NC)"
	@cargo build --profile release-lto $(CARGO_FLAGS) $(if $(FEATURES),--features $(FEATURES))

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Dependencies and Prerequisites
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

deps: check-deps install-deps ## Install and verify all dependencies

check-deps: ## Check system dependencies
	@echo "$(BLUE)Checking system dependencies...$(NC)"
	@command -v cargo >/dev/null 2>&1 || { echo "$(RED)Error: Rust/Cargo not found$(NC)"; exit 1; }
	@command -v git >/dev/null 2>&1 || { echo "$(RED)Error: Git not found$(NC)"; exit 1; }
	@echo "$(GREEN)âœ“ Core dependencies verified$(NC)"

install-deps: ## Install additional dependencies
	@echo "$(BLUE)Installing additional dependencies...$(NC)"
	@if command -v apt-get >/dev/null 2>&1; then \
		echo "$(YELLOW)Installing Ubuntu/Debian dependencies...$(NC)"; \
		sudo apt-get update; \
		sudo apt-get install -y build-essential pkg-config libssl-dev; \
	elif command -v dnf >/dev/null 2>&1; then \
		echo "$(YELLOW)Installing Fedora dependencies...$(NC)"; \
		sudo dnf install -y gcc pkg-config openssl-devel; \
	elif command -v brew >/dev/null 2>&1; then \
		echo "$(YELLOW)Installing macOS dependencies...$(NC)"; \
		brew install pkg-config openssl; \
	else \
		echo "$(YELLOW)Please install: build tools, pkg-config, openssl development libraries$(NC)"; \
	fi

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Installation Targets
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

install: build setup-dirs install-binary install-configs ## Install vimmacs system-wide
	@echo "$(GREEN)âœ“ Vimmacs installation completed$(NC)"
	@echo "$(YELLOW)  Installed to: $(BIN_DIR)/vimmacs$(NC)"
	@echo "$(YELLOW)  Config dir:   $(CONFIG_DIR)$(NC)"
	@echo "$(YELLOW)  Run 'vimmacs --help' to get started$(NC)"

install-binary: $(VIMMACS_BIN) ## Install vimmacs binary
	@echo "$(BLUE)Installing vimmacs binary...$(NC)"
	@install -d $(BIN_DIR)
	@install -m 755 $(VIMMACS_BIN) $(BIN_DIR)/vimmacs
	@echo "$(GREEN)âœ“ Binary installed to $(BIN_DIR)/vimmacs$(NC)"

install-configs: ## Install default configuration files
	@echo "$(BLUE)Setting up configuration directories...$(NC)"
	@install -d $(CONFIG_DIR)
	@install -d $(DATA_DIR)
	@install -d $(CACHE_DIR)
	@if [ -f config/default-config.toml ]; then \
		install -m 644 config/default-config.toml $(CONFIG_DIR)/config.toml; \
	fi
	@echo "$(GREEN)âœ“ Configuration directories created$(NC)"

setup-dirs: ## Create necessary directories
	@install -d $(BIN_DIR)
	@install -d $(CONFIG_DIR)
	@install -d $(DATA_DIR)
	@install -d $(CACHE_DIR)

uninstall: ## Remove installed vimmacs components
	@echo "$(BLUE)Uninstalling vimmacs...$(NC)"
	@rm -f $(BIN_DIR)/vimmacs
	@echo "$(YELLOW)Configuration preserved at: $(CONFIG_DIR)$(NC)"
	@echo "$(GREEN)âœ“ Vimmacs uninstalled$(NC)"

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Development and Testing
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

dev: build-debug ## Build for development
	@echo "$(GREEN)âœ“ Development build ready$(NC)"

run: build ## Build and run vimmacs
	@echo "$(BLUE)Starting vimmacs...$(NC)"
	@$(WRAPPER_SCRIPT) $(ARGS)

run-debug: build-debug ## Build and run in debug mode
	@echo "$(BLUE)Starting vimmacs (debug mode)...$(NC)"
	@$(DEBUG_DIR)/vimmacs $(ARGS)

test: ## Run all tests
	@echo "$(BLUE)Running tests...$(NC)"
	@cargo test $(CARGO_FLAGS)
	@echo "$(GREEN)âœ“ All tests passed$(NC)"

test-integration: build ## Run integration tests
	@echo "$(BLUE)Running integration tests...$(NC)"
	@if [ -f tests/integration_suite.py ]; then \
		cd tests && python3 integration_suite.py; \
	fi
	@echo "$(GREEN)âœ“ Integration tests completed$(NC)"

check: ## Run code quality checks
	@echo "$(BLUE)Running code quality checks...$(NC)"
	@cargo fmt --all -- --check
	@cargo clippy $(CARGO_FLAGS) -- -D warnings
	@echo "$(GREEN)âœ“ Code quality checks passed$(NC)"

security: ## Run security audits
	@echo "$(BLUE)Running security audit...$(NC)"
	@cargo audit
	@if [ -f scripts/security-audit.sh ]; then \
		./scripts/security-audit.sh; \
	fi
	@echo "$(GREEN)âœ“ Security audit completed$(NC)"

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Specialized Targets
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

setup: install ## Complete system setup (alias for install)

init: setup ## Initialize vimmacs for first use
	@echo "$(BLUE)Initializing vimmacs...$(NC)"
	@$(BIN_DIR)/vimmacs init
	@echo "$(GREEN)âœ“ Vimmacs initialized$(NC)"

clean: ## Clean build artifacts
	@echo "$(BLUE)Cleaning build artifacts...$(NC)"
	@cargo clean
	@echo "$(GREEN)âœ“ Clean completed$(NC)"

clean-all: clean ## Clean everything including caches
	@echo "$(BLUE)Cleaning all artifacts and caches...$(NC)"
	@rm -rf $(CACHE_DIR)
	@cargo clean
	@echo "$(GREEN)âœ“ Deep clean completed$(NC)"

docs: ## Generate documentation
	@echo "$(BLUE)Generating documentation...$(NC)"
	@cargo doc --workspace --no-deps
	@echo "$(GREEN)âœ“ Documentation generated$(NC)"
	@echo "$(YELLOW)  Open: target/doc/vimmacs/index.html$(NC)"

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Container and Deployment Targets
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

docker: ## Build Docker image
	@echo "$(BLUE)Building Docker image...$(NC)"
	@docker build -t vimmacs:latest .
	@echo "$(GREEN)âœ“ Docker image built: vimmacs:latest$(NC)"

podman: ## Build Podman image
	@echo "$(BLUE)Building Podman image...$(NC)"
	@podman build -t vimmacs:latest .
	@echo "$(GREEN)âœ“ Podman image built: vimmacs:latest$(NC)"

deploy-dev: ## Deploy to development environment
	@echo "$(BLUE)Deploying to development...$(NC)"
	@if [ -f scripts/deploy-dev.sh ]; then \
		./scripts/deploy-dev.sh; \
	else \
		echo "$(YELLOW)No deployment script found$(NC)"; \
	fi

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
