update: ## Update dependencies
	@echo "$(BLUE)Updating dependencies...$(NC)"
	@cargo update
	@echo "$(GREEN)âœ“ Dependencies updated$(NC)"

format: ## Format code
	@echo "$(BLUE)Formatting code...$(NC)"
	@cargo fmt --all
	@echo "$(GREEN)âœ“ Code formatted$(NC)"

lint: ## Run linting
	@echo "$(BLUE)Running linter...$(NC)"
	@cargo clippy $(CARGO_FLAGS) -- -D warnings
	@echo "$(GREEN)âœ“ Linting completed$(NC)"

bench: ## Run benchmarks
	@echo "$(BLUE)Running benchmarks...$(NC)"
	@cargo bench $(CARGO_FLAGS)
	@echo "$(GREEN)âœ“ Benchmarks completed$(NC)"

info: ## Show build information
	@echo "$(BLUE)Vimmacs Build Information$(NC)"
	@echo "$(YELLOW)â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•$(NC)"
	@echo "Project Root: $(PROJECT_ROOT)"
	@echo "Target Dir:   $(TARGET_DIR)"
	@echo "Build Type:   $(BUILD_TYPE)"
	@echo "Install Dir:  $(BIN_DIR)"
	@echo "Config Dir:   $(CONFIG_DIR)"
	@echo ""
	@echo "$(BLUE)System Information:$(NC)"
	@echo "Rust Version: $$(cargo --version)"
	@echo "Git Version:  $$(git --version)"
	@if [ -f $(VIMMACS_BIN) ]; then \
		echo "Binary Size:  $$(ls -lh $(VIMMACS_BIN) | awk '{print $$5}')"; \
		echo "Binary Date:  $$(ls -l $(VIMMACS_BIN) | awk '{print $$6, $$7, $$8}')"; \
