$(VIMMACS_BIN): Cargo.toml $(shell find src -name "*.rs" 2>/dev/null)
	@echo "$(BLUE)Building vimmacs binary...$(NC)"
	@cargo build --profile $(BUILD_TYPE) $(CARGO_FLAGS) $(if $(FEATURES),--features $(FEATURES))

