# Vimmacs Master Makefile
# Production-ready hybrid NeoVim/Doom Emacs IDE with AI integration
# Author: Generated with Claude Code
# Version: 1.0.0

.PHONY: help all build install clean test run setup deps check security dev prod
.DEFAULT_GOAL := help

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Configuration Variables
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

# Project paths
PROJECT_ROOT := $(shell pwd)
TARGET_DIR := $(PROJECT_ROOT)/target
RELEASE_DIR := $(TARGET_DIR)/release
DEBUG_DIR := $(TARGET_DIR)/debug

# Installation paths
INSTALL_PREFIX ?= /usr/local
BIN_DIR := $(INSTALL_PREFIX)/bin
CONFIG_DIR := $(HOME)/.config/vimmacs
DATA_DIR := $(HOME)/.local/share/vimmacs
CACHE_DIR := $(HOME)/.cache/vimmacs

# Build configuration
RUST_TARGET ?= 
CARGO_FLAGS ?= --workspace
BUILD_TYPE ?= release
FEATURES ?= 

# Runtime configuration
VIMMACS_BIN := $(RELEASE_DIR)/vimmacs
WRAPPER_SCRIPT := $(PROJECT_ROOT)/vimmacs

# Colors for output
GREEN := \033[0;32m
YELLOW := \033[1;33m
BLUE := \033[0;34m
RED := \033[0;31m
NC := \033[0m

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
