help: ## Show this help message
	@echo ""
	@echo "$(BLUE)Vimmacs Build System$(NC)"
	@echo "$(YELLOW)â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•$(NC)"
	@echo ""
	@echo "$(GREEN)Primary Targets:$(NC)"
	@grep -E '^[a-zA-Z_-]+:.*?## .*$$' $(MAKEFILE_LIST) | grep -E '(build|install|run|setup)' | awk 'BEGIN {FS = ":.*?## "}; {printf "  $(BLUE)%-15s$(NC) %s\n", $$1, $$2}'
	@echo ""
	@echo "$(GREEN)Development Targets:$(NC)"
	@grep -E '^[a-zA-Z_-]+:.*?## .*$$' $(MAKEFILE_LIST) | grep -E '(dev|test|check|clean)' | awk 'BEGIN {FS = ":.*?## "}; {printf "  $(BLUE)%-15s$(NC) %s\n", $$1, $$2}'
	@echo ""
	@echo "$(GREEN)Specialized Targets:$(NC)"
	@grep -E '^[a-zA-Z_-]+:.*?## .*$$' $(MAKEFILE_LIST) | grep -vE '(build|install|run|setup|dev|test|check|clean)' | awk 'BEGIN {FS = ":.*?## "}; {printf "  $(BLUE)%-15s$(NC) %s\n", $$1, $$2}'
	@echo ""
	@echo "$(YELLOW)Environment Variables:$(NC)"
	@echo "  $(BLUE)BUILD_TYPE$(NC)      Build type: debug, release (default: release)"
	@echo "  $(BLUE)FEATURES$(NC)        Cargo features to enable"
	@echo "  $(BLUE)INSTALL_PREFIX$(NC)  Installation prefix (default: /usr/local)"
	@echo ""

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Primary Build Targets
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

all: build ## Build all components (default)

build: deps build-rust build-wrapper ## Build the complete vimmacs system
	@echo "$(GREEN)âœ“ Vimmacs build completed successfully$(NC)"
	@echo "$(YELLOW)  Binary location: $(VIMMACS_BIN)$(NC)"
	@echo "$(YELLOW)  Wrapper script:  $(WRAPPER_SCRIPT)$(NC)"

build-rust: ## Build Rust workspace components
	@echo "$(BLUE)Building Rust workspace...$(NC)"
	@cargo build --profile $(BUILD_TYPE) $(CARGO_FLAGS) $(if $(FEATURES),--features $(FEATURES))
	@echo "$(GREEN)âœ“ Rust build completed$(NC)"

build-wrapper: $(VIMMACS_BIN) ## Ensure wrapper script is executable
	@echo "$(BLUE)Setting up wrapper script...$(NC)"
	@chmod +x $(WRAPPER_SCRIPT)
	@echo "$(GREEN)âœ“ Wrapper script ready$(NC)"

build-debug: ## Build in debug mode
	$(MAKE) build BUILD_TYPE=debug

build-optimized: ## Build with maximum optimization
	@echo "$(BLUE)Building optimized release...$(NC)"
	@cargo build --profile release-lto $(CARGO_FLAGS) $(if $(FEATURES),--features $(FEATURES))

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Dependencies and Prerequisites
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

deps: check-deps install-deps ## Install and verify all dependencies

check-deps: ## Check system dependencies
	@echo "$(BLUE)Checking system dependencies...$(NC)"
	@command -v cargo >/dev/null 2>&1 || { echo "$(RED)Error: Rust/Cargo not found$(NC)"; exit 1; }
	@command -v git >/dev/null 2>&1 || { echo "$(RED)Error: Git not found$(NC)"; exit 1; }
	@echo "$(GREEN)âœ“ Core dependencies verified$(NC)"

install-deps: ## Install additional dependencies
	@echo "$(BLUE)Installing additional dependencies...$(NC)"
	@if command -v apt-get >/dev/null 2>&1; then \
		echo "$(YELLOW)Installing Ubuntu/Debian dependencies...$(NC)"; \
		sudo apt-get update; \
		sudo apt-get install -y build-essential pkg-config libssl-dev; \
	elif command -v dnf >/dev/null 2>&1; then \
		echo "$(YELLOW)Installing Fedora dependencies...$(NC)"; \
		sudo dnf install -y gcc pkg-config openssl-devel; \
	elif command -v brew >/dev/null 2>&1; then \
		echo "$(YELLOW)Installing macOS dependencies...$(NC)"; \
		brew install pkg-config openssl; \
	else \
		echo "$(YELLOW)Please install: build tools, pkg-config, openssl development libraries$(NC)"; \
	fi

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Installation Targets
