install: build setup-dirs install-binary install-configs ## Install vimmacs system-wide
	@echo "$(GREEN)âœ“ Vimmacs installation completed$(NC)"
	@echo "$(YELLOW)  Installed to: $(BIN_DIR)/vimmacs$(NC)"
	@echo "$(YELLOW)  Config dir:   $(CONFIG_DIR)$(NC)"
	@echo "$(YELLOW)  Run 'vimmacs --help' to get started$(NC)"

install-binary: $(VIMMACS_BIN) ## Install vimmacs binary
	@echo "$(BLUE)Installing vimmacs binary...$(NC)"
	@install -d $(BIN_DIR)
	@install -m 755 $(VIMMACS_BIN) $(BIN_DIR)/vimmacs
	@echo "$(GREEN)âœ“ Binary installed to $(BIN_DIR)/vimmacs$(NC)"

install-configs: ## Install default configuration files
	@echo "$(BLUE)Setting up configuration directories...$(NC)"
	@install -d $(CONFIG_DIR)
	@install -d $(DATA_DIR)
	@install -d $(CACHE_DIR)
	@if [ -f config/default-config.toml ]; then \
		install -m 644 config/default-config.toml $(CONFIG_DIR)/config.toml; \
	fi
	@echo "$(GREEN)âœ“ Configuration directories created$(NC)"

setup-dirs: ## Create necessary directories
	@install -d $(BIN_DIR)
	@install -d $(CONFIG_DIR)
	@install -d $(DATA_DIR)
	@install -d $(CACHE_DIR)

uninstall: ## Remove installed vimmacs components
	@echo "$(BLUE)Uninstalling vimmacs...$(NC)"
	@rm -f $(BIN_DIR)/vimmacs
	@echo "$(YELLOW)Configuration preserved at: $(CONFIG_DIR)$(NC)"
	@echo "$(GREEN)âœ“ Vimmacs uninstalled$(NC)"

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Development and Testing
# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

dev: build-debug ## Build for development
	@echo "$(GREEN)âœ“ Development build ready$(NC)"

run: build ## Build and run vimmacs
	@echo "$(BLUE)Starting vimmacs...$(NC)"
	@$(WRAPPER_SCRIPT) $(ARGS)

run-debug: build-debug ## Build and run in debug mode
	@echo "$(BLUE)Starting vimmacs (debug mode)...$(NC)"
	@$(DEBUG_DIR)/vimmacs $(ARGS)

test: ## Run all tests
	@echo "$(BLUE)Running tests...$(NC)"
	@cargo test $(CARGO_FLAGS)
	@echo "$(GREEN)âœ“ All tests passed$(NC)"

test-integration: build ## Run integration tests
	@echo "$(BLUE)Running integration tests...$(NC)"
	@if [ -f tests/integration_suite.py ]; then \
		cd tests && python3 integration_suite.py; \
	fi
	@echo "$(GREEN)âœ“ Integration tests completed$(NC)"

check: ## Run code quality checks
	@echo "$(BLUE)Running code quality checks...$(NC)"
	@cargo fmt --all -- --check
	@cargo clippy $(CARGO_FLAGS) -- -D warnings
	@echo "$(GREEN)âœ“ Code quality checks passed$(NC)"

security: ## Run security audits
	@echo "$(BLUE)Running security audit...$(NC)"
	@cargo audit
	@if [ -f scripts/security-audit.sh ]; then \
		./scripts/security-audit.sh; \
	fi
	@echo "$(GREEN)âœ“ Security audit completed$(NC)"

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
# Specialized Targets
