setup: install ## Complete system setup (alias for install)

init: setup ## Initialize vimmacs for first use
	@echo "$(BLUE)Initializing vimmacs...$(NC)"
	@$(BIN_DIR)/vimmacs init
	@echo "$(GREEN)âœ“ Vimmacs initialized$(NC)"

clean: ## Clean build artifacts
	@echo "$(BLUE)Cleaning build artifacts...$(NC)"
	@cargo clean
	@echo "$(GREEN)âœ“ Clean completed$(NC)"

clean-all: clean ## Clean everything including caches
	@echo "$(BLUE)Cleaning all artifacts and caches...$(NC)"
	@rm -rf $(CACHE_DIR)
	@cargo clean
	@echo "$(GREEN)âœ“ Deep clean completed$(NC)"

docs: ## Generate documentation
	@echo "$(BLUE)Generating documentation...$(NC)"
	@cargo doc --workspace --no-deps
	@echo "$(GREEN)âœ“ Documentation generated$(NC)"
