docker: ## Build Docker image
	@echo "$(BLUE)Building Docker image...$(NC)"
	@docker build -t vimmacs:latest .
	@echo "$(GREEN)âœ“ Docker image built: vimmacs:latest$(NC)"

podman: ## Build Podman image
	@echo "$(BLUE)Building Podman image...$(NC)"
	@podman build -t vimmacs:latest .
	@echo "$(GREEN)âœ“ Podman image built: vimmacs:latest$(NC)"

deploy-dev: ## Deploy to development environment
	@echo "$(BLUE)Deploying to development...$(NC)"
	@if [ -f scripts/deploy-dev.sh ]; then \
		./scripts/deploy-dev.sh; \
	else \
		echo "$(YELLOW)No deployment script found$(NC)"; \
	fi

# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
