input.content:
{
  "mcpServers": {
    "unified-mcp-local": {
      "name": "Unified MCP Server (Local)",
      "transport": "websocket",
      "url": "ws://localhost:8080/mcp/ws",
      "headers": {
        "X-API-Key": "YOUR_API_KEY_HERE"
      },
      "capabilities": {
        "tools": true,
        "resources": true,
        "prompts": true
      },
      "providers": [
        "github",
        "perplexity",
        "bytedriver",
        "deepwiki",
        "devin",
        "shuffle",
        "hashicorp",
        "phoenix"
      ],
      "settings": {
        "reconnect": true,
        "reconnectInterval": 5000,
        "timeout": 30000,
        "maxRetries": 3
      }
    },
    "unified-mcp-production": {
      "name": "Unified MCP Server (Production)",
      "transport": "websocket",
      "url": "wss://your-mcp-server.fly.dev/mcp/ws",
      "headers": {
        "X-API-Key": "YOUR_PRODUCTION_API_KEY"
      },
      "capabilities": {
        "tools": true,
        "resources": true,
        "prompts": true
      },
      "settings": {
        "reconnect": true,
        "reconnectInterval": 10000,
        "timeout": 60000,
        "maxRetries": 5
      }
    }
  },
  "defaultServer": "unified-mcp-local",
  "logging": {
    "level": "info",
    "file": "/var/log/mcp-client.log"
  }
}

