[features]
default = ["web"]
web = ["wasm-bindgen", "web-sys", "js-sys"]
wasi = ["wasmtime", "wasmtime-wasi"]
rustler-bridge = ["rustler"]

[dependencies.rustler]
workspace = true
optional = true

