//! Katalyst Rust Core Runtime
//! 
//! This module provides the core functionality for the Katalyst framework,
//! designed to be compiled to WebAssembly for multi-runtime deployment.

use std::collections::HashMap;
use std::sync::Arc;
use wasm_bindgen::prelude::*;
use serde::{Deserialize, Serialize};
use dashmap::DashMap;
use uuid::Uuid;

// Import utilities for different target environments
#[cfg(target_arch = "wasm32")]
mod wasm_utils;

#[cfg(not(target_arch = "wasm32"))]
mod native_utils;

/// Core context for Katalyst operations
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct KatalystContext {
    pub id: String,
    pub session_id: String,
    pub workspace: String,
    pub metadata: HashMap<String, serde_json::Value>,
    pub created_at: i64,
}

/// Task processing result
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct TaskResult {
    pub task_id: String,
    pub status: TaskStatus,
    pub result: serde_json::Value,
    pub duration_ms: u64,
    pub error: Option<String>,
}

/// Task execution status
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum TaskStatus {
    Pending,
    Running,
    Completed,
    Failed,
    Cancelled,
}

/// Core Katalyst runtime engine
pub struct KatalystCore {
    contexts: Arc<DashMap<String, KatalystContext>>,
    tasks: Arc<DashMap<String, TaskResult>>,
}

impl KatalystCore {
    pub fn new() -> Self {
        Self {
            contexts: Arc::new(DashMap::new()),
            tasks: Arc::new(DashMap::new()),
        }
    }

    pub fn create_context(&self, workspace: String, metadata: HashMap<String, serde_json::Value>) -> anyhow::Result<KatalystContext> {
        let context = KatalystContext {
            id: Uuid::new_v4().to_string(),
            session_id: Uuid::new_v4().to_string(),
            workspace,
            metadata,
            created_at: chrono::Utc::now().timestamp_millis(),
        };

        self.contexts.insert(context.id.clone(), context.clone());
        Ok(context)
    }

    pub fn get_context(&self, context_id: &str) -> Option<KatalystContext> {
        self.contexts.get(context_id).map(|entry| entry.value().clone())
    }

    pub async fn process_task(&self, context_id: &str, task_data: serde_json::Value) -> anyhow::Result<TaskResult> {
        let start_time = std::time::Instant::now();
        let task_id = Uuid::new_v4().to_string();

        // Get context
        let context = self.get_context(context_id)
            .ok_or_else(|| anyhow::anyhow!("Context not found: {}", context_id))?;

        // Process the task (simplified implementation)
        let result = self.execute_task_logic(&context, &task_data).await?;

        let task_result = TaskResult {
            task_id: task_id.clone(),
            status: TaskStatus::Completed,
            result,
            duration_ms: start_time.elapsed().as_millis() as u64,
            error: None,
        };

        self.tasks.insert(task_id, task_result.clone());
        Ok(task_result)
    }

    async fn execute_task_logic(&self, context: &KatalystContext, task_data: &serde_json::Value) -> anyhow::Result<serde_json::Value> {
        // This is where the actual task processing logic would go
        // For now, we'll return a simple response
        Ok(serde_json::json!({
            "message": "Task processed successfully",
            "context_id": context.id,
            "workspace": context.workspace,
            "task_data": task_data
        }))
    }

    pub fn get_task_result(&self, task_id: &str) -> Option<TaskResult> {
        self.tasks.get(task_id).map(|entry| entry.value().clone())
    }

    pub fn list_contexts(&self) -> Vec<KatalystContext> {
        self.contexts.iter().map(|entry| entry.value().clone()).collect()
    }
}

// WebAssembly bindings
#[wasm_bindgen]
pub struct WasmKatalystCore {
    inner: KatalystCore,
}

#[wasm_bindgen]
impl WasmKatalystCore {
    #[wasm_bindgen(constructor)]
    pub fn new() -> WasmKatalystCore {
        console_error_panic_hook::set_once();

        Self {
            inner: KatalystCore::new(),
        }
    }

    #[wasm_bindgen(js_name = createContext)]
    pub fn create_context(&self, workspace: &str, metadata_json: &str) -> Result<String, JsValue> {
        let metadata: HashMap<String, serde_json::Value> = serde_json::from_str(metadata_json)
            .map_err(|e| JsValue::from_str(&format!("Invalid metadata JSON: {}", e)))?;

        let context = self.inner.create_context(workspace.to_string(), metadata)
            .map_err(|e| JsValue::from_str(&e.to_string()))?;

        serde_json::to_string(&context)
            .map_err(|e| JsValue::from_str(&e.to_string()))
    }

    #[wasm_bindgen(js_name = getContext)]
    pub fn get_context(&self, context_id: &str) -> Option<String> {
        self.inner.get_context(context_id)
            .and_then(|ctx| serde_json::to_string(&ctx).ok())
    }

    #[wasm_bindgen(js_name = processTask)]
    pub async fn process_task(&self, context_id: &str, task_data_json: &str) -> Result<String, JsValue> {
        let task_data: serde_json::Value = serde_json::from_str(task_data_json)
            .map_err(|e| JsValue::from_str(&format!("Invalid task data JSON: {}", e)))?;

        let result = self.inner.process_task(context_id, task_data).await
            .map_err(|e| JsValue::from_str(&e.to_string()))?;

        serde_json::to_string(&result)
            .map_err(|e| JsValue::from_str(&e.to_string()))
    }

    #[wasm_bindgen(js_name = getTaskResult)]
    pub fn get_task_result(&self, task_id: &str) -> Option<String> {
        self.inner.get_task_result(task_id)
            .and_then(|result| serde_json::to_string(&result).ok())
    }

    #[wasm_bindgen(js_name = listContexts)]
    pub fn list_contexts(&self) -> String {
        let contexts = self.inner.list_contexts();
        serde_json::to_string(&contexts).unwrap_or_else(|_| "[]".to_string())
    }
}

// Native Rust interface for NIFs and other integrations
impl Default for KatalystCore {
    fn default() -> Self {
        Self::new()
    }
}

#[cfg(feature = "rustler-bridge")]
mod rustler_bridge {
    use super::*;
    use rustler::prelude::*;

    #[rustler::nif]
    fn create_context(workspace: String, metadata_json: String) -> Result<String, String> {
        let core = KatalystCore::new();
        let metadata: HashMap<String, serde_json::Value> = serde_json::from_str(&metadata_json)
            .map_err(|e| format!("Invalid metadata JSON: {}", e))?;

        let context = core.create_context(workspace, metadata)
            .map_err(|e| e.to_string())?;

        serde_json::to_string(&context)
            .map_err(|e| e.to_string())
    }

    rustler::init!("Elixir.KatalystNif", [create_context]);
}

// Export commonly used types
pub use {KatalystContext, TaskResult, TaskStatus, KatalystCore};

