//! WASM-specific utilities for the Katalyst runtime

use wasm_bindgen::prelude::*;
use web_sys::console;

/// Log to browser console in WASM environment
pub fn log(message: &str) {
    console::log_1(&message.into());
}

/// Log error to browser console in WASM environment  
pub fn error(message: &str) {
    console::error_1(&message.into());
}

/// Get current timestamp in WASM environment
pub fn now() -> f64 {
    js_sys::Date::now()
}

/// Performance measurement utilities
pub struct WasmPerformance {
    start_time: f64,
}

impl WasmPerformance {
    pub fn new() -> Self {
        Self {
            start_time: now(),
        }
    }

    pub fn elapsed_ms(&self) -> f64 {
        now() - self.start_time
    }
}

impl Default for WasmPerformance {
    fn default() -> Self {
        Self::new()
    }
}

#[wasm_bindgen]
extern "C" {
    #[wasm_bindgen(js_namespace = performance)]
    fn now() -> f64;
}

/// Memory utilities for WASM
pub fn memory_usage() -> usize {
    // In WASM, we can only estimate memory usage
    // This would need to be implemented with more sophisticated tracking
    0
}

/// Check if running in a web worker context
pub fn is_web_worker() -> bool {
    js_sys::global()
        .dyn_into::<web_sys::DedicatedWorkerGlobalScope>()
        .is_ok()
}

/// Post message to main thread (if in web worker)
pub fn post_message(message: &serde_json::Value) -> Result<(), JsValue> {
    if is_web_worker() {
        let global = js_sys::global();
        let worker_scope: web_sys::DedicatedWorkerGlobalScope = global.dyn_into()?;
        let js_message = JsValue::from_serde(message).map_err(|e| JsValue::from_str(&e.to_string()))?;
        worker_scope.post_message(&js_message)?;
    }
    Ok(())
}

