//! Native utilities for the Katalyst runtime (non-WASM environments)

use std::time::{SystemTime, UNIX_EPOCH, Instant};

/// Log message in native environment
pub fn log(message: &str) {
    println!("[KATALYST] {}", message);
}

/// Log error in native environment
pub fn error(message: &str) {
    eprintln!("[KATALYST ERROR] {}", message);
}

/// Get current timestamp in native environment
pub fn now() -> f64 {
    SystemTime::now()
        .duration_since(UNIX_EPOCH)
        .unwrap_or_default()
        .as_millis() as f64
}

/// Performance measurement utilities for native environment
pub struct NativePerformance {
    start_time: Instant,
}

impl NativePerformance {
    pub fn new() -> Self {
        Self {
            start_time: Instant::now(),
        }
    }

    pub fn elapsed_ms(&self) -> f64 {
        self.start_time.elapsed().as_millis() as f64
    }
}

impl Default for NativePerformance {
    fn default() -> Self {
        Self::new()
    }
}

/// Get memory usage in native environment
pub fn memory_usage() -> usize {
    // This is a placeholder - real implementation would use system APIs
    // or memory profiling tools
    0
}

/// Check if running in a threaded context
pub fn is_threaded() -> bool {
    std::thread::current().name().is_some()
}

/// Send message through channel (native equivalent of post_message)
pub fn send_message<T>(sender: &tokio::sync::mpsc::UnboundedSender<T>, message: T) -> Result<(), String> {
    sender.send(message)
        .map_err(|e| format!("Failed to send message: {}", e))
}

