  task_id: string;
  status: 'Pending' | 'Running' | 'Completed' | 'Failed' | 'Cancelled';
  result: any;
  duration_ms: number;
  error?: string;
}

export class WasmKatalystCore {
  constructor();
  createContext(workspace: string, metadata_json: string): string;
  getContext(context_id: string): string | undefined;
  processTask(context_id: string, task_data_json: string): Promise<string>;
  getTaskResult(task_id: string): string | undefined;
  listContexts(): string;
}

export function get_version(): string;
export function get_build_info(): string;
export function force_gc(): void;
export function get_memory_usage(): number;

// Runtime-specific exports
export namespace RustCore {
  export function init(): Promise<void>;
  export const core: WasmKatalystCore;
}
EOF

# Generate package.json for npm distribution
echo -e "${YELLOW}📦 Generating package.json for npm distribution...${NC}"
cat > wasm-modules/dist/package.json << EOF
