      "types": "./katalyst-wasm.d.ts"
    },
    "./web": "./rust-core-web/katalyst_rust_core_wasm.js",
    "./node": "./rust-core-nodejs/katalyst_rust_core_wasm.js",
    "./bundler": "./rust-core-bundler/katalyst_rust_core_wasm.js"
  },
  "keywords": [
    "webassembly",
    "wasm", 
    "rust",
    "katalyst",
    "runtime",
    "multi-runtime",
    "vercel",
    "edge-functions"
  ],
  "author": "Katalyst Team <team@katalyst.dev>",
