//! Katalyst Elixir Runtime Bridge
//! 
//! This module provides a bridge between Elixir processes and WebAssembly,
//! enabling Phoenix LiveView and GenServer functionality in WASM environments.

use std::collections::HashMap;
use std::sync::Arc;
use serde::{Deserialize, Serialize};
use tokio::sync::RwLock;
use uuid::Uuid;

/// Message types for Elixir-WASM communication
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ElixirMessage {
    GenServerCall {
        module: String,
        function: String,
        args: serde_json::Value,
        from: String,
    },
    GenServerCast {
        module: String,
        function: String,
        args: serde_json::Value,
    },
    LiveViewEvent {
        view: String,
        event: String,
        params: HashMap<String, serde_json::Value>,
        socket_id: String,
    },
    LiveViewMount {
        view: String,
        params: HashMap<String, serde_json::Value>,
        socket_id: String,
    },
    PhoenixChannelJoin {
        topic: String,
        payload: serde_json::Value,
        socket_id: String,
    },
    PhoenixChannelLeave {
        topic: String,
        socket_id: String,
    },
}

/// Response from Elixir runtime
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ElixirResponse {
    GenServerReply {
        request_id: String,
        result: serde_json::Value,
        error: Option<String>,
    },
    LiveViewUpdate {
        socket_id: String,
        html: String,
        assigns: HashMap<String, serde_json::Value>,
    },
    ChannelBroadcast {
        topic: String,
        event: String,
        payload: serde_json::Value,
    },
    Error {
        message: String,
        details: Option<serde_json::Value>,
    },
}

/// Elixir runtime state manager
pub struct ElixirRuntimeState {
    pub processes: Arc<RwLock<HashMap<String, ProcessState>>>,
    pub sockets: Arc<RwLock<HashMap<String, SocketState>>>,
    pub channels: Arc<RwLock<HashMap<String, ChannelState>>>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ProcessState {
    pub id: String,
    pub module: String,
    pub state: serde_json::Value,
    pub created_at: i64,
    pub last_activity: i64,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SocketState {
    pub id: String,
    pub transport: String,
    pub assigns: HashMap<String, serde_json::Value>,
    pub connected_at: i64,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ChannelState {
    pub topic: String,
    pub socket_id: String,
    pub joined_at: i64,
    pub state: serde_json::Value,
}

/// Main Elixir runtime interface
pub struct ElixirRuntime {
    state: ElixirRuntimeState,
}

impl ElixirRuntime {
    pub fn new() -> Self {
        Self {
            state: ElixirRuntimeState {
                processes: Arc::new(RwLock::new(HashMap::new())),
                sockets: Arc::new(RwLock::new(HashMap::new())),
                channels: Arc::new(RwLock::new(HashMap::new())),
            },
        }
    }

    pub async fn handle_message(&self, message: ElixirMessage) -> anyhow::Result<ElixirResponse> {
        match message {
            ElixirMessage::GenServerCall { module, function, args, from } => {
                self.handle_genserver_call(module, function, args, from).await
            }
            ElixirMessage::GenServerCast { module, function, args } => {
                self.handle_genserver_cast(module, function, args).await
            }
            ElixirMessage::LiveViewEvent { view, event, params, socket_id } => {
                self.handle_liveview_event(view, event, params, socket_id).await
            }
            ElixirMessage::LiveViewMount { view, params, socket_id } => {
                self.handle_liveview_mount(view, params, socket_id).await
            }
            ElixirMessage::PhoenixChannelJoin { topic, payload, socket_id } => {
                self.handle_channel_join(topic, payload, socket_id).await
            }
            ElixirMessage::PhoenixChannelLeave { topic, socket_id } => {
                self.handle_channel_leave(topic, socket_id).await
            }
        }
    }

    async fn handle_genserver_call(&self, module: String, function: String, args: serde_json::Value, from: String) -> anyhow::Result<ElixirResponse> {
        // Simulate GenServer call processing
        let result = serde_json::json!({
            "module": module,
            "function": function,
            "args": args,
            "processed_at": chrono::Utc::now().timestamp_millis()
        });

        Ok(ElixirResponse::GenServerReply {
            request_id: from,
            result,
            error: None,
        })
    }

    async fn handle_genserver_cast(&self, _module: String, _function: String, _args: serde_json::Value) -> anyhow::Result<ElixirResponse> {
        // GenServer cast doesn't return a response
        Ok(ElixirResponse::GenServerReply {
            request_id: "cast".to_string(),
            result: serde_json::json!({"status": "ok"}),
            error: None,
        })
    }

    async fn handle_liveview_event(&self, view: String, event: String, params: HashMap<String, serde_json::Value>, socket_id: String) -> anyhow::Result<ElixirResponse> {
        // Simulate LiveView event handling
        let updated_html = format!("<div>LiveView {} handled event {} with params {:?}</div>", view, event, params);

        let mut assigns = HashMap::new();
        assigns.insert("last_event".to_string(), serde_json::Value::String(event));
        assigns.insert("event_time".to_string(), serde_json::Value::Number(chrono::Utc::now().timestamp_millis().into()));

        Ok(ElixirResponse::LiveViewUpdate {
            socket_id,
            html: updated_html,
            assigns,
        })
    }

    async fn handle_liveview_mount(&self, view: String, params: HashMap<String, serde_json::Value>, socket_id: String) -> anyhow::Result<ElixirResponse> {
        // Create socket state
        let socket = SocketState {
            id: socket_id.clone(),
            transport: "websocket".to_string(),
            assigns: params.clone(),
            connected_at: chrono::Utc::now().timestamp_millis(),
        };

        self.state.sockets.write().await.insert(socket_id.clone(), socket);

        let html = format!("<div>LiveView {} mounted with params {:?}</div>", view, params);

        Ok(ElixirResponse::LiveViewUpdate {
            socket_id,
            html,
            assigns: params,
        })
    }

    async fn handle_channel_join(&self, topic: String, payload: serde_json::Value, socket_id: String) -> anyhow::Result<ElixirResponse> {
        let channel = ChannelState {
            topic: topic.clone(),
            socket_id: socket_id.clone(),
            joined_at: chrono::Utc::now().timestamp_millis(),
            state: payload.clone(),
        };

        self.state.channels.write().await.insert(topic.clone(), channel);

        Ok(ElixirResponse::ChannelBroadcast {
            topic,
            event: "phx_reply".to_string(),
            payload: serde_json::json!({
                "status": "ok",
                "response": payload
            }),
        })
    }

    async fn handle_channel_leave(&self, topic: String, _socket_id: String) -> anyhow::Result<ElixirResponse> {
        self.state.channels.write().await.remove(&topic);

        Ok(ElixirResponse::ChannelBroadcast {
            topic,
            event: "phx_close".to_string(),
            payload: serde_json::json!({"status": "ok"}),
        })
    }

    pub async fn get_socket_state(&self, socket_id: &str) -> Option<SocketState> {
        self.state.sockets.read().await.get(socket_id).cloned()
    }

    pub async fn get_process_state(&self, process_id: &str) -> Option<ProcessState> {
        self.state.processes.read().await.get(process_id).cloned()
    }
}

impl Default for ElixirRuntime {
    fn default() -> Self {
        Self::new()
    }
}

// WebAssembly bindings
#[cfg(feature = "wasm-web")]
mod wasm_bindings {
    use super::*;
    use wasm_bindgen::prelude::*;

    #[wasm_bindgen]
    pub struct WasmElixirRuntime {
        inner: ElixirRuntime,
    }

    #[wasm_bindgen]
    impl WasmElixirRuntime {
        #[wasm_bindgen(constructor)]
        pub fn new() -> WasmElixirRuntime {
            Self {
                inner: ElixirRuntime::new(),
            }
        }

        #[wasm_bindgen(js_name = handleMessage)]
        pub async fn handle_message(&self, message_json: &str) -> Result<String, JsValue> {
            let message: ElixirMessage = serde_json::from_str(message_json)
                .map_err(|e| JsValue::from_str(&format!("Invalid message JSON: {}", e)))?;

            let response = self.inner.handle_message(message).await
                .map_err(|e| JsValue::from_str(&e.to_string()))?;

            serde_json::to_string(&response)
                .map_err(|e| JsValue::from_str(&e.to_string()))
        }

        #[wasm_bindgen(js_name = getSocketState)]
        pub async fn get_socket_state(&self, socket_id: &str) -> Option<String> {
            self.inner.get_socket_state(socket_id).await
                .and_then(|state| serde_json::to_string(&state).ok())
        }
    }
}

#[cfg(feature = "wasm-web")]
pub use wasm_bindings::WasmElixirRuntime;

// Rustler NIF bindings for Elixir integration
#[cfg(feature = "rustler-nif")]
mod rustler_bindings {
    use super::*;
    use rustler::prelude::*;

    #[derive(NifStruct)]
    #[module = "KatalystElixirRuntime.Message"]
    struct NifElixirMessage {
        message_type: String,
